// export default api;
// services/api.js
import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 10000,
});

// QUAN TR·ªåNG: Interceptor ƒë·ªÉ t·ª± ƒë·ªông th√™m token
api.interceptors.request.use(
  (config) => {
    console.log(`üöÄ API Request: ${config.method?.toUpperCase()} ${config.url}`);

    const token = localStorage.getItem('token');
    if (token) {
      console.log("üîê Adding token to request headers");
      config.headers.Authorization = `Bearer ${token}`;
      console.log("üìã Request headers:", config.headers);
    } else {
      console.log("‚ö†Ô∏è No token found in localStorage");
    }

    return config;
  },
  (error) => {
    console.error("‚ùå Request interceptor error:", error);
    return Promise.reject(error);
  }
);

// Response interceptor - QUAN TR·ªåNG: KH√îNG redirect t·ª± ƒë·ªông
api.interceptors.response.use(
  (response) => {
    console.log(`‚úÖ API Response: ${response.status} ${response.config.url}`);
    return response;
  },
  (error) => {
    console.error(`‚ùå API Error: ${error.response?.status} ${error.config?.url}`);
    console.error("Error details:", error.response?.data);

    // QUAN TR·ªåNG: Kh√¥ng redirect t·ª± ƒë·ªông ·ªü ƒë√¢y
    // ƒê·ªÉ component t·ª± x·ª≠ l√Ω l·ªói 401
    return Promise.reject(error);
  }
);

export default api;
