import { useEffect, useState } from "react";
import productService from "../../services/productService";

const ProductList = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    productService
      .getAllProducts()
      .then((data) => {
        // n·∫øu API tr·∫£ v·ªÅ d·∫°ng Page (Spring Data)
        setProducts(data.content || data);
      })
      .catch((err) => console.error("‚ùå Kh√¥ng th·ªÉ t·∫£i s·∫£n ph·∫©m:", err));
  }, []);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4 text-gray-800">üõçÔ∏è Danh s√°ch s·∫£n ph·∫©m</h2>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {products.map((p) => (
          <div
            key={p.productId}
            className="border rounded-xl shadow-md hover:shadow-lg transition p-4 bg-white"
          >
            <img
              src={productService.getProductImage(p.imageName)}
              alt={p.productName}
              className="w-full h-56 object-cover rounded-lg mb-3"
              onError={(e) => {
                e.target.src = "https://cdn-icons-png.flaticon.com/512/679/679922.png";
              }}
            />
            <h3 className="text-lg font-semibold text-gray-700 mb-1">
              {p.productName}
            </h3>
            <p className="text-red-500 font-bold">
              {p.discountedPrice
                ? `${p.discountedPrice.toLocaleString()} ‚Ç´`
                : `${p.price.toLocaleString()} ‚Ç´`}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
