import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import authService from "../../services/authService";
import userService from "../../services/userService";

const Login = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    remember: true,
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [messageType, setMessageType] = useState(""); // "success" or "error"

  const showMessage = (text, type) => {
    setMessage(text);
    setMessageType(type);
    setTimeout(() => {
      setMessage("");
      setMessageType("");
    }, 3000);
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const validate = () => {
    const { email, password } = formData;
    const errors = [];

    if (!email || !password) {
      errors.push("Vui l√≤ng nh·∫≠p ƒë·∫ßy ƒë·ªß th√¥ng tin.");
    }

    if (email && !/\S+@\S+\.\S+/.test(email)) {
      errors.push("Email kh√¥ng h·ª£p l·ªá.");
    }

    return errors.length > 0 ? errors : null;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const errors = validate();
    if (errors) {
      showMessage(errors.join(". "), "error");
      return;
    }

    setLoading(true);
    setMessage("");

    try {
      console.log("üîê Attempting login with:", { email: formData.email });

      // G·ªçi API login - theo ƒë√∫ng service c·ªßa b·∫°n
      const res = await authService.login({
        email: formData.email,
        password: formData.password
      });

      console.log("‚úÖ LOGIN SUCCESS:", res.data);
      const loginData = res.data;

      // L·∫•y token t·ª´ response - theo ƒë√∫ng service c·ªßa b·∫°n
      const token = loginData.token || loginData.access_token || loginData.jwt;

      if (!token) {
        throw new Error("Server kh√¥ng tr·∫£ v·ªÅ token");
      }

      console.log("üîê Token received");

      // L∆∞u token v√†o localStorage - theo ƒë√∫ng service c·ªßa b·∫°n
      localStorage.setItem("token", token);

      // L·∫•y th√¥ng tin user t·ª´ API b·∫±ng email sau khi login
      console.log("üîç Getting user info by email...");
      let userInfo = null;

      try {
        // Th·ª≠ l·∫•y user info b·∫±ng email t·ª´ public endpoint
        userInfo = await userService.getUserByEmail?.(formData.email);
        console.log("‚úÖ User info from email lookup:", userInfo);
      } catch (emailError) {
        console.warn("‚ö†Ô∏è Could not get user by email:", emailError);
        // Fallback: s·ª≠ d·ª•ng th√¥ng tin t·ª´ login response
        userInfo = loginData.user || loginData;
      }

      // N·∫øu kh√¥ng c√≥ user info, th·ª≠ l·∫•y t·ª´ userService.getUserProfile
      if (!userInfo || !userInfo.userId) {
        try {
          // Gi·∫£ s·ª≠ loginData c√≥ userId, ho·∫∑c decode token ƒë·ªÉ l·∫•y userId
          const payload = JSON.parse(atob(token.split('.')[1]));
          const userIdFromToken = payload.userId || payload.sub;

          if (userIdFromToken) {
            userInfo = await userService.getUserProfile(userIdFromToken);
            console.log("‚úÖ User info from profile:", userInfo);
          }
        } catch (profileError) {
          console.warn("‚ö†Ô∏è Could not get user profile:", profileError);
        }
      }

      // Chu·∫©n h√≥a th√¥ng tin user theo database schema c·ªßa b·∫°n
      const userToStore = {
        userId: userInfo?.userId || userInfo?.id || 0,
        firstName: userInfo?.firstName || userInfo?.first_name || "",
        lastName: userInfo?.lastName || userInfo?.last_name || "",
        mobileNumber: userInfo?.mobileNumber || userInfo?.mobile_number || "",
        email: userInfo?.email || formData.email,
        roles: userInfo?.roles || userInfo?.role || ["user"] // M·∫∑c ƒë·ªãnh l√† user
      };

      // L∆∞u th√¥ng tin user v√†o localStorage
      localStorage.setItem("user", JSON.stringify(userToStore));
      console.log("üíæ User data saved to localStorage:", userToStore);

      // L∆∞u role v√†o localStorage (n·∫øu c√≥)
      if (userToStore.roles && userToStore.roles.length > 0) {
        const primaryRole = Array.isArray(userToStore.roles) 
          ? userToStore.roles[0] 
          : userToStore.roles;
        localStorage.setItem("role", primaryRole);

        // Set cookie for role (n·∫øu c·∫ßn)
        const expiryDate = new Date();
        expiryDate.setDate(expiryDate.getDate() + 30);
        document.cookie = `role=${primaryRole}; path=/; expires=${expiryDate.toUTCString()};`;
      }

      // Ki·ªÉm tra final
      const savedToken = localStorage.getItem("token");
      const savedUser = JSON.parse(localStorage.getItem("user"));

      console.log("üîç FINAL AUTH CHECK:", {
        token: savedToken ? "EXISTS" : "MISSING",
        user: savedUser ? `EXISTS (ID: ${savedUser.userId})` : "MISSING",
        roles: savedUser?.roles || "NO ROLES"
      });

      showMessage("ƒêƒÉng nh·∫≠p th√†nh c√¥ng!", "success");

      // Redirect based on role or default
      setTimeout(() => {
        const userRole = savedUser?.roles;
        if (userRole && (userRole.includes("admin") || userRole.includes("admin-web"))) {
          window.location.href = "/admin";
        } else if (userRole && userRole.includes("Authenticated")) {
          window.location.href = "/site";
        } else {
          window.location.href = "/";
        }
      }, 1000);

    } catch (err) {
      console.error("‚ùå LOGIN ERROR:", err);
      // Clear auth data khi c√≥ l·ªói - theo ƒë√∫ng service c·ªßa b·∫°n
      authService.logout();

      const errorMessage = err.response?.data?.message || 
                          err.response?.data?.error || 
                          err.message || 
                          "ƒêƒÉng nh·∫≠p th·∫•t b·∫°i!";

      showMessage(errorMessage, "error");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-4">
      <div className="card mx-auto" style={{ maxWidth: "400px", marginTop: "50px" }}>
        <div className="card-body">
          <h4 className="card-title mb-4 text-center text-primary">Sign in</h4>

          {/* Message Alert */}
          {message && (
            <div 
              className={`alert ${messageType === "success" ? "alert-success" : "alert-danger"}`} 
              role="alert"
            >
              {messageType === "success" ? "‚úÖ " : "‚ùå "}
              {message}
            </div>
          )}

          <form onSubmit={handleSubmit}>
            <div className="form-group mb-3">
              <label className="form-label">Email</label>
              <input
                name="email"
                className="form-control"
                placeholder="Enter your email"
                type="email"
                value={formData.email}
                onChange={handleChange}
                required
                disabled={loading}
              />
            </div>

            <div className="form-group mb-3">
              <label className="form-label">Password</label>
              <input
                name="password"
                className="form-control"
                placeholder="Enter your password"
                type="password"
                value={formData.password}
                onChange={handleChange}
                required
                disabled={loading}
              />
            </div>

            <div className="form-group mb-3">
              <div className="form-check">
                <input
                  name="remember"
                  className="form-check-input"
                  type="checkbox"
                  checked={formData.remember}
                  onChange={handleChange}
                  disabled={loading}
                />
                <label className="form-check-label">
                  Remember me
                </label>
              </div>
            </div>

            <button 
              type="submit" 
              className="btn btn-primary w-100"
              disabled={loading}
            >
              {loading ? (
                <>
                  <span className="spinner-border spinner-border-sm me-2" role="status"></span>
                  ƒêang ƒëƒÉng nh·∫≠p...
                </>
              ) : (
                'Login'
              )}
            </button>
          </form>

          <div className="text-center mt-3">
            <a href="/forgot-password" className="text-decoration-none">
              Forgot password?
            </a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
