import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import userService from "../services/userService";
import categoryService from "../services/categoryService";
import bannerService from "../services/bannerService";

// ‚úÖ Import CSS, Font, Bootstrap
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";
import "../assets/fonts/fontawesome/css/all.min.css";

// ‚úÖ Import h√¨nh ·∫£nh
import flagVN from "../assets/images/icons/flags/VN.webp";

const IMAGE_BASE_URL = "http://localhost:8080/api/public/banners/image";

const Header = () => {
  const [user, setUser] = useState(null);
  const [userInfo, setUserInfo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [categories, setCategories] = useState([]);
  const [loadingCategories, setLoadingCategories] = useState(false);
  const [logo, setLogo] = useState(null);
  const navigate = useNavigate();

  // ‚úÖ L·∫•y user t·ª´ localStorage
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      try {
        const parsedUser = JSON.parse(storedUser);
        setUser(parsedUser);

        if (parsedUser.userId && typeof parsedUser.userId === 'number') {
          setLoading(true);

          userService
            .getUserById(parsedUser.userId)
            .then((userData) => {
              console.log("‚úÖ User data received:", userData);
              setUserInfo(userData);

              const updatedUser = {
                ...parsedUser,
                first_name: userData.first_name || userData.firstName,
                last_name: userData.last_name || userData.lastName,
                email: userData.email,
                mobile_number: userData.mobile_number
              };
              localStorage.setItem("user", JSON.stringify(updatedUser));
            })
            .catch((err) => {
              console.error("‚ùå L·ªói l·∫•y th√¥ng tin user:", err);
              if (parsedUser.first_name || parsedUser.last_name) {
                setUserInfo({
                  first_name: parsedUser.first_name,
                  last_name: parsedUser.last_name
                });
              }
            })
            .finally(() => {
              setLoading(false);
            });
        } else {
          if (parsedUser.first_name || parsedUser.last_name) {
            setUserInfo({
              first_name: parsedUser.first_name,
              last_name: parsedUser.last_name
            });
          }
        }
      } catch (error) {
        console.error("‚ùå Parse user l·ªói:", error);
        setLoading(false);
      }
    }
  }, []);

  // ‚úÖ L·∫•y danh s√°ch categories t·ª´ API
  useEffect(() => {
    fetchCategories();
  }, []);

  // ‚úÖ L·∫•y logo t·ª´ bannerService (id 4)
  useEffect(() => {
    fetchLogo();
  }, []);

  const fetchCategories = async () => {
    try {
      setLoadingCategories(true);
      const categoriesData = await categoryService.getAllCategories();
      setCategories(categoriesData || []);
    } catch (error) {
      console.error('‚ùå L·ªói khi l·∫•y danh m·ª•c:', error);
      setCategories([]);
    } finally {
      setLoadingCategories(false);
    }
  };

  // ‚úÖ H√†m l·∫•y logo t·ª´ bannerService
  const fetchLogo = async () => {
    try {
      const banners = await bannerService.getAllBanners();
      const logoBanner = banners.find(banner => 
        banner.bannerId === 4 || banner.id === 4
      );

      if (logoBanner) {
        setLogo(logoBanner);
      } else if (banners.length > 0) {
        setLogo(banners[0]);
      }
    } catch (error) {
      console.error('‚ùå L·ªói khi l·∫•y logo:', error);
    }
  };

  // ‚úÖ H√†m l·∫•y URL ·∫£nh logo
  const getLogoUrl = () => {
    if (!logo) return "https://via.placeholder.com/120x120/667eea/ffffff?text=LOGO";

    const imageName = logo.imageUrl || logo.image_url;

    if (!imageName) {
      return "https://via.placeholder.com/120x120/667eea/ffffff?text=LOGO";
    }

    if (imageName.startsWith('http')) {
      return imageName;
    }

    return `${IMAGE_BASE_URL}/${imageName}`;
  };

  // ‚úÖ ƒêi·ªÅu h∆∞·ªõng ƒë·∫øn trang s·∫£n ph·∫©m theo category
  const handleCategoryClick = (category) => {
    const categoryId = category.categoryId || category.id;
    const categoryName = category.categoryName || category.name || category.title || 'Danh m·ª•c';

    if (categoryId) {
      navigate(`/product`, { 
        state: { 
          selectedCategory: categoryId,
          categoryName: categoryName
        }
      });
    }
  };

  // ‚úÖ ƒêƒÉng xu·∫•t
  const handleLogout = () => {
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    setUser(null);
    setUserInfo(null);
    navigate("/login");
  };

  // ‚úÖ Chuy·ªÉn ƒë·∫øn trang ch·ªânh s·ª≠a profile
  const handleProfileClick = () => {
    if (user) {
      navigate("/profile");
    } else {
      navigate("/login");
    }
  };

  // ‚úÖ H√†m hi·ªÉn th·ªã t√™n ng∆∞·ªùi d√πng
  const renderUserName = () => {
    if (!user) return "My profile";

    if (loading) {
      return "Loading...";
    }

    if (userInfo) {
      const firstName = userInfo.first_name || "";
      const lastName = userInfo.last_name || "";
      const fullName = `${firstName} ${lastName}`.trim();
      if (fullName) return fullName;
    }

    if (user.email) return user.email;

    return "User";
  };

  // ‚úÖ H√†m hi·ªÉn th·ªã l·ªùi ch√†o
  const renderGreeting = () => {
    if (!user) return null;

    if (userInfo && (userInfo.first_name || userInfo.last_name)) {
      const firstName = userInfo.first_name || "";
      return `üëã Hi, ${firstName}`;
    }

    if (user.email) {
      return `üëã Hi, ${user.email.split('@')[0]}`;
    }

    return "üëã Hi, User";
  };

  // ‚úÖ H√†m hi·ªÉn th·ªã t√™n category
  const getCategoryName = (category) => {
    return category.categoryName || category.name || category.title || 'Danh m·ª•c';
  };

  return (
    <header className="section-header">
      {/* ======= THANH TR√äN ======= */}
      <nav className="navbar d-none d-md-flex p-md-0 navbar-expand-sm navbar-light border-bottom">
        <div className="container">
          <div className="collapse navbar-collapse" id="navbarTop4">
            <ul className="navbar-nav mr-auto">
              <li className="nav-item">
                {user ? (
                  <span className="nav-link">
                    {renderGreeting()} |{" "}
                    <button
                      onClick={handleLogout}
                      className="btn btn-link p-0 text-danger"
                      style={{ textDecoration: "none" }}
                    >
                      Logout
                    </button>
                  </span>
                ) : (
                  <span className="nav-link">
                    Hi, <Link to="/login">Log In</Link> or{" "}
                    <Link to="/register">Register</Link>
                  </span>
                )}
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">
                  Deals
                </a>
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">
                  Sell
                </a>
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">
                  Help
                </a>
              </li>
            </ul>

            <ul className="navbar-nav">
              <li className="nav-item">
                <a href="#" className="nav-link">
                  <img src={flagVN} height="24" alt="flag" /> Ship to
                </a>
              </li>
              <li className="nav-item dropdown">
                <a
                  href="#"
                  className="nav-link dropdown-toggle"
                  data-bs-toggle="dropdown" 
                  aria-expanded="false"
                >
                  Watchlist
                </a>
                <ul className="dropdown-menu">
                  <li>
                    <a className="dropdown-item" href="#">
                      First item
                    </a>
                  </li>
                  <li>
                    <a className="dropdown-item" href="#">
                      Second item
                    </a>
                  </li>
                  <li>
                    <a className="dropdown-item" href="#">
                      Third item
                    </a>
                  </li>
                </ul>
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">
                  My shop
                </a>
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">
                  <i className="fa fa-bell"></i>
                </a>
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">
                  <i className="fa fa-shopping-cart"></i>
                </a>
              </li>
            </ul>
          </div>
        </div>
      </nav>

      {/* ======= THANH CH√çNH - LOGO + MENU C√ôNG H√ÄNG ======= */}
      <div className="container">
        <section className="header-main border-bottom py-3">
          <div className="d-flex align-items-center justify-content-between">
            {/* ‚úÖ Logo to b√™n tr√°i */}
            <div className="flex-shrink-0">
              <Link to="/" className="brand-wrap text-decoration-none">
                <div className="d-flex align-items-center">
                  <div 
                    className="rounded-circle overflow-hidden border shadow-lg"
                    style={{
                      width: '100px',
                      height: '100px',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      backgroundColor: '#f8f9fa',
                      border: '3px solid #667eea'
                    }}
                  >
                    <img
                      src={getLogoUrl()}
                      alt="Logo"
                      className="img-fluid"
                      style={{
                        width: '100%',
                        height: '100%',
                        objectFit: 'cover'
                      }}
                      onError={(e) => {
                        e.target.src = "https://via.placeholder.com/100x100/667eea/ffffff?text=LOGO";
                      }}
                    />
                  </div>
                  <div className="ms-3">
                    <h3 className="fw-bold text-primary mb-1">SHOPMATE</h3>
                    <small className="text-muted fw-semibold">Premium Shopping</small>
                  </div>
                </div>
              </Link>
            </div>

            {/* ‚úÖ Menu ch√≠nh - N·∫∞M NGANG C√ôNG H√ÄNG */}
            <div className="flex-grow-1 mx-4">
              <nav className="navbar navbar-main p-0">
                <ul className="navbar-nav flex-row justify-content-center w-100">
                  <li className="nav-item mx-3">
                    <Link to="/" className="nav-link fw-bold text-dark fs-5">
                      <i className="fa fa-home me-2 text-primary"></i>
                      Home
                    </Link>
                  </li>
                  <li className="nav-item mx-3">
                    <Link to="/product" className="nav-link fw-bold text-dark fs-5">
                      <i className="fa fa-box me-2 text-primary"></i>
                      Products
                    </Link>
                  </li>

                  {/* ‚úÖ Categories Dropdown */}
                  <li className="nav-item dropdown mx-3">
                    <a
                      className="nav-link dropdown-toggle fw-bold text-dark fs-5"
                      href="#"
                      id="categoriesDropdown"
                      role="button"
                      data-bs-toggle="dropdown"
                      aria-expanded="false"
                    >
                      <i className="fa fa-list me-2 text-primary"></i>
                      Categories
                      {loadingCategories && (
                        <span className="ms-1">
                          <div className="spinner-border spinner-border-sm text-primary" role="status">
                            <span className="visually-hidden">Loading...</span>
                          </div>
                        </span>
                      )}
                    </a>

                    <ul 
                      className="dropdown-menu" 
                      aria-labelledby="categoriesDropdown"
                      style={{ maxHeight: '400px', overflowY: 'auto' }}
                    >
                      {loadingCategories ? (
                        <li>
                          <div className="dropdown-item text-muted">
                            <div className="d-flex align-items-center">
                              <div className="spinner-border spinner-border-sm me-2" role="status">
                                <span className="visually-hidden">Loading...</span>
                              </div>
                              Loading categories...
                            </div>
                          </div>
                        </li>
                      ) : categories.length > 0 ? (
                        categories.map((category, index) => (
                          <li key={category.categoryId || category.id || index}>
                            <a
                              className="dropdown-item"
                              href="#"
                              onClick={(e) => {
                                e.preventDefault();
                                handleCategoryClick(category);
                              }}
                            >
                              <i className="fa fa-folder me-2 text-primary"></i>
                              {getCategoryName(category)}
                            </a>
                          </li>
                        ))
                      ) : (
                        <li>
                          <div className="dropdown-item text-muted">
                            <i className="fa fa-exclamation-circle me-2"></i>
                            No categories found
                          </div>
                        </li>
                      )}
                    </ul>
                  </li>
                </ul>
              </nav>
            </div>

            {/* ‚úÖ C√°c icon b√™n ph·∫£i */}
            <div className="flex-shrink-0">
              <div className="widgets-wrap d-flex align-items-center">
                {/* Widget Profile */}
                <div className="widget-header me-4">
                  <a 
                    href="#" 
                    className="widget-view text-decoration-none"
                    onClick={(e) => {
                      e.preventDefault();
                      handleProfileClick();
                    }}
                  >
                    <div className="icon-area text-center mb-1">
                      <div className="bg-primary rounded-circle p-3 shadow">
                        <i className="fa fa-user text-white fa-lg"></i>
                      </div>
                    </div>
                    <small className="text-dark fw-semibold d-block text-center">
                      {renderUserName()}
                    </small>
                  </a>
                </div>



                <div className="widget-header">
                  <a href="#" className="widget-view text-decoration-none">
                    <div className="icon-area text-center mb-1">
                      <div className="bg-warning rounded-circle p-3 shadow position-relative">
                        <i className="fa fa-shopping-cart text-white fa-lg"></i>
                        <span className="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger">
                          5
                        </span>
                      </div>
                    </div>
                    <small className="text-dark fw-semibold d-block text-center">Cart</small>
                  </a>
                </div>
              </div>
            </div>
          </div>
        </section>
      </div>
    </header>
  );
};

export default Header;
