// src/pages/order/Checkout.js
import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import cartService from '../../services/cartService';
import orderService from '../../services/orderService';

const Checkout = () => {
  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(true);
  const [processing, setProcessing] = useState(false);
  const [user, setUser] = useState(null);
  const [paymentMethod, setPaymentMethod] = useState('COD');
  const [shippingAddress, setShippingAddress] = useState('');
  const navigate = useNavigate();
  const location = useLocation();

  const currentUser = JSON.parse(localStorage.getItem('user')) || { email: 'guest@example.com' };

  useEffect(() => {
    loadCartAndUser();
  }, []);

  const loadCartAndUser = async () => {
    try {
      setLoading(true);

      // L·∫•y gi·ªè h√†ng
      const cartData = await cartService.getActiveCart(currentUser.email);
      console.log("üì¶ Gi·ªè h√†ng thanh to√°n:", cartData);

      if (!cartData || !cartData.products || cartData.products.length === 0) {
        alert('‚ùå Gi·ªè h√†ng tr·ªëng!');
        navigate('/cart');
        return;
      }

      setCart(cartData);

      // L·∫•y th√¥ng tin user t·ª´ localStorage ho·∫∑c API
      const userData = JSON.parse(localStorage.getItem('user')) || {};
      setUser(userData);

      // L·∫•y ƒë·ªãa ch·ªâ t·ª´ localStorage n·∫øu c√≥
      const savedAddress = localStorage.getItem('shippingAddress') || '';
      setShippingAddress(savedAddress);

    } catch (error) {
      console.error('‚ùå L·ªói khi t·∫£i th√¥ng tin:', error);
      alert('‚ùå L·ªói khi t·∫£i th√¥ng tin: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

const handlePlaceOrder = async () => {
    // ‚úÖ KI·ªÇM TRA ƒêI·ªÄU KI·ªÜN TR∆Ø·ªöC KHI ƒê·∫∂T H√ÄNG
    if (!shippingAddress.trim()) {
        alert('‚ùå Vui l√≤ng nh·∫≠p ƒë·ªãa ch·ªâ giao h√†ng!');
        return;
    }

    if (!user || !user.email) {
        alert('‚ùå Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ ƒë·∫∑t h√†ng!');
        navigate('/login');
        return;
    }

    if (!cart || !cart.cartId) {
        alert('‚ùå Kh√¥ng t√¨m th·∫•y gi·ªè h√†ng!');
        return;
    }

    try {
        setProcessing(true);
        console.log('üîÑ B·∫Øt ƒë·∫ßu ƒë·∫∑t h√†ng...');
        console.log('üìã Th√¥ng tin ƒë·∫∑t h√†ng:', {
            email: user.email,
            cartId: cart.cartId,
            paymentMethod: paymentMethod,
            shippingAddress: shippingAddress,
            cartItems: cart.products?.length || 0
        });

        // ‚úÖ G·ªåI API ƒê·∫∂T H√ÄNG
        const order = await cartService.createOrder(
            user.email, 
            cart.cartId, 
            paymentMethod,
            shippingAddress
        );

        console.log('‚úÖ ƒê∆°n h√†ng ƒë√£ t·∫°o:', order);

        // ‚úÖ HI·ªÇN TH·ªä TH√îNG B√ÅO TH√ÄNH C√îNG
        const paymentInfo = `
üéâ ƒê·∫∂T H√ÄNG TH√ÄNH C√îNG!

üìã Th√¥ng tin ƒë∆°n h√†ng:
‚Ä¢ M√£ ƒë∆°n h√†ng: ${order.orderId}
‚Ä¢ Ng√†y ƒë·∫∑t: ${new Date().toLocaleDateString('vi-VN')}
‚Ä¢ Ph∆∞∆°ng th·ª©c: ${order.paymentMethod || paymentMethod}
‚Ä¢ Tr·∫°ng th√°i: ${order.orderStatus || 'ƒêang x·ª≠ l√Ω'}
‚Ä¢ T·ªïng ti·ªÅn: ${order.totalAmount?.toLocaleString('vi-VN')}‚Ç´
‚Ä¢ ƒê·ªãa ch·ªâ: ${order.shippingAddress || shippingAddress}

C·∫£m ∆°n b·∫°n ƒë√£ mua s·∫Øm! üõçÔ∏è
        `;

        alert(paymentInfo);

        // ‚úÖ L∆ØU ƒê·ªäA CH·ªà V√ÄO LOCALSTORAGE
        localStorage.setItem('shippingAddress', shippingAddress);

        // ‚úÖ L√ÄM R·ªñNG GI·ªé H√ÄNG (optional - backend ƒë√£ x·ª≠ l√Ω)
        try {
            await clearCartAfterOrder(user.email, cart.cartId);
        } catch (clearError) {
            console.warn('‚ö†Ô∏è L·ªói khi clear cart (c√≥ th·ªÉ ƒë√£ ƒë∆∞·ª£c clear t·ª± ƒë·ªông):', clearError);
        }

        // ‚úÖ CHUY·ªÇN H∆Ø·ªöNG ƒê·∫æN TRANG TH√ÄNH C√îNG
        navigate('/order-success', { 
            state: { 
                order: order,
                message: 'ƒê∆°n h√†ng c·ªßa b·∫°n ƒë√£ ƒë∆∞·ª£c x√°c nh·∫≠n!'
            } 
        });

    } catch (error) {
        console.error('‚ùå L·ªói khi ƒë·∫∑t h√†ng:', error);

        // ‚úÖ HI·ªÇN TH·ªä L·ªñI CHI TI·∫æT
        const errorMessage = error.response?.data?.message || error.message || 'L·ªói kh√¥ng x√°c ƒë·ªãnh';
        alert(`‚ùå L·ªói khi ƒë·∫∑t h√†ng: ${errorMessage}`);

        // ‚úÖ GHI LOG L·ªñI CHI TI·∫æT
        console.error('Error details:', {
            status: error.response?.status,
            data: error.response?.data,
            config: error.config
        });
    } finally {
        setProcessing(false);
    }
};

  // ‚úÖ TH√äM: H√†m l√†m r·ªóng gi·ªè h√†ng sau khi ƒë·∫∑t h√†ng
  const clearCartAfterOrder = async (email, cartId) => {
    try {
      console.log(`üßπ ƒêang l√†m r·ªóng gi·ªè h√†ng: ${cartId}`);

      // C√°ch 1: G·ªçi API x√≥a to√†n b·ªô gi·ªè h√†ng
      await cartService.clearCart(email, cartId);

      console.log('‚úÖ ƒê√£ l√†m r·ªóng gi·ªè h√†ng th√†nh c√¥ng');

      // C·∫≠p nh·∫≠t state local
      setCart(prevCart => ({
        ...prevCart,
        products: [],
        totalPrice: 0
      }));

    } catch (error) {
      console.error('‚ùå L·ªói khi l√†m r·ªóng gi·ªè h√†ng:', error);
      // Kh√¥ng throw error ·ªü ƒë√¢y v√¨ ƒë∆°n h√†ng ƒë√£ ƒë∆∞·ª£c t·∫°o th√†nh c√¥ng
    }
  };

  // T√≠nh t·ªïng ti·ªÅn
  const totalAmount = cart ? cartService.calculateTotal(cart.products) : 0;
  const shippingFee = 0; // Mi·ªÖn ph√≠ v·∫≠n chuy·ªÉn
  const finalAmount = totalAmount + shippingFee;

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-2">ƒêang t·∫£i th√¥ng tin thanh to√°n...</p>
        </div>
      </div>
    );
  }

  if (!cart || !cart.products || cart.products.length === 0) {
    return (
      <div className="container mt-4">
        <div className="alert alert-warning text-center">
          <h4>Gi·ªè h√†ng tr·ªëng</h4>
          <p>B·∫°n ch∆∞a c√≥ s·∫£n ph·∫©m n√†o ƒë·ªÉ thanh to√°n</p>
          <Link to="/product" className="btn btn-primary">
            <i className="fas fa-shopping-bag me-2"></i>
            Mua s·∫Øm ngay
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      {/* Breadcrumb */}
      <nav aria-label="breadcrumb">
        <ol className="breadcrumb">
          <li className="breadcrumb-item">
            <Link to="/" className="text-decoration-none">üè† Trang ch·ªß</Link>
          </li>
          <li className="breadcrumb-item">
            <Link to="/cart" className="text-decoration-none">Gi·ªè h√†ng</Link>
          </li>
          <li className="breadcrumb-item active" aria-current="page">
            Thanh to√°n
          </li>
        </ol>
      </nav>

      <div className="row">
        <div className="col-12">
          <h1 className="h2 text-primary mb-4">Thanh to√°n ƒë∆°n h√†ng</h1>
        </div>
      </div>

      <div className="row">
        {/* Th√¥ng tin giao h√†ng */}
        <div className="col-lg-8">
          <div className="card shadow-sm mb-4">
            <div className="card-header bg-white">
              <h5 className="mb-0">
                <i className="fas fa-user me-2"></i>
                Th√¥ng tin giao h√†ng
              </h5>
            </div>
            <div className="card-body">
              {user ? (
                <div className="row">
                  <div className="col-md-6 mb-3">
                    <label className="form-label">H·ªç v√† t√™n</label>
                    <input 
