import React, { useState, useEffect, useCallback } from "react";
import api from "../../../services/api"; // Đảm bảo đường dẫn này đúng với file axios config của mày
import Swal from "sweetalert2";

const CategoryListAD = () => {
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [isEdit, setIsEdit] = useState(false);
  const [formData, setFormData] = useState({ categoryId: "", categoryName: "" });

  // Quản lý phân trang
  const [pageConfig, setPageConfig] = useState({
    pageNumber: 0,
    pageSize: 10,
    totalPages: 0,
    totalElements: 0,
  });

  // ================= FETCH DATA =================
  const fetchCategories = useCallback(async (page = 0) => {
    try {
      setLoading(true);
      // Endpoint lấy danh mục phân trang (Public)
      const response = await api.get(`/public/categories?pageNumber=${page}&pageSize=${pageConfig.pageSize}`);
      
      // Chỗ này tùy thuộc vào cấu trúc trả về của Backend (PageResponse)
      const data = response.data;
      setCategories(data.content || []);
      setPageConfig((prev) => ({
        ...prev,
        pageNumber: data.pageNumber || 0,
        totalPages: data.totalPages || 0,
        totalElements: data.totalElements || 0,
      }));
    } catch (err) {
      console.error("❌ Lỗi load Category:", err);
      Swal.fire("Lỗi", "Không thể tải danh sách danh mục", "error");
    } finally {
      setLoading(false);
    }
  }, [pageConfig.pageSize]);

  useEffect(() => {
    fetchCategories(0);
  }, [fetchCategories]);

  // ================= LOGIC MODAL =================
  const handleOpenModal = (cat = null) => {
    if (cat) {
      setIsEdit(true);
      setFormData({ categoryId: cat.categoryId, categoryName: cat.categoryName });
    } else {
      setIsEdit(false);
      setFormData({ categoryId: "", categoryName: "" });
    }
    setShowModal(true);
  };

  // ================= SUBMIT (THÊM / SỬA) =================
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validation phía Client (Khớp với @Size(min = 5) ở Java)
    if (formData.categoryName.trim().length < 5) {
      return Swal.fire("Lỗi", "Tên danh mục phải có ít nhất 5 ký tự", "warning");
    }

    try {
      const token = localStorage.getItem("token");
      const config = { headers: { Authorization: `Bearer ${token}` } };

      if (isEdit) {
        // Cập nhật: PUT /admin/categories/{id}
        await api.put(`/admin/categories/${formData.categoryId}`, { 
            categoryName: formData.categoryName 
        }, config);
        Swal.fire("Thành công", "Đã cập nhật tên danh mục", "success");
      } else {
        // Thêm mới: POST /admin/category
        await api.post("/admin/category", { 
            categoryName: formData.categoryName 
        }, config);
        Swal.fire("Thành công", "Đã thêm danh mục mới", "success");
      }

      setShowModal(false);
      fetchCategories(pageConfig.pageNumber); // Reload lại trang hiện tại
    } catch (err) {
      const errMsg = err.response?.data?.message || "Thao tác thất bại";
      Swal.fire("Lỗi", errMsg, "error");
    }
  };

  // ================= XÓA DANH MỤC =================
  const handleDelete = async (id) => {
    const result = await Swal.fire({
      title: "Xác nhận xóa?",
      text: "Lưu ý: Xóa danh mục sẽ xóa tất cả sản phẩm liên quan (Cascade)!",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#d33",
      confirmButtonText: "Đồng ý xóa",
      cancelButtonText: "Hủy"
    });

    if (result.isConfirmed) {
      try {
        const token = localStorage.getItem("token");
        await api.delete(`/admin/categories/${id}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        Swal.fire("Đã xóa!", "Danh mục đã được loại bỏ.", "success");
        fetchCategories(0); // Quay về trang 1
      } catch (err) {
        Swal.fire("Lỗi", "Không thể xóa danh mục này!", "error");
      }
    }
  };

  return (
    <div className="p-4">
      <div className="card shadow-sm border-0">
        <div className="card-header bg-white d-flex justify-content-between align-items-center py-3">
          <h5 className="mb-0 fw-bold"><i className="bi bi-tag-fill me-2"></i>QUẢN LÝ DANH MỤC</h5>
          <button className="btn btn-primary btn-sm" onClick={() => handleOpenModal()}>
            + Thêm mới
          </button>
        </div>

        <div className="table-responsive">
          <table className="table table-hover align-middle mb-0">
            <thead className="table-light">
              <tr>
                <th width="10%">ID</th>
                <th width="70%">Tên danh mục</th>
                <th width="20%" className="text-center">Hành động</th>
              </tr>
            </thead>
            <tbody>
              {loading ? (
                <tr><td colSpan="3" className="text-center py-3">Đang tải dữ liệu...</td></tr>
              ) : (
                categories.map((cat) => (
                  <tr key={cat.categoryId}>
                    <td>{cat.categoryId}</td>
                    <td className="fw-semibold">{cat.categoryName}</td>
                    <td className="text-center">
                      <button className="btn btn-sm btn-outline-info me-2" onClick={() => handleOpenModal(cat)}>
                        Sửa
                      </button>
                      <button className="btn btn-sm btn-outline-danger" onClick={() => handleDelete(cat.categoryId)}>
                        Xóa
                      </button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>

        {/* Phân trang */}
        <div className="card-footer bg-white d-flex justify-content-between align-items-center py-3">
          <p className="small text-muted mb-0">Tổng: {pageConfig.totalElements} danh mục</p>
          <nav>
            <ul className="pagination pagination-sm mb-0">
              <li className={`page-item ${pageConfig.pageNumber === 0 ? "disabled" : ""}`}>
                <button className="page-link" onClick={() => fetchCategories(pageConfig.pageNumber - 1)}>Trước</button>
              </li>
              {[...Array(pageConfig.totalPages)].map((_, i) => (
                <li key={i} className={`page-item ${pageConfig.pageNumber === i ? "active" : ""}`}>
                  <button className="page-link" onClick={() => fetchCategories(i)}>{i + 1}</button>
                </li>
              ))}
              <li className={`page-item ${pageConfig.pageNumber >= pageConfig.totalPages - 1 ? "disabled" : ""}`}>
                <button className="page-link" onClick={() => fetchCategories(pageConfig.pageNumber + 1)}>Sau</button>
              </li>
            </ul>
          </nav>
        </div>
      </div>

      {/* Modal form */}
      {showModal && (
        <div className="modal d-block" style={{ backgroundColor: "rgba(0,0,0,0.5)" }}>
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content border-0 shadow">
              <form onSubmit={handleSubmit}>
                <div className="modal-header">
                  <h5 className="modal-title fw-bold">{isEdit ? "Cập nhật danh mục" : "Thêm danh mục"}</h5>
                  <button type="button" className="btn-close" onClick={() => setShowModal(false)}></button>
                </div>
                <div className="modal-body">
                  <div className="mb-3">
                    <label className="form-label small">Tên danh mục</label>
                    <input
                      type="text"
                      className="form-control"
                      value={formData.categoryName}
                      onChange={(e) => setFormData({ ...formData, categoryName: e.target.value })}
                      placeholder="Nhập trên 5 ký tự..."
                      required
                    />
                  </div>
                </div>
                <div className="modal-footer border-0">
                  <button type="button" className="btn btn-light btn-sm" onClick={() => setShowModal(false)}>Đóng</button>
                  <button type="submit" className="btn btn-primary btn-sm px-4">Lưu lại</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CategoryListAD;
