// src/services/userService.js
import api from "./api";

const userService = {
  // ✅ Lấy thông tin user theo ID (dành cho người dùng thường)
  getUserById: (userId) => api.get(`/api/public/users/${userId}`),

  // ✅ Nếu bạn có quyền admin, có thể dùng API admin
  getUserByIdAdmin: (userId) => api.get(`/api/admin/users/${userId}`),

  // ✅ Lấy danh sách tất cả user (chỉ dành cho admin)
  getAllUsersAdmin: () => api.get(`/api/admin/users`),
};

export default userService;
