import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import authService from "../../services/authService";
import Swal from "sweetalert2";

const AdminLogin = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      /* ================== 1. G·ªåI API ================== */
      const res = await authService.login({ email, password });
      const data = res.data;
      const token = data.token || data.jwt || data["jwt-token"];

      if (!token) throw new Error("Kh√¥ng nh·∫≠n ƒë∆∞·ª£c token t·ª´ h·ªá th·ªëng!");

      /* ================== 2. GI·∫¢I M√É CHECK ROLE ================== */
      const base64Url = token.split('.')[1];
      const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
      const payload = JSON.parse(window.atob(base64));
      
      const rawRole = payload.roles || payload.authorities || payload.role || payload.sub;
      const roleStr = JSON.stringify(rawRole).toUpperCase();

      if (!roleStr.includes("ADMIN")) {
        await Swal.fire({
          icon: "warning",
          title: "TRUY C·∫¨P B·ªä CH·∫∂N",
          text: "M√†y kh√¥ng ph·∫£i Qu·∫£n tr·ªã vi√™n, ƒë·ª´ng c·ªë v√†o ƒë√¢y!",
          background: "#1e293b",
          color: "#fff",
          confirmButtonColor: "#3b82f6"
        });
        setLoading(false);
        return;
      }

      /* ================== 3. L∆ØU TR·ªÆ ================== */
      localStorage.setItem("admin-token", token);
      localStorage.setItem("admin-role", "ADMIN");

      /* ================== 4. TH√ÄNH C√îNG ================== */
      await Swal.fire({
        icon: "success",
        title: "X√ÅC TH·ª∞C TH√ÄNH C√îNG",
        text: "H·ªá th·ªëng ƒë√£ s·∫µn s√†ng, th∆∞a S·∫øp! üëë",
        timer: 1500,
        showConfirmButton: false,
        background: "#1e293b",
        color: "#fff"
      });

      navigate("/admin");
    } catch (err) {
      /* ================== 5. TH·∫§T B·∫†I ================== */
      Swal.fire({
        icon: "error",
        title: "ƒêƒÇNG NH·∫¨P TH·∫§T B·∫†I",
        text: err.response?.data?.message || "Sai t√†i kho·∫£n ho·∫∑c m·∫≠t kh·∫©u r·ªìi!",
        background: "#1e293b",
        color: "#fff",
        confirmButtonColor: "#ef4444"
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      {/* C√°c kh·ªëi m√†u m·ªù ·∫£o ch·∫°y n·ªÅn */}
      <div style={styles.blob1}></div>
      <div style={styles.blob2}></div>

      <div style={styles.glassCard}>
        <div style={styles.header}>
          <div style={styles.iconCircle}>
            <span style={{ fontSize: '28px' }}>üîê</span>
          </div>
          <h2 style={styles.title}>ADMIN PORTAL</h2>
          <p style={styles.subtitle}>H·ªá th·ªëng qu·∫£n l√Ω m·ªπ ph·∫©m n·ªôi b·ªô</p>
        </div>

        <form onSubmit={handleSubmit} style={styles.form}>
          <div style={styles.inputGroup}>
            <label style={styles.label}>EMAIL QU·∫¢N TR·ªä</label>
            <input
              type="email"
              placeholder="admin@cosmetic.com"
              style={styles.input}
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              disabled={loading}
            />
          </div>

          <div style={styles.inputGroup}>
            <label style={styles.label}>M·∫¨T KH·∫®U B·∫¢O M·∫¨T</label>
            <input
              type="password"
              placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
              style={styles.input}
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              disabled={loading}
            />
          </div>

          <button 
            type="submit" 
            style={loading ? {...styles.button, opacity: 0.7} : styles.button} 
            disabled={loading}
          >
            {loading ? "ƒêANG X√ÅC TH·ª∞C..." : "ƒêƒÇNG NH·∫¨P H·ªÜ TH·ªêNG"}
          </button>
        </form>

        <div style={styles.footer}>
          <button onClick={() => navigate("/")} style={styles.backLink}>
             ‚Üê Quay l·∫°i trang ch·ªß User
          </button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    height: "100vh",
    width: "100vw",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    background: "#0f172a", 
    overflow: "hidden",
    position: "fixed", // C·ªë ƒë·ªãnh ƒë·ªÉ kh√¥ng d√≠nh Header/Footer c·ªßa ai h·∫øt
    top: 0,
    left: 0,
    zIndex: 99999, // Lu√¥n lu√¥n n·∫±m tr√™n c√πng
    fontFamily: "'Inter', sans-serif"
  },
  blob1: {
    position: "absolute",
    width: "500px",
    height: "500px",
    background: "radial-gradient(circle, rgba(59, 130, 246, 0.2) 0%, rgba(0, 0, 0, 0) 70%)",
    borderRadius: "50%",
    top: "-150px",
    left: "-150px",
    zIndex: 0
  },
  blob2: {
    position: "absolute",
    width: "400px",
    height: "400px",
    background: "radial-gradient(circle, rgba(239, 68, 68, 0.15) 0%, rgba(0, 0, 0, 0) 70%)",
    borderRadius: "50%",
    bottom: "-100px",
    right: "-100px",
    zIndex: 0
  },
  glassCard: {
    width: "400px",
    background: "rgba(255, 255, 255, 0.03)",
    backdropFilter: "blur(20px)",
    border: "1px solid rgba(255, 255, 255, 0.1)",
    borderRadius: "28px",
    padding: "45px 40px",
    boxShadow: "0 25px 50px -12px rgba(0, 0, 0, 0.7)",
    zIndex: 1,
  },
  iconCircle: {
    width: "65px",
    height: "65px",
    background: "rgba(59, 130, 246, 0.1)",
    border: "1px solid rgba(59, 130, 246, 0.2)",
    borderRadius: "20px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    margin: "0 auto 20px",
    color: "#fff",
  },
  title: {
    color: "#fff",
    fontSize: "26px",
    fontWeight: "800",
    letterSpacing: "1px",
    margin: "0 0 8px 0",
    textAlign: "center"
  },
  subtitle: {
    color: "#64748b",
    fontSize: "14px",
