    letterSpacing: "0.5px"
  },
  input: {
    width: "100%",
    padding: "14px 16px",
    background: "rgba(0, 0, 0, 0.2)",
    border: "1px solid rgba(255, 255, 255, 0.1)",
    borderRadius: "14px",
    color: "#fff",
    outline: "none",
    boxSizing: "border-box",
    fontSize: "14px"
  },
  button: {
    width: "100%",
    padding: "16px",
    background: "linear-gradient(135deg, #3b82f6 0%, #1d4ed8 100%)",
    color: "#fff",
    border: "none",
    borderRadius: "14px",
    fontWeight: "bold",
    cursor: "pointer",
    marginTop: "10px",
    transition: "0.3s",
    boxShadow: "0 10px 20px -5px rgba(59, 130, 246, 0.5)"
  },
  footer: {
    marginTop: "30px",
    borderTop: "1px solid rgba(255, 255, 255, 0.05)",
    paddingTop: "20px",
    textAlign: "center"
  },
  backLink: {
    background: "none",
    border: "none",
    color: "#475569",
    fontSize: "13px",
    cursor: "pointer",
    transition: "0.3s"
  }
};

export default AdminLogin;
