import React, { useState, useEffect, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import categoryService from "../../../services/categoryService";
import Swal from "sweetalert2";

const CategoryListAD = () => {
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [isEdit, setIsEdit] = useState(false);
  const [formData, setFormData] = useState({ categoryId: "", categoryName: "" });
  const navigate = useNavigate();

  // ================= CHECK QUY·ªÄN ADMIN T·ª™ ROLE =================
  const checkAdminAuth = useCallback(() => {
    const token = localStorage.getItem("token");
    const storedRole = localStorage.getItem("role"); // L·∫•y role t·ª´ m√°y kh√°ch

    console.log("üîë [Token Check]:", token ? "ƒê√£ c√≥" : "Kh√¥ng c√≥");
    console.log("üë§ [Role Check]:", storedRole);

    if (!token) return false;

    // Ch·∫•p nh·∫≠n c·∫£ "ADMIN" ho·∫∑c "ROLE_ADMIN"
    // N·∫øu role l∆∞u d·∫°ng m·∫£ng ["USER", "ADMIN"] th√¨ d√πng .includes
    const isAdmin = storedRole === "ADMIN" || 
                    storedRole === "ROLE_ADMIN" || 
                    (Array.isArray(storedRole) && storedRole.includes("ADMIN"));

    if (!isAdmin) {
      console.error("üö´ [Auth] Quy·ªÅn truy c·∫≠p b·ªã t·ª´ ch·ªëi. Role kh√¥ng h·ª£p l·ªá.");
      return false;
    }
    
    console.log("‚úÖ [Auth] X√°c nh·∫≠n quy·ªÅn ADMIN. ƒêang t·∫£i d·ªØ li·ªáu...");
    return true;
  }, []);

  // ================= FETCH DATA =================
  const fetchCategories = useCallback(async () => {
    try {
      setLoading(true);
      const data = await categoryService.getAllCategories();
      
      // Kh·ªõp v·ªõi h√†m getAllCategories m√†y v·ª´a vi·∫øt (tr·∫£ v·ªÅ m·∫£ng)
      setCategories(data);
      console.log("üìä [Data] ƒê√£ render danh s√°ch:", data.length, "m·ª•c");
    } catch (err) {
      console.error("‚ùå [Fetch Error]:", err);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    if (checkAdminAuth()) {
      fetchCategories();
    } else {
      Swal.fire({
        icon: "error",
        title: "T·ª´ ch·ªëi truy c·∫≠p",
        text: "M√†y kh√¥ng c√≥ quy·ªÅn ADMIN!",
        confirmButtonText: "Quay l·∫°i"
      });
      navigate("/login");
    }
  }, [fetchCategories, checkAdminAuth, navigate]);

  // ================= TH√äM / S·ª¨A =================
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const payload = { categoryName: formData.categoryName };
      
      if (isEdit) {
        await categoryService.updateCategory(formData.categoryId, payload);
        Swal.fire("Th√†nh c√¥ng", "ƒê√£ c·∫≠p nh·∫≠t danh m·ª•c", "success");
      } else {
        await categoryService.createCategory(payload);
        Swal.fire("Th√†nh c√¥ng", "ƒê√£ th√™m m·ªõi danh m·ª•c", "success");
      }
      
      setShowModal(false);
      fetchCategories();
    } catch (err) {
      // Log l·ªói chi ti·∫øt ƒë·ªÉ xem Backend b√°o g√¨ (v√≠ d·ª•: Token expired ho·∫∑c Role mismatch)
      console.error("‚ùå [Submit Error] Status:", err.response?.status);
      console.error("‚ùå [Submit Error] Data:", err.response?.data);
      
      const errorMsg = err.response?.data?.message || "L·ªói quy·ªÅn ADMIN ho·∫∑c d·ªØ li·ªáu kh√¥ng h·ª£p l·ªá";
      Swal.fire("Th·∫•t b·∫°i", errorMsg, "error");
    }
  };

  // ================= X√ìA =================
  const handleDelete = async (id) => {
    const confirm = await Swal.fire({
      title: "X√°c nh·∫≠n x√≥a?",
      icon: "warning",
      showCancelButton: true,
      confirmButtonText: "X√≥a lu√¥n",
      confirmButtonColor: "#d33"
    });

    if (confirm.isConfirmed) {
      try {
        await categoryService.deleteCategory(id);
        Swal.fire("ƒê√£ x√≥a!", "", "success");
        fetchCategories();
      } catch (err) {
        console.error("‚ùå [Delete Error]:", err.response?.data);
        Swal.fire("L·ªói", "Kh√¥ng th·ªÉ x√≥a danh m·ª•c n√†y!", "error");
      }
    }
  };

  return (
    <div className="p-4 bg-light min-vh-100">
      <div className="card shadow-sm border-0">
        <div className="card-header bg-white py-3 d-flex justify-content-between align-items-center">
          <h5 className="mb-0 fw-bold">DANH M·ª§C (ADMIN)</h5>
          <button className="btn btn-dark btn-sm" onClick={() => { setIsEdit(false); setFormData({categoryId:"", categoryName:""}); setShowModal(true); }}>
            + Th√™m m·ªõi
          </button>
        </div>
        
        <div className="table-responsive">
          <table className="table table-hover align-middle mb-0">
            <thead>
              <tr>
                <th width="10%">ID</th>
                <th>T√™n danh m·ª•c</th>
                <th width="20%" className="text-center">Thao t√°c</th>
              </tr>
            </thead>
            <tbody>
              {loading ? (
                <tr><td colSpan="3" className="text-center py-5">ƒêang t·∫£i d·ªØ li·ªáu...</td></tr>
              ) : categories.length > 0 ? (
                categories.map((cat) => (
                  <tr key={cat.categoryId}>
                    <td>#{cat.categoryId}</td>
                    <td className="fw-bold">{cat.categoryName}</td>
                    <td className="text-center">
                      <button className="btn btn-sm btn-outline-primary me-2" onClick={() => { setIsEdit(true); setFormData(cat); setShowModal(true); }}>S·ª≠a</button>
                      <button className="btn btn-sm btn-outline-danger" onClick={() => handleDelete(cat.categoryId)}>X√≥a</button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr><td colSpan="3" className="text-center py-5">Ch∆∞a c√≥ d·ªØ li·ªáu danh m·ª•c.</td></tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* Modal form */}
      {showModal && (
        <div className="modal d-block" style={{backgroundColor: "rgba(0,0,0,0.5)"}}>
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content border-0">
              <form onSubmit={handleSubmit}>
                <div className="modal-header">
                  <h5 className="fw-bold">{isEdit ? "S·ª≠a danh m·ª•c" : "Th√™m danh m·ª•c"}</h5>
                  <button type="button" className="btn-close" onClick={() => setShowModal(false)}></button>
                </div>
                <div className="modal-body">
                  <label className="form-label small fw-bold">T√™n danh m·ª•c</label>
                  <input 
                    type="text" className="form-control" 
                    value={formData.categoryName} 
                    onChange={e => setFormData({...formData, categoryName: e.target.value})}
                    placeholder="Nh·∫≠p t√™n tr√™n 5 k√Ω t·ª±..." required 
                  />
                </div>
                <div className="modal-footer">
                  <button type="submit" className="btn btn-primary w-100">L∆∞u d·ªØ li·ªáu</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CategoryListAD;
