import React, { useEffect, useState, useCallback } from "react";
import { ShoppingCart, Package, DollarSign, TrendingUp, ArrowUpRight } from "lucide-react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler,
} from "chart.js";
import { Line } from "react-chartjs-2";
import api from "../../../services/api";
import { useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler);

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalRevenue: 0,
    ordersCount: 0,
    productsCount: 0,
  });
  const [latestOrders, setLatestOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const checkAuth = useCallback(() => {
    const token = localStorage.getItem("admin-token") || localStorage.getItem("token");
    if (!token) return null;
    try {
      const decoded = jwtDecode(token);
      return decoded.role === "ADMIN" ? token : null;
    } catch { return null; }
  }, []);

  const fetchData = useCallback(async () => {
    const token = checkAuth();
    if (!token) { navigate("/login"); return; }

    try {
      setLoading(true);
      const [statsRes, ordersRes] = await Promise.all([
        api.get("/admin/stats/summary", { headers: { Authorization: `Bearer ${token}` } }),
        api.get("/admin/orders?pageSize=8&sortBy=orderDate&sortOrder=desc", { 
          headers: { Authorization: `Bearer ${token}` } 
        })
      ]);
      setStats(statsRes.data);
      setLatestOrders(ordersRes.data.content || []);
    } catch (err) {
      console.error("‚ùå L·ªói fetch:", err);
    } finally {
      setLoading(false);
    }
  }, [checkAuth, navigate]);

  useEffect(() => { fetchData(); }, [fetchData]);

  const chartData = {
    labels: ["T1", "T2", "T3", "T4", "T5", "T6"],
    datasets: [{
      fill: true,
      label: "Doanh thu",
      data: stats.monthlyRevenue || [0, 0, 0, 0, 0, 0],
      borderColor: "#4e73df",
      backgroundColor: "rgba(78, 115, 223, 0.1)",
      tension: 0.4,
    }],
  };

  if (loading) return <div className="p-5 text-center fw-bold">ƒêang qu√©t d·ªØ li·ªáu h·ªá th·ªëng...</div>;

  return (
    <div className="container-fluid p-4" style={{ backgroundColor: "#f4f6f9", minHeight: "100vh" }}>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h3 className="fw-bold text-dark m-0">üìä Dashboard Qu·∫£n Tr·ªã</h3>
        <button className="btn btn-dark btn-sm shadow-sm" onClick={fetchData}>L√†m m·ªõi</button>
      </div>

      {/* 3 Th·∫ª th·ªëng k√™ ch√≠nh */}
      <div className="row g-3 mb-4">
        <div className="col-md-4">
          <StatCard title="T·ªîNG DOANH THU" value={`${stats.totalRevenue?.toLocaleString()}ƒë`} icon={<DollarSign color="#fff" />} bgColor="bg-primary" />
        </div>
        <div className="col-md-4">
          <StatCard title="T·ªîNG ƒê∆†N H√ÄNG" value={stats.ordersCount} icon={<ShoppingCart color="#fff" />} bgColor="bg-success" />
        </div>
        <div className="col-md-4">
          <StatCard title="S·∫¢N PH·∫®M TRONG KHO" value={stats.productsCount} icon={<Package color="#fff" />} bgColor="bg-warning" />
        </div>
      </div>

      <div className="row g-4">
        <div className="col-lg-7">
          <div className="card border-0 shadow-sm p-4 h-100">
            <h6 className="fw-bold text-secondary mb-4">TƒÇNG TR∆Ø·ªûNG DOANH S·ªê</h6>
            <div style={{ height: "320px" }}>
              <Line data={chartData} options={{ responsive: true, maintainAspectRatio: false }} />
            </div>
          </div>
        </div>

        <div className="col-lg-5">
          <div className="card border-0 shadow-sm p-4 h-100">
            <h6 className="fw-bold text-secondary mb-3">LU·ªíNG ƒê∆†N H√ÄNG M·ªöI</h6>
            <div className="list-group list-group-flush">
              {latestOrders.map((order) => (
                <OrderRow 
                  key={order.orderId}
                  id={order.orderId} 
                  name={order.email.split('@')[0]} // C·∫Øt l·∫•y t√™n t·ª´ email
                  price={`${order.totalAmount?.toLocaleString()}ƒë`} 
                  status={order.orderStatus} 
                />
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const StatCard = ({ title, value, icon, bgColor }) => (
  <div className="card border-0 shadow-sm h-100 transition-hover">
    <div className="card-body p-4 d-flex align-items-center">
      <div className={`p-3 rounded-4 ${bgColor} me-4 shadow`}>{icon}</div>
      <div>
        <p className="text-muted small fw-bold mb-0 text-uppercase">{title}</p>
        <h3 className="fw-bold m-0">{value}</h3>
      </div>
    </div>
  </div>
);

const OrderRow = ({ id, name, price, status }) => (
  <div className="list-group-item px-0 border-0 d-flex justify-content-between align-items-center">
    <div className="d-flex align-items-center">
      <div className={`p-1 rounded-circle me-3 ${status === 'CANCELLED' ? 'bg-danger' : 'bg-success'}`} style={{ width: "8px", height: "8px" }}></div>
      <div>
        <div className="fw-bold small text-dark">#{id} - <span className="text-capitalize">{name}</span></div>
        <div className="text-muted" style={{ fontSize: "11px" }}>{status}</div>
      </div>
    </div>
    <div className="fw-bold text-primary small">{price}</div>
  </div>
);

export default Dashboard;
