import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api, { IMAGE_API } from "../services/api"; // Instance axios ƒë√£ c·∫•u h√¨nh Interceptor
import cartService from "../services/cartService";
import Swal from "sweetalert2";
import { AiOutlinePlus, AiOutlineMinus, AiFillHeart, AiOutlineHeart } from "react-icons/ai";

const ProductDetail = () => {
  const { id } = useParams(); // L·∫•y productId t·ª´ URL
  const navigate = useNavigate();

  const [product, setProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [isFavorite, setIsFavorite] = useState(false);
  const [loading, setLoading] = useState(true);

  /* ================= LOAD PRODUCT (Gi·ªëng Mobile) ================= */
  useEffect(() => {
    const loadProduct = async () => {
      try {
        const res = await api.get(`/public/products/${id}`);
        setProduct(res.data);
      } catch (error) {
        console.error("Load product error:", error);
        Swal.fire("L·ªói", "Kh√¥ng th·ªÉ t·∫£i th√¥ng tin s·∫£n ph·∫©m", "error");
      } finally {
        setLoading(false);
      }
    };
    loadProduct();
  }, [id]);

  /* ================= HANDLE ADD TO CART (Fix gi·ªëng Mobile) ================= */
  const handleAddToCart = async () => {
    // 1. L·∫•y cartId t·ª´ localStorage (Thay v√¨ g·ªçi API getActiveCart b·ªã l·ªói 401)
    const cartId = cartService.getCartId();
    const token = localStorage.getItem("token");

    console.log("DEBUG Web - cartId:", cartId);
    console.log("DEBUG Web - quantity:", quantity);

    // 2. Ki·ªÉm tra ƒëƒÉng nh·∫≠p/gi·ªè h√†ng
    if (!token || !cartId) {
      Swal.fire({
        title: "B·∫°n ch∆∞a ƒëƒÉng nh·∫≠p",
        text: "Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ th√™m v√†o gi·ªè h√†ng!",
        icon: "warning",
        showCancelButton: true,
        confirmButtonText: "ƒêƒÉng nh·∫≠p ngay",
      }).then((result) => {
        if (result.isConfirmed) navigate("/login");
      });
      return;
    }

    try {
      // 3. G·ªçi h√†m ADD_TO_CART (Kh·ªõp logic v·ªõi Mobile)
      await cartService.addToCart(cartId, product.productId, quantity);

      Swal.fire({
        icon: "success",
        title: "ƒê√£ th√™m v√†o gi·ªè h√†ng üõí",
        showConfirmButton: false,
        timer: 1500,
      });

      // N·∫øu mu·ªën chuy·ªÉn trang gi·ªëng router.push("/mycart"):
      // navigate("/cart");
    } catch (err) {
      console.error("ADD TO CART ERROR:", err.response?.data || err);
      Swal.fire("L·ªói", "Kh√¥ng th·ªÉ th√™m v√†o gi·ªè h√†ng", "error");
    }
  };

  if (loading) return <div className="text-center mt-10">ƒêang t·∫£i s·∫£n ph·∫©m...</div>;
  if (!product) return <div className="text-center mt-10">S·∫£n ph·∫©m kh√¥ng t·ªìn t·∫°i</div>;

  const totalPrice = quantity * (product.specialPrice ?? product.price ?? 0);

  return (
    <div className="max-w-6xl mx-auto p-4 md:flex gap-8">
      {/* C·ªòT TR√ÅI: H√åNH ·∫¢NH */}
      <div className="md:w-1/2 bg-gray-100 rounded-2xl overflow-hidden">
        <img
          src={`${IMAGE_API}${product.image}`}
          alt={product.name}
          className="w-full h-[400px] object-cover"
        />
      </div>

      {/* C·ªòT PH·∫¢I: N·ªòI DUNG */}
      <div className="md:w-1/2 flex flex-col justify-between py-4">
        <div>
          <div className="flex justify-between items-center mb-2">
            <h1 className="text-3xl font-bold text-gray-800">{product.name}</h1>
            <button onClick={() => setIsFavorite(!isFavorite)}>
              {isFavorite ? (
                <AiFillHeart size={30} color="#FF4D4D" />
              ) : (
                <AiOutlineHeart size={30} color="#333" />
              )}
            </button>
          </div>
          <p className="text-gray-500 mb-6">{product.unitWeight || "S·ªë l∆∞·ª£ng 1"}</p>

          <div className="flex justify-between items-center mb-8">
            {/* B·ªô ƒëi·ªÅu khi·ªÉn s·ªë l∆∞·ª£ng */}
            <div className="flex items-center border border-gray-300 rounded-xl p-1">
              <button 
                onClick={() => setQuantity(q => Math.max(1, q - 1))}
                className="p-2 hover:bg-gray-100 rounded-lg"
              >
                <AiOutlineMinus size={20} />
              </button>
              <span className="px-6 text-lg font-semibold">{quantity}</span>
              <button 
                onClick={() => setQuantity(q => q + 1)}
                className="p-2 hover:bg-gray-100 rounded-lg"
              >
                <AiOutlinePlus size={20} />
              </button>
            </div>

            <p className="text-2xl font-bold text-green-600">
              {totalPrice.toLocaleString()} VND
            </p>
          </div>

          <hr className="my-6 border-gray-200" />
          <h3 className="text-lg font-semibold mb-2">Chi ti·∫øt s·∫£n ph·∫©m</h3>
          <p className="text-gray-600 leading-relaxed mb-8">{product.description}</p>
        </div>

        {/* N√öT TH√äM V√ÄO GI·ªé (Footer gi·ªëng Mobile) */}
        <button
          onClick={handleAddToCart}
          className="w-full bg-green-500 hover:bg-green-600 text-white text-xl font-bold py-4 rounded-2xl flex items-center justify-center gap-2 transition-colors"
        >
          <AiOutlinePlus size={24} />
          Add to Basket
        </button>
      </div>
    </div>
  );
};

export default ProductDetail;
