import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import productService from "../../services/productService";

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [page, setPage] = useState(0);
  const size = 8;   // s·ªë s·∫£n ph·∫©m / trang

  const totalPages = Math.ceil(products.length / size);

  useEffect(() => {
    productService
      .getAllProducts()
      .then((data) => {
        console.log("üì¶ Products:", data);
        setProducts(data || []);
      })
      .catch((err) => console.error("‚ùå L·ªói khi l·∫•y s·∫£n ph·∫©m:", err));
  }, []);

  const paginated = products.slice(page * size, page * size + size);

  const handleNext = () => {
    if (page < totalPages - 1) setPage(page + 1);
  };

  const handlePrev = () => {
    if (page > 0) setPage(page - 1);
  };

  return (
    <section className="padding-bottom">
      <header className="section-heading mb-4 text-center">
        <h3 className="title-section text-uppercase font-bold text-primary">
          üõçÔ∏è T·∫•t c·∫£ s·∫£n ph·∫©m
        </h3>
      </header>

      <div className="row">
        {paginated.map((p) => {
          const productId = p.product_id;

          return (
            <div key={productId} className="col-xl-3 col-lg-3 col-md-4 col-6 mb-4">
              <div className="card card-product-grid shadow-sm h-100">
                <div className="img-wrap">
                  <Link to={`/product/${productId}`}>
                    <img
                      src={`${IMAGE_BASE_URL}/${p.image}`}
                      alt={p.product_name}
                      onError={(e) =>
                        (e.target.src =
                          "https://cdn-icons-png.flaticon.com/512/679/679922.png")
                      }
                      style={{
                        width: "100%",
                        height: "250px",
                        objectFit: "cover",
                        borderRadius: "10px",
                      }}
                    />
                  </Link>
                </div>

                <div className="info-wrap p-3 d-flex flex-column">
                  <Link to={`/product/${productId}`} className="text-decoration-none">
                    <h6 className="text-truncate text-dark fw-bold">
                      {p.product_name}
                    </h6>
                  </Link>

                  <p className="text-muted small mb-2 flex-grow-1">
                    {p.description?.substring(0, 40) || "ƒêang c·∫≠p nh·∫≠t m√¥ t·∫£..."}
                  </p>

                  <div className="price-wrapper">
                    {p.special_price ? (
                      <>
                        <span className="price h5 text-danger mb-0">
                          {p.special_price?.toLocaleString()} ‚Ç´
                        </span>
                        <span className="original-price text-muted text-decoration-line-through small ms-2">
                          {p.price?.toLocaleString()} ‚Ç´
                        </span>
                      </>
                    ) : (
                      <span className="price h5 text-danger mb-0">
                        {p.price?.toLocaleString()} ‚Ç´
                      </span>
                    )}
                  </div>

                  {p.discount > 0 && (
                    <small className="text-success mt-1">
                      üî• Gi·∫£m {p.discount}%
                    </small>
                  )}

                  <small
                    className={`mt-1 ${
                      p.quantity === 0 ? "text-danger" : "text-success"
                    }`}
                  >
                    {p.quantity === 0
                      ? "‚ùå H·∫øt h√†ng"
                      : `üì¶ C√≤n ${p.quantity} s·∫£n ph·∫©m`}
                  </small>
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {/* ‚úÖ Pagination */}
      {products.length > size && (
        <div className="d-flex justify-content-center mt-4">
          <button
            className="btn btn-outline-primary mx-2"
            onClick={handlePrev}
            disabled={page === 0}
          >
            ‚óÄ Tr∆∞·ªõc
          </button>

          <span className="mx-3 pt-2">
            Trang {page + 1} / {totalPages}
          </span>

          <button
            className="btn btn-outline-primary mx-2"
            onClick={handleNext}
            disabled={page >= totalPages - 1}
          >
            Sau ‚ñ∂
          </button>
        </div>
      )}
    </section>
  );
};

export default ProductList;
