import React, { useState } from "react";
import Swal from "sweetalert2";
import api from "../../services/api";
import { useNavigate } from "react-router-dom";

const ForgotPassword = () => {
  const navigate = useNavigate();

  const [step, setStep] = useState(1); // 1-email | 2-otp | 3-new password
  const [email, setEmail] = useState("");
  const [otp, setOtp] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [loading, setLoading] = useState(false);

  // üü¢ STEP 1: G·ª¨I OTP
  const sendOtp = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      await api.post("/public/users/send-otp", { email });

      Swal.fire({
        icon: "success",
        title: "ƒê√£ g·ª≠i OTP üì©",
        text: "Vui l√≤ng ki·ªÉm tra email c·ªßa b·∫°n",
      });

      setStep(2);
    } catch (err) {
      Swal.fire({
        icon: "error",
        title: "L·ªói",
        text: err.response?.data?.message || "Kh√¥ng g·ª≠i ƒë∆∞·ª£c OTP",
      });
    } finally {
      setLoading(false);
    }
  };

  // üü° STEP 2: X√ÅC NH·∫¨N OTP
  const verifyOtp = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      await api.post("/public/users/verify-otp", { email, otp });

      Swal.fire({
        icon: "success",
        title: "OTP h·ª£p l·ªá ‚úÖ",
        text: "Nh·∫≠p m·∫≠t kh·∫©u m·ªõi nh√©!",
      });

      setStep(3);
    } catch (err) {
      Swal.fire({
        icon: "error",
        title: "Sai OTP",
        text: "M√£ x√°c nh·∫≠n kh√¥ng ƒë√∫ng ho·∫∑c ƒë√£ h·∫øt h·∫°n",
      });
    } finally {
      setLoading(false);
    }
  };

  // üîµ STEP 3: ƒê·ªîI M·∫¨T KH·∫®U
  const resetPassword = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      await api.put("/public/users/reset-password", {
        email,
        newPassword,
      });

      Swal.fire({
        icon: "success",
        title: "Th√†nh c√¥ng üéâ",
        text: "M·∫≠t kh·∫©u ƒë√£ ƒë∆∞·ª£c c·∫≠p nh·∫≠t",
        timer: 1500,
        showConfirmButton: false,
      });

      navigate("/login");
    } catch (err) {
      Swal.fire({
        icon: "error",
        title: "L·ªói",
        text: "Kh√¥ng th·ªÉ ƒë·ªïi m·∫≠t kh·∫©u",
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mt-5" style={{ maxWidth: 400 }}>
      <h3 className="text-center mb-3">Qu√™n m·∫≠t kh·∫©u üîê</h3>

      {/* STEP 1 */}
      {step === 1 && (
        <form onSubmit={sendOtp}>
          <div className="mb-3">
            <label>Email</label>
            <input
              type="email"
              className="form-control"
              required
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>

          <button className="btn btn-primary w-100" disabled={loading}>
            {loading ? "ƒêang g·ª≠i..." : "G·ª≠i m√£ OTP"}
          </button>
        </form>
      )}

      {/* STEP 2 */}
      {step === 2 && (
        <form onSubmit={verifyOtp}>
          <div className="mb-3">
            <label>M√£ OTP</label>
            <input
              type="text"
              className="form-control"
              required
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
            />
          </div>

          <button className="btn btn-success w-100" disabled={loading}>
            {loading ? "ƒêang x√°c nh·∫≠n..." : "X√°c nh·∫≠n OTP"}
          </button>
        </form>
      )}

      {/* STEP 3 */}
      {step === 3 && (
        <form onSubmit={resetPassword}>
          <div className="mb-3">
            <label>M·∫≠t kh·∫©u m·ªõi</label>
            <input
              type="password"
              className="form-control"
              required
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
            />
          </div>

          <button className="btn btn-warning w-100" disabled={loading}>
            {loading ? "ƒêang c·∫≠p nh·∫≠t..." : "ƒê·∫∑t l·∫°i m·∫≠t kh·∫©u"}
          </button>
        </form>
      )}
    </div>
  );
};

export default ForgotPassword;
