import React, { useEffect, useState } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import userService from "../services/userService";
import categoryService from "../services/categoryService";
import bannerService from "../services/bannerService";

// ‚úÖ Import CSS, Font, Bootstrap
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";
import "../assets/fonts/fontawesome/css/all.min.css";

// ‚úÖ Import h√¨nh ·∫£nh
import flagVN from "../assets/images/icons/flags/VN.webp";

const IMAGE_BASE_URL = "http://localhost:8080/api/public/banners/image";

const Header = () => {
  const [user, setUser] = useState(null);
  const [userInfo, setUserInfo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [categories, setCategories] = useState([]);
  const [loadingCategories, setLoadingCategories] = useState(false);
  const [logo, setLogo] = useState(null);
  const navigate = useNavigate();
  const location = useLocation();

  // ‚úÖ H√†m ki·ªÉm tra active menu
  const isActive = (path) => {
    return location.pathname === path;
  };

  // ‚úÖ H√†m ki·ªÉm tra active cho category
  const isCategoryActive = () => {
    return location.pathname === '/product' && location.state?.selectedCategory;
  };

  // ‚úÖ L·∫•y user t·ª´ localStorage v√† fetch th√¥ng tin m·ªõi nh·∫•t
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      try {
        const parsedUser = JSON.parse(storedUser);
        console.log("üë§ User from localStorage:", parsedUser);
        setUser(parsedUser);

        // ‚úÖ LU√îN FETCH TH√îNG TIN USER M·ªöI NH·∫§T T·ª™ API
        if (parsedUser.userId && typeof parsedUser.userId === 'number') {
          setLoading(true);

          userService.getUserById(parsedUser.userId)
            .then((userData) => {
              console.log("‚úÖ User data from API:", userData);

              // ‚úÖ S·ª¨A: S·ª≠ d·ª•ng ƒë√∫ng field names t·ª´ API response
              const updatedUser = {
                ...parsedUser,
                // ‚úÖ QUAN TR·ªåNG: S·ª≠ d·ª•ng ƒë√∫ng field names t·ª´ API
                firstName: userData.firstName || userData.first_name || "",
                lastName: userData.lastName || userData.last_name || "",
                email: userData.email,
                mobileNumber: userData.mobileNumber || userData.mobile_number || ""
              };

              setUserInfo({
                firstName: updatedUser.firstName,
                lastName: updatedUser.lastName,
                email: updatedUser.email
              });

              // ‚úÖ C·∫¨P NH·∫¨T LOCALSTORAGE V·ªöI D·ªÆ LI·ªÜU M·ªöI
              localStorage.setItem("user", JSON.stringify(updatedUser));
              console.log("üíæ Updated user in localStorage:", updatedUser);
            })
            .catch((err) => {
              console.error("‚ùå L·ªói l·∫•y th√¥ng tin user:", err);
              // Fallback: s·ª≠ d·ª•ng data t·ª´ localStorage
              if (parsedUser.firstName || parsedUser.first_name) {
                setUserInfo({
                  firstName: parsedUser.firstName || parsedUser.first_name || "",
                  lastName: parsedUser.lastName || parsedUser.last_name || "",
                  email: parsedUser.email
                });
              }
            })
            .finally(() => {
              setLoading(false);
            });
        } else {
          // Fallback cho user kh√¥ng c√≥ userId
          if (parsedUser.firstName || parsedUser.first_name) {
            setUserInfo({
              firstName: parsedUser.firstName || parsedUser.first_name || "",
              lastName: parsedUser.lastName || parsedUser.last_name || "",
              email: parsedUser.email
            });
          }
        }
      } catch (error) {
        console.error("‚ùå Parse user l·ªói:", error);
        setLoading(false);
      }
    }
  }, []);

  // ‚úÖ L·∫•y danh s√°ch categories t·ª´ API
  useEffect(() => {
    fetchCategories();
  }, []);

  // ‚úÖ L·∫•y logo t·ª´ bannerService
  useEffect(() => {
    fetchLogo();
  }, []);

  const fetchCategories = async () => {
    try {
      setLoadingCategories(true);
      const categoriesData = await categoryService.getAllCategories();
      setCategories(categoriesData || []);
    } catch (error) {
      console.error('‚ùå L·ªói khi l·∫•y danh m·ª•c:', error);
      setCategories([]);
    } finally {
      setLoadingCategories(false);
    }
  };

  const fetchLogo = async () => {
    try {
      const banners = await bannerService.getAllBanners();
      const logoBanner = banners.find(banner => 
        banner.bannerId === 4 || banner.id === 4
      );

      if (logoBanner) {
        setLogo(logoBanner);
      } else if (banners.length > 0) {
        setLogo(banners[0]);
      }
    } catch (error) {
      console.error('‚ùå L·ªói khi l·∫•y logo:', error);
    }
  };

  const getLogoUrl = () => {
    if (!logo) return "https://via.placeholder.com/120x120/667eea/ffffff?text=LOGO";

    const imageName = logo.imageUrl || logo.image_url;

    if (!imageName) {
      return "https://via.placeholder.com/120x120/667eea/ffffff?text=LOGO";
    }

    if (imageName.startsWith('http')) {
      return imageName;
    }

    return `${IMAGE_BASE_URL}/${imageName}`;
  };

  // ‚úÖ ƒêi·ªÅu h∆∞·ªõng ƒë·∫øn trang s·∫£n ph·∫©m theo category
  const handleCategoryClick = (category) => {
    const categoryId = category.categoryId || category.id;
    const categoryName = category.categoryName || category.name || category.title || 'Danh m·ª•c';

    if (categoryId) {
      navigate(`/product`, { 
        state: { 
          selectedCategory: categoryId,
          categoryName: categoryName
        }
      });
    }
  };

  // ‚úÖ ƒêƒÉng xu·∫•t
  const handleLogout = () => {
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    setUser(null);
    setUserInfo(null);
    navigate("/login");
  };

  // ‚úÖ Chuy·ªÉn ƒë·∫øn trang ch·ªânh s·ª≠a profile
  const handleProfileClick = () => {
    if (user) {
      navigate("/profile");
    } else {
      navigate("/login");
    }
  };

  // ‚úÖ H√†m hi·ªÉn th·ªã t√™n ng∆∞·ªùi d√πng - S·ª¨A L·∫†I PH·∫¶N N√ÄY
  const renderUserName = () => {
    if (!user) return "My profile";

    if (loading) {
      return (
        <div className="d-flex align-items-center">
          <div className="spinner-border spinner-border-sm me-2" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          Loading...
        </div>
      );
    }

    // ‚úÖ ∆ØU TI√äN HI·ªÇN TH·ªä FIRSTNAME + LASTNAME
    if (userInfo && (userInfo.firstName || userInfo.lastName)) {
      const firstName = userInfo.firstName || "";
      const lastName = userInfo.lastName || "";
      const fullName = `${firstName} ${lastName}`.trim();

      if (fullName) {
        console.log("üéØ Displaying full name:", fullName);
        return fullName;
      }
    }

    // ‚úÖ FALLBACK: Ki·ªÉm tra tr·ª±c ti·∫øp t·ª´ user object
    if (user.firstName || user.first_name) {
      const firstName = user.firstName || user.first_name || "";
      const lastName = user.lastName || user.last_name || "";
      const fullName = `${firstName} ${lastName}`.trim();

      if (fullName) {
        console.log("üéØ Displaying full name from user object:", fullName);
        return fullName;
      }
    }

    // ‚úÖ FALLBACK CU·ªêI C√ôNG: Hi·ªÉn th·ªã email
    if (user.email) {
      console.log("üìß Displaying email as fallback:", user.email);
      return user.email.split('@')[0]; // Ch·ªâ hi·ªÉn th·ªã ph·∫ßn tr∆∞·ªõc @
    }

    return "User";
  };

  // ‚úÖ H√†m hi·ªÉn th·ªã l·ªùi ch√†o - S·ª¨A L·∫†I
  const renderGreeting = () => {
    if (!user) return null;

    // ‚úÖ ∆ØU TI√äN FIRSTNAME
    if (userInfo && userInfo.firstName) {
      return `üëã Hi, ${userInfo.firstName}`;
    }

    // ‚úÖ FALLBACK: t·ª´ user object
    if (user.firstName || user.first_name) {
      const firstName = user.firstName || user.first_name;
      return `üëã Hi, ${firstName}`;
    }

    // ‚úÖ FALLBACK CU·ªêI: email
    if (user.email) {
      return `üëã Hi, ${user.email.split('@')[0]}`;
    }

    return "üëã Hi, User";
  };

  // ‚úÖ H√†m hi·ªÉn th·ªã t√™n category
  const getCategoryName = (category) => {
    return category.categoryName || category.name || category.title || 'Danh m·ª•c';
  };

  // ‚úÖ H√†m get menu style v·ªõi active state
  const getMenuStyle = (isActive) => {
    const baseStyle = {
      padding: '0.5rem 1.5rem',
      borderRadius: '25px',
      transition: 'all 0.3s ease',
      fontWeight: '600'
    };

    if (isActive) {
      return {
        ...baseStyle,
        backgroundColor: '#667eea',
        color: 'white',
        transform: 'scale(1.05)'
      };
    }

    return {
      ...baseStyle,
      backgroundColor: 'transparent',
      color: '#212529'
    };
  };

  // ‚úÖ H√†m get dropdown style v·ªõi active state
  const getDropdownStyle = (isActive) => {
    const baseStyle = {
      padding: '0.5rem 1.5rem',
      borderRadius: '25px',
      transition: 'all 0.3s ease',
      fontWeight: '600',
      cursor: 'pointer'
    };

    if (isActive) {
      return {
        ...baseStyle,
        backgroundColor: '#667eea',
        color: 'white',
        transform: 'scale(1.05)'
      };
    }

    return {
      ...baseStyle,
      backgroundColor: 'transparent',
      color: '#212529'
    };
  };

  return (
    <header className="section-header">
      {/* ======= THANH TR√äN ======= */}
   <nav className="navbar navbar-expand-lg navbar-light border-bottom">
  <div className="container">
    {/* Button toggle for mobile */}
    <button 
      className="navbar-toggler" 
      type="button" 
      data-bs-toggle="collapse" 
      data-bs-target="#navbarTop4"
      aria-controls="navbarTop4" 
      aria-expanded="false" 
      aria-label="Toggle navigation"
    >
      <span className="navbar-toggler-icon"></span>
    </button>

    <div className="collapse navbar-collapse" id="navbarTop4">
      <ul className="navbar-nav me-auto">
        <li className="nav-item">
          {user ? (
            <span className="nav-link">
              {renderGreeting()} |{" "}
              <button
                onClick={handleLogout}
                className="btn btn-link p-0 text-danger"
                style={{ textDecoration: "none" }}
              >
                Logout
              </button>
            </span>
          ) : (
            <span className="nav-link">
              Hi,{" "}
              <Link 
                to="/login" 
                className="text-decoration-none fw-semibold"
                style={{ margin: '0 8px' }}
              >
                Log In
              </Link>{" "}
              or{" "}
              <Link 
                to="/register" 
                className="text-decoration-none fw-semibold"
                style={{ margin: '0 8px' }}
              >
                Register
              </Link>
            </span>
          )}
        </li>
        <li className="nav-item">
          <a href="#" className="nav-link" style={{ padding: '8px 16px' }}>
            Deals
          </a>
        </li>
        <li className="nav-item">
          <a href="#" className="nav-link" style={{ padding: '8px 16px' }}>
            Sell
          </a>
        </li>
        <li className="nav-item">
          <a href="#" className="nav-link" style={{ padding: '8px 16px' }}>
            Help
          </a>
        </li>
      </ul>

      <ul className="navbar-nav">
        <li className="nav-item">
          <a href="#" className="nav-link" style={{ padding: '8px 16px' }}>
            <img src={flagVN} height="20" alt="flag" className="me-2" /> 
            Ship to
          </a>
        </li>
        <li className="nav-item dropdown">
          <a
            href="#"
