import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import userService from "../../services/userService";

const EditProfile = () => {
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({
    first_name: "",
    last_name: "",
    email: "",
    mobile_number: ""
  });
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);
  const [message, setMessage] = useState({ type: "", text: "" });
  const navigate = useNavigate();

  useEffect(() => {
    console.log("üîÑ EditProfile Component Mounted");

    const storedUser = localStorage.getItem("user");
    const storedToken = localStorage.getItem("token");

    console.log("üîç DEBUG STORAGE:", {
      user: storedUser ? JSON.parse(storedUser) : "NO USER",
      token: storedToken ? `EXISTS (${storedToken.length} chars)` : "NO TOKEN",
      userId: storedUser ? (JSON.parse(storedUser).userId || JSON.parse(storedUser).user_id) : "NO ID"
    });

    if (!storedUser) {
      console.log("‚ùå NO USER - REDIRECTING TO LOGIN");
      setMessage({ 
        type: "error", 
        text: "Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ ch·ªânh s·ª≠a h·ªì s∆°" 
      });
      setTimeout(() => navigate("/login"), 2000);
      return;
    }

    const initializeUser = async () => {
      try {
        const parsedUser = JSON.parse(storedUser);
        setUser(parsedUser);

        const userId = parsedUser.userId || parsedUser.user_id;
        console.log("üë§ User ID from storage:", userId);

        // KI·ªÇM TRA USER ID C√ì PH·∫¢I L√Ä TEMP KH√îNG
        if (userId && userId.toString().startsWith('temp_')) {
          console.log("‚ö†Ô∏è TEMPORARY USER ID DETECTED - User may not be properly registered");
          setMessage({ 
            type: "warning", 
            text: "T√†i kho·∫£n t·∫°m th·ªùi. Vui l√≤ng ƒëƒÉng nh·∫≠p l·∫°i." 
          });
