import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import productService from '../../services/productService';
import postService from '../../services/postService';

const ProductSold = () => {
  const [topProducts, setTopProducts] = useState([]);
  const [allProducts, setAllProducts] = useState([]);
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(0);

  const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

  useEffect(() => {
    fetchInitialData();
  }, []);

  const fetchInitialData = async () => {
    setLoading(true);
    try {
      // 1. L·∫•y top s·∫£n ph·∫©m b√°n ch·∫°y
      const topRes = await productService.getTopSellingProducts(0, 8);
      setTopProducts(topRes.content || []);

      // 2. L·∫•y danh s√°ch s·∫£n ph·∫©m th√¥ng th∆∞·ªùng
      const allRes = await productService.getAllProducts(0, 8);
      setAllProducts(allRes.content || []);

      // 3. L·∫•y tin t·ª©c t·ª± ƒë·ªông
      const externalPosts = await postService.getExternalPosts();
      setPosts(externalPosts.slice(0, 3)); // Ch·ªâ l·∫•y 3 b√†i m·ªõi nh·∫•t
    } catch (err) {
      console.error('L·ªói t·∫£i d·ªØ li·ªáu:', err);
    } finally {
      setLoading(false);
    }
  };

  const loadMoreProducts = async () => {
    const nextPage = page + 1;
    try {
      const res = await productService.getAllProducts(nextPage, 8);
      setAllProducts([...allProducts, ...(res.content || [])]);
      setPage(nextPage);
    } catch (err) {
      console.error('L·ªói t·∫£i th√™m s·∫£n ph·∫©m:', err);
    }
  };

  // Component con hi·ªÉn th·ªã Card S·∫£n ph·∫©m ƒë·ªÉ t√°i s·ª≠ d·ª•ng
  const ProductItem = ({ p, index, isTop }) => {
    const productId = p.productId || p.product_id;
    return (
      <div className="col-xl-3 col-lg-4 col-md-6 col-12 mb-4">
        <div className="card card-product-grid shadow-sm h-100 border-0 hover-up">
          {isTop && index < 3 && (
            <div className="position-absolute top-0 start-0 m-2 badge bg-warning text-dark shadow" style={{zIndex: 5}}>
              #{index + 1} Best Seller
            </div>
          )}
          <div className="img-wrap p-3 text-center">
            <Link to={`/product/${productId}`}>
              <img
                src={`${IMAGE_BASE_URL}/${p.image}`}
                alt={p.productName}
                onError={(e) => (e.target.src = "https://cdn-icons-png.flaticon.com/512/679/679922.png")}
                className="img-fluid"
                style={{ height: "200px", objectFit: "contain" }}
              />
            </Link>
          </div>
          <div className="info-wrap p-3 pt-0">
            <Link to={`/product/${productId}`} className="title text-dark fw-bold text-decoration-none d-block mb-2 text-truncate">
              {p.productName}
            </Link>
            <div className="price-wrap mb-2">
              <span className="price text-danger fw-bold h5">{(p.specialPrice || p.price).toLocaleString()} ‚Ç´</span>
              {p.specialPrice && <del className="text-muted ms-2 small">{p.price.toLocaleString()} ‚Ç´</del>}
            </div>
            <div className="d-flex justify-content-between align-items-center small text-muted mb-3">
              <span>ƒê√£ b√°n: {p.sold || 0}</span>
              <span><i className="fa fa-eye"></i> {p.views || 0}</span>
            </div>
            <Link to={`/product/${productId}`} className="btn btn-orange-outline w-100 rounded-pill btn-sm">
               Xem chi ti·∫øt
            </Link>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="main-content-wrapper bg-light pb-5">

      {/* SECTION 1: TOP SELLING */}
      <section className="container pt-5">
        <div className="section-heading-custom mb-4">
          <h3 className="fw-bold text-orange"><i className="fa fa-fire me-2"></i>S·∫¢N PH·∫®M B√ÅN CH·∫†Y</h3>
          <p className="text-muted">ƒê∆∞·ª£c c√°c m·∫π tin d√πng nhi·ªÅu nh·∫•t trong th√°ng</p>
        </div>
        <div className="row">
          {topProducts.map((p, i) => <ProductItem key={`top-${i}`} p={p} index={i} isTop={true} />)}
        </div>
      </section>

      {/* SECTION 2: DISCOVER ALL */}
      <section className="container mt-5">
        <div className="section-heading-custom mb-4">
          <h3 className="fw-bold text-dark">KH√ÅM PH√Å TH·∫æ GI·ªöI C·ª¶A B√â</h3>
          <p className="text-muted">H√†ng ng√†n s·∫£n ph·∫©m ch·∫•t l∆∞·ª£ng ƒëang ch·ªù ƒë√≥n</p>
        </div>
        <div className="row">
          {allProducts.map((p, i) => <ProductItem key={`all-${i}`} p={p} isTop={false} />)}
        </div>
        <div className="text-center mt-4">
          <button className="btn btn-orange px-5 rounded-pill shadow-sm" onClick={loadMoreProducts} disabled={loading}>
            {loading ? "ƒêang t·∫£i..." : "Xem th√™m s·∫£n ph·∫©m"}
          </button>
        </div>
      </section>

      {/* SECTION 3: AUTOMATIC BLOG POSTS */}
      <section className="container mt-5 pt-5 border-top">
        <div className="d-flex justify-content-between align-items-center mb-4">
          <div>
            <h3 className="fw-bold text-dark mb-1">üí° G√ìC CHIA S·∫∫ KINH NGHI·ªÜM</h3>
            <p className="text-muted mb-0">C·∫≠p nh·∫≠t tin t·ª©c m·∫π v√† b√© m·ªõi nh·∫•t m·ªói ng√†y</p>
          </div>
          <span className="badge bg-success p-2">Tin t·ª± ƒë·ªông</span>
        </div>

        <div className="row">
          {posts.map((post, idx) => (
            <div key={idx} className="col-md-4 mb-4">
              <div className="card border-0 shadow-sm h-100 overflow-hidden post-card">
                <div className="position-relative">
                  <img src={post.img} className="card-img-top" alt={post.title} style={{height: '200px', objectFit: 'cover'}} />
                  <div className="post-date-tag">{post.date}</div>
                </div>
                <div className="card-body">
                  <h6 className="card-title fw-bold" style={{minHeight: '45px'}}>{post.title}</h6>
                  <p className="card-text text-muted small" style={{minHeight: '60px'}}>{post.desc}</p>
                  <a href={post.link} target="_blank" rel="noopener noreferrer" className="btn btn-link p-0 text-orange fw-bold text-decoration-none">
                    ƒê·ªçc to√†n b·ªô b√†i vi·∫øt <i className="fa fa-angle-double-right ms-1"></i>
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default ProductSold;
