import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

const GeminiChat = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [messages, setMessages] = useState([
        { role: 'ai', text: 'Ch√†o n√†ng! Em l√† Ng·ªçc Anh Beauty AI, n√†ng c·∫ßn t∆∞ v·∫•n m·ªπ ph·∫©m g√¨ c·ª© b·∫£o em nh√©! ‚ù§Ô∏è' }
    ]);
    const [input, setInput] = useState('');
    const [loading, setLoading] = useState(false);
    const scrollRef = useRef(null);

    // T·ª± ƒë·ªông cu·ªôn xu·ªëng khi c√≥ tin nh·∫Øn m·ªõi
    useEffect(() => {
        if (scrollRef.current) {
            scrollRef.current.scrollIntoView({ behavior: 'smooth' });
        }
    }, [messages, loading]);

    const handleSend = async () => {
        if (!input.trim() || loading) return;

        const userMsg = input.trim();
        setMessages(prev => [...prev, { role: 'user', text: userMsg }]);
        setInput('');
        setLoading(true);

        try {
            // G·ªçi API Backend (Ch·ªâ g·ª≠i message, kh√¥ng c·∫ßn CartId n·ªØa)
            const res = await axios.post('http://localhost:8080/api/ai/ask', {
                message: userMsg
            });

            const { reply, suggested_products } = res.data;

            setMessages(prev => [...prev, { 
                role: 'ai', 
                text: reply || 'Em nghe ƒë√¢y, n√†ng n√≥i ti·∫øp ƒëi...',
                suggestions: suggested_products || [] // Nh·∫≠n danh s√°ch s·∫£n ph·∫©m t·ª´ BE
            }]);

        } catch (error) {
            console.error("L·ªói Chat AI:", error);
            setMessages(prev => [...prev, { role: 'ai', text: 'Hic, k·∫øt n·ªëi b·∫≠n r·ªìi, n√†ng th·ª≠ l·∫°i nh√©! üòÖ' }]);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={{ position: 'fixed', bottom: '20px', right: '20px', zIndex: 1050 }}>
            {/* N√∫t b·∫•m m·ªü Chat */}
            <button onClick={() => setIsOpen(!isOpen)} className="btn btn-warning rounded-circle shadow-lg p-0" style={{ width: '60px', height: '60px' }}>
                <i className={`bi ${isOpen ? 'bi-x-lg' : 'bi-chat-dots-fill'} fs-3 text-white`}></i>
            </button>

            {/* C·ª≠a s·ªï Chat */}
            {isOpen && (
                <div className="card shadow-lg border-0 rounded-4 mt-2" style={{ width: '380px', height: '550px', display: 'flex', flexDirection: 'column', overflow: 'hidden', backgroundColor: '#fff' }}>
                    
                    {/* Header */}
                    <div className="card-header bg-warning text-white py-3 px-3 border-0">
                        <div className="d-flex align-items-center">
                            <div className="bg-white rounded-circle p-1 me-2 shadow-sm d-flex justify-content-center align-items-center" style={{width: '35px', height: '35px'}}>
                                <i className="bi bi-robot text-warning fs-5"></i>
                            </div>
                            <div>
                                <h6 className="mb-0 fw-bold">Ng·ªçc Anh Beauty AI</h6>
                                <small className="text-white-50">T∆∞ v·∫•n vi√™n 24/7</small>
                            </div>
                        </div>
                    </div>

                    {/* N·ªôi dung Chat */}
                    <div className="card-body p-3 overflow-auto" style={{ flex: 1, backgroundColor: '#f8f9fa' }}>
                        {messages.map((msg, idx) => (
                            <div key={idx} className={`d-flex flex-column mb-3 ${msg.role === 'user' ? 'align-items-end' : 'align-items-start'}`}>
                                
                                {/* Bong b√≥ng chat text */}
                                <div className={`p-2 px-3 rounded-4 shadow-sm ${msg.role === 'user' ? 'bg-warning text-white' : 'bg-white text-dark border'}`} 
                                     style={{ maxWidth: '85%', fontSize: '14px', whiteSpace: 'pre-line' }}>
                                    {msg.text}
                                </div>
                                
                                {/* Danh s√°ch s·∫£n ph·∫©m g·ª£i √Ω (Card tr∆∞·ª£t ngang) */}
                                {msg.suggestions && msg.suggestions.length > 0 && (
                                    <div className="w-100 mt-2 d-flex overflow-auto pb-2 custom-scrollbar" style={{ gap: '12px' }}>
                                        {msg.suggestions.map((p, i) => (
                                            <div key={i} className="card border-0 shadow-sm flex-shrink-0" 
                                                 style={{ width: '160px', borderRadius: '15px', overflow: 'hidden', backgroundColor: '#fff' }}>
                                                
                                                {/* ·∫¢nh s·∫£n ph·∫©m */}
                                                <div style={{ height: '120px', backgroundColor: '#f0f0f0' }}>
                                                    <img 
                                                        src={`http://localhost:8080/api/public/products/image/${p.image}`} 
                                                        style={{ width: '100%', height: '100%', objectFit: 'cover' }}
                                                        alt={p.productName}
                                                        onError={(e) => { e.target.src = 'https://via.placeholder.com/160x120?text=No+Image' }}
                                                    />
                                                </div>

                                                {/* Th√¥ng tin s·∫£n ph·∫©m */}
                                                <div className="p-2">
                                                    <div className="small fw-bold text-dark text-truncate mb-1" title={p.productName}>
                                                        {p.productName}
                                                    </div>
                                                    <div className="d-flex justify-content-between align-items-center">
                                                        <span className="small text-danger fw-bold">
                                                            {(p.specialPrice > 0 ? p.specialPrice : p.price).toLocaleString()}‚Ç´
                                                        </span>
                                                        {/* N√∫t xem chi ti·∫øt */}
                                                        <a href={`/product/${p.productId}`} className="btn btn-sm btn-outline-warning rounded-pill px-2 py-0" style={{ fontSize: '10px' }}>
                                                            Xem
                                                        </a>
                                                    </div>
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                )}
                            </div>
                        ))}

                        {/* Loading indicator */}
                        {loading && (
                            <div className="text-muted small mb-3">
                                <span className="spinner-border spinner-border-sm me-2 text-warning"></span> ƒêang t√¨m m·ªπ ph·∫©m cho n√†ng...
                            </div>
                        )}
                        <div ref={scrollRef} />
                    </div>

                    {/* √î nh·∫≠p tin nh·∫Øn */}
                    <div className="card-footer bg-white border-top-0 p-3">
                        <div className="input-group bg-light rounded-pill px-3 py-1 border">
                            <input 
                                type="text" 
                                className="form-control border-0 bg-transparent shadow-none" 
                                placeholder="N√†ng mu·ªën t√¨m g√¨ h√¥m nay?" 
                                value={input} 
                                onChange={(e) => setInput(e.target.value)} 
                                onKeyDown={(e) => e.key === 'Enter' && handleSend()} 
                            />
                            <button onClick={handleSend} className="btn border-0 text-warning px-0" disabled={loading}>
                                <i className="bi bi-send-fill fs-5"></i>
                            </button>
                        </div>
                    </div>
                </div>
            )}
            
            {/* CSS ·∫©n thanh cu·ªôn ƒë·ªÉ giao di·ªán m∆∞·ª£t m√† */}
            <style>{`
                .custom-scrollbar::-webkit-scrollbar { display: none; }
                .custom-scrollbar { -ms-overflow-style: none; scrollbar-width: none; }
                .card-body { scrollbar-width: thin; scrollbar-color: #ffc107 #f8f9fa; }
            `}</style>
        </div>
    );
};

export default GeminiChat;
