import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import authService from "../../services/authService";

const Login = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await authService.login(form);
      console.log("LOGIN RESULT:", res.data);

      // üü© Gi·∫£ s·ª≠ backend tr·∫£ v·ªÅ d·ªØ li·ªáu d·∫°ng:
      // { user: { id, first_name, email, ... }, token: "xxx" }
      const userData = res.data.user || {};
      const token = res.data.token || "";

      // ‚úÖ L∆∞u v√†o localStorage cho Header ƒë·ªçc
      localStorage.setItem("user", JSON.stringify(userData));
      if (token) {
        localStorage.setItem("token", token);
      }

      alert(`‚úÖ Ch√†o m·ª´ng ${userData.first_name || "b·∫°n"} ƒë√£ ƒëƒÉng nh·∫≠p!`);

      // ‚úÖ Quay v·ªÅ trang ch·ªß sau khi ƒëƒÉng nh·∫≠p
      navigate("/");
      window.location.reload(); // reload l·∫°i ƒë·ªÉ Header c·∫≠p nh·∫≠t ngay
    } catch (err) {
      console.error("LOGIN ERROR:", err);
      alert("‚ùå ƒêƒÉng nh·∫≠p th·∫•t b·∫°i! Vui l√≤ng ki·ªÉm tra l·∫°i.");
    }
  };

  return (
    <div
      className="card mx-auto"
      style={{
        maxWidth: "400px",
        marginTop: "100px",
        padding: "20px",
        boxShadow: "0 0 10px rgba(0,0,0,0.1)",
        borderRadius: "10px",
      }}
    >
      <div className="card-body">
        <h3 className="card-title mb-4 text-center">Sign In</h3>

        <form onSubmit={handleSubmit}>
          <div className="form-group mb-3">
            <input
              name="email"
              className="form-control"
              placeholder="Nh·∫≠p email..."
              type="email"
