        <div className="text-center mt-3">
          <span>Ch∆∞a c√≥ t√†i kho·∫£n? </span>
          <a href="/register">ƒêƒÉng k√Ω ngay</a>
        </div>
      </div>
    </div>
  );
};

export default Login;
