import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BannerTwo = () => {
  const [banner, setBanner] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchBanner();
  }, []);

  const fetchBanner = async () => {
    try {
      // Lấy banner ID 6 trực tiếp
      const response = await axios.get('http://localhost:8080/api/public/banners/6');
      setBanner(response.data);
    } catch (err) {
      console.error('Lỗi lấy banner:', err);
      // Nếu ID 6 không tồn tại, lấy banner đầu tiên
      try {
        const allResponse = await axios.get('http://localhost:8080/api/public/banners?size=1');
        if (allResponse.data?.content?.length > 0) {
          setBanner(allResponse.data.content[0]);
        }
      } catch (fallbackErr) {
        console.error('Lỗi lấy banner fallback:', fallbackErr);
      }
    } finally {
      setLoading(false);
    }
  };

  const getImageUrl = () => {
    if (!banner?.imageUrl) return 'https://via.placeholder.com/1920x400/667eea/ffffff?text=Banner+Image';
    return `http://localhost:8080/api/public/banners/image/${banner.imageUrl}`;
  };

  if (loading) {
    return (
      <section className="banner-two-section">
        <div className="placeholder-glow">
          <div className="placeholder" style={{ height: '400px', width: '100%' }}></div>
        </div>
      </section>
    );
  }

  if (!banner) {
    return null;
  }

  return (
    <section className="banner-two-section">
      {/* Container-fluid để tràn full width */}
      <div className="container-fluid p-0">
        <div className="banner-full-width">
          <img
            src={getImageUrl()}
            alt={banner.title || 'Banner'}
            className="img-fluid w-100"
            style={{ 
              height: '400px',
              objectFit: 'cover',
              objectPosition: 'center'
            }}
          />
        </div>
      </div>
    </section>
  );
};

export default BannerTwo;
