import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import bannerService from "../../services/bannerService";

const IMAGE_BASE_URL = "http://localhost:8080/api/public/banners/image";

const Banner = () => {
  const [banner, setBanner] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    loadBanner();
  }, []);

 const loadBanner = async () => {
  try {
    setLoading(true);
    setError("");

    const data = await bannerService.getActiveBanners();
    console.log("üìå Response:", data);

    const bannersArr = Array.isArray(data?.content) ? data.content : [];

    if (bannersArr.length > 0) {
      setBanner(bannersArr[0]); // ‚úÖ ch·ªâ l·∫•y banner ƒë·∫ßu ti√™n
    } else {
      setBanner(null);
    }

  } catch (err) {
    setError("Kh√¥ng th·ªÉ t·∫£i banner");
  } finally {
    setLoading(false);
  }
};


  const getImageUrl = (banner) => {
    if (!banner) return "";

    const imageName = banner.imageUrl || banner.image_url || banner.imageName;

    if (!imageName) return "";

    if (imageName.startsWith("http")) return imageName;

    return `${IMAGE_BASE_URL}/${imageName}`;
  };

  if (loading) {
    return (
      <div className="text-center py-5">
        <div className="spinner-border text-primary"></div>
        <p>ƒêang t·∫£i banner...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-5 text-danger">
        {error}
      </div>
    );
  }

  if (!banner) {
    return (
      <div className="text-center py-5 text-muted">
        Ch∆∞a c√≥ banner
      </div>
    );
  }

  return (
    <section className="banner-section mb-5">
      <div className="container-fluid px-0">
        <div
          className="w-100 d-flex align-items-center justify-content-center text-white"
          style={{
            background: banner.backgroundColor || "#FFEBCC",
            minHeight: "400px",
          }}
        >
          <div className="row w-100 m-0 p-4 align-items-center">

            {/* LEFT CONTENT */}
            <div className="col-lg-6">
              <h1 className="fw-bold mb-3">
                {banner.title || "Banner"}
              </h1>

              {banner.description && (
                <p className="mb-4">{banner.description}</p>
              )}

              {banner.buttonText && (
                <Link
                  to={banner.targetUrl || "/products"}
                  className="btn btn-primary btn-lg"
                >
                  {banner.buttonText}
                </Link>
              )}
            </div>

            {/* RIGHT IMAGE */}
            <div className="col-lg-6 text-center">
              <img
                src={getImageUrl(banner)}
                alt={banner.title || "banner"}
                className="img-fluid rounded shadow"
                style={{
                  maxHeight: "350px",
                  objectFit: "cover",
                }}
              />
            </div>

          </div>
        </div>
      </div>
    </section>
  );
};

export default Banner;
