// src/pages/cart/ProductCart.js
import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import cartService from '../../services/cartService';

const ProductCart = () => {
  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(true);
  const [updating, setUpdating] = useState(false);
  const [updatingProductId, setUpdatingProductId] = useState(null);
  const navigate = useNavigate();

  const currentUser = JSON.parse(localStorage.getItem('user')) || { email: 'guest@example.com' };

  useEffect(() => {
    loadCart();
  }, []);

  const loadCart = async () => {
    try {
      setLoading(true);
      console.log("üîÑ ƒêang t·∫£i gi·ªè h√†ng cho user:", currentUser.email);

      // S·ª≠ d·ª•ng getActiveCart v·ªõi fallback
      const cartData = await cartService.getActiveCart(currentUser.email);
      console.log("üì¶ D·ªØ li·ªáu gi·ªè h√†ng:", cartData);

      setCart(cartData);

      // C·∫≠p nh·∫≠t localStorage
      if (cartData) {
        cartService.updateCartInStorage(cartData);
      }

    } catch (error) {
      console.error('‚ùå L·ªói khi t·∫£i gi·ªè h√†ng:', error);

      // Th·ª≠ l·∫•y t·ª´ localStorage nh∆∞ fallback
      const localCart = cartService.getCartFromStorage();
      if (localCart) {
        console.log("üîÑ S·ª≠ d·ª•ng cart t·ª´ localStorage");
        setCart(localCart);
      } else {
        alert('‚ùå Kh√¥ng th·ªÉ t·∫£i gi·ªè h√†ng: ' + (error.response?.data?.message || error.message));
      }
    } finally {
      setLoading(false);
    }
  };

  const handleUpdateQuantity = async (productId, newQuantity) => {
    if (newQuantity < 1) return;

    try {
      setUpdating(true);
      setUpdatingProductId(productId);

      // L·∫•y cartId t·ª´ cart ho·∫∑c user
      let cartId = cart?.cartId;
      if (!cartId) {
        const user = JSON.parse(localStorage.getItem('user') || '{}');
        cartId = user.cart?.cartId;
      }

      if (!cartId) {
        alert('‚ùå Kh√¥ng t√¨m th·∫•y gi·ªè h√†ng');
        return;
      }

      console.log(`üîÑ C·∫≠p nh·∫≠t s·ªë l∆∞·ª£ng s·∫£n ph·∫©m ${productId} th√†nh ${newQuantity}`);

      await cartService.updateQuantity(cartId, productId, newQuantity);

      // Reload gi·ªè h√†ng ƒë·ªÉ c√≥ data m·ªõi nh·∫•t
      await loadCart();

      // C·∫≠p nh·∫≠t cart count global
      window.dispatchEvent(new Event('cartUpdated'));

    } catch (error) {
      console.error('‚ùå L·ªói khi c·∫≠p nh·∫≠t s·ªë l∆∞·ª£ng:', error);
      alert('‚ùå L·ªói khi c·∫≠p nh·∫≠t s·ªë l∆∞·ª£ng: ' + (error.response?.data?.message || error.message));
    } finally {
      setUpdating(false);
      setUpdatingProductId(null);
    }
  };

  const handleRemoveItem = async (productId) => {
    if (!window.confirm('B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a s·∫£n ph·∫©m n√†y kh·ªèi gi·ªè h√†ng?')) return;

    try {
      setUpdating(true);
      setUpdatingProductId(productId);

      // L·∫•y cartId t·ª´ cart ho·∫∑c user
      let cartId = cart?.cartId;
      if (!cartId) {
        const user = JSON.parse(localStorage.getItem('user') || '{}');
        cartId = user.cart?.cartId;
      }

      if (!cartId) {
        alert('‚ùå Kh√¥ng t√¨m th·∫•y gi·ªè h√†ng');
        return;
      }

      console.log(`üóëÔ∏è X√≥a s·∫£n ph·∫©m ${productId} kh·ªèi gi·ªè h√†ng`);

      await cartService.removeFromCart(cartId, productId);

      // Reload gi·ªè h√†ng
      await loadCart();

      // C·∫≠p nh·∫≠t cart count global
      window.dispatchEvent(new Event('cartUpdated'));

      alert('‚úÖ ƒê√£ x√≥a s·∫£n ph·∫©m kh·ªèi gi·ªè h√†ng');
    } catch (error) {
      console.error('‚ùå L·ªói khi x√≥a s·∫£n ph·∫©m:', error);
      alert('‚ùå L·ªói khi x√≥a s·∫£n ph·∫©m: ' + (error.response?.data?.message || error.message));
    } finally {
      setUpdating(false);
      setUpdatingProductId(null);
    }
  };

  const handleClearCart = async () => {
    if (!window.confirm('B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a t·∫•t c·∫£ s·∫£n ph·∫©m kh·ªèi gi·ªè h√†ng?')) return;

    try {
      setUpdating(true);

      // L·∫•y cartId t·ª´ cart ho·∫∑c user
      let cartId = cart?.cartId;
      if (!cartId) {
        const user = JSON.parse(localStorage.getItem('user') || '{}');
        cartId = user.cart?.cartId;
      }

      if (!cartId) {
        alert('‚ùå Kh√¥ng t√¨m th·∫•y gi·ªè h√†ng');
        return;
      }

      console.log(`üßπ X√≥a to√†n b·ªô gi·ªè h√†ng`);

      await cartService.clearCart(currentUser.email, cartId);

      // Reload gi·ªè h√†ng
      await loadCart();

      // C·∫≠p nh·∫≠t cart count global
      window.dispatchEvent(new Event('cartUpdated'));

      alert('‚úÖ ƒê√£ x√≥a to√†n b·ªô s·∫£n ph·∫©m kh·ªèi gi·ªè h√†ng');
    } catch (error) {
      console.error('‚ùå L·ªói khi x√≥a gi·ªè h√†ng:', error);
      alert('‚ùå L·ªói khi x√≥a gi·ªè h√†ng: ' + (error.response?.data?.message || error.message));
    } finally {
      setUpdating(false);
    }
  };

  // T√≠nh t·ªïng ti·ªÅn
  const totalAmount = cart ? cartService.calculateTotal(cart.products) : 0;
  const totalItems = cart ? cartService.getTotalItems(cart.products) : 0;

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="text-center py-5">
          <div className="spinner-border text-primary" style={{width: '3rem', height: '3rem'}} role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-3">ƒêang t·∫£i gi·ªè h√†ng...</p>
        </div>
      </div>
    );
  }

  if (!cart || !cart.products || cart.products.length === 0) {
    return (
      <div className="container mt-4">
        <div className="text-center py-5">
          <div className="empty-cart-icon mb-4">
            <i className="fas fa-shopping-cart text-muted" style={{ fontSize: '5rem', opacity: 0.5 }}></i>
          </div>
          <h3 className="text-muted mb-3">Gi·ªè h√†ng tr·ªëng</h3>
          <p className="text-muted mb-4">B·∫°n ch∆∞a c√≥ s·∫£n ph·∫©m n√†o trong gi·ªè h√†ng</p>
          <Link to="/product" className="btn btn-warning btn-lg">
            <i className="fas fa-shopping-bag me-2"></i>
            Ti·∫øp t·ª•c mua s·∫Øm
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      {/* Breadcrumb */}
      <nav aria-label="breadcrumb" className="mb-4">
        <ol className="breadcrumb">
          <li className="breadcrumb-item">
            <Link to="/" className="text-decoration-none">üè† Trang ch·ªß</Link>
          </li>
          <li className="breadcrumb-item active">Gi·ªè h√†ng</li>
        </ol>
      </nav>

      <div className="row">
        <div className="col-12">
          <div className="d-flex justify-content-between align-items-center mb-4">
            <h1 className="h2 text-primary mb-0">Gi·ªè h√†ng c·ªßa b·∫°n</h1>
            {cart.cartId && (
              <span className="text-muted">M√£ gi·ªè h√†ng: <strong>#{cart.cartId}</strong></span>
            )}
          </div>
        </div>
      </div>

      <div className="row">
        {/* Danh s√°ch s·∫£n ph·∫©m */}
        <div className="col-lg-8">
          <div className="card shadow-sm mb-4">
            <div className="card-header bg-white d-flex justify-content-between align-items-center">
              <h5 className="mb-0">
                <i className="fas fa-shopping-bag me-2"></i>
                S·∫£n ph·∫©m ({totalItems})
              </h5>
              <div>
                <button 
                  className="btn btn-outline-danger btn-sm me-2"
                  onClick={handleClearCart}
                  disabled={updating}
                >
                  <i className="fas fa-trash me-1"></i>
                  X√≥a t·∫•t c·∫£
                </button>
                <button 
                  className="btn btn-outline-secondary btn-sm"
                  onClick={loadCart}
                  disabled={updating}
                >
                  <i className="fas fa-sync-alt me-1"></i>
                  L√†m m·ªõi
                </button>
              </div>
            </div>
            <div className="card-body p-0">
              {cart.products.map((item, index) => {
                const itemTotal = (item.specialPrice || item.price || 0) * (item.quantity || 1);
                const isUpdating = updating && updatingProductId === item.productId;

                return (
                  <div key={item.productId || index} className="border-bottom p-3">
                    <div className="row align-items-center">
                      {/* H√¨nh ·∫£nh */}
                      <div className="col-md-2">
                        <img
                          src={item.image ? `http://localhost:8080/api/public/products/image/${item.image}` : 'https://cdn-icons-png.flaticon.com/512/679/679922.png'}
                          alt={item.productName}
                          className="img-fluid rounded shadow-sm"
                          style={{ width: '80px', height: '80px', objectFit: 'cover' }}
                          onError={(e) => {
                            e.target.src = 'https://cdn-icons-png.flaticon.com/512/679/679922.png';
                          }}
                        />
                      </div>

                      {/* Th√¥ng tin s·∫£n ph·∫©m */}
                      <div className="col-md-4">
                        <h6 className="mb-1">
                          <Link 
                            to={`/products/${item.productId}`}
                            className="text-decoration-none text-dark fw-semibold"
                          >
                            {item.productName || 'S·∫£n ph·∫©m kh√¥ng t·ªìn t·∫°i'}
                          </Link>
                        </h6>
                        {item.category && (
                          <p className="text-muted small mb-1">
                            {item.category.categoryName}
                          </p>
                        )}
                        <div className="d-flex align-items-center gap-2">
                          <span className="text-warning fw-bold">
                            {cartService.formatPrice(item.specialPrice || item.price)}
                          </span>
                          {item.specialPrice && item.price && item.specialPrice < item.price && (
                            <span className="text-muted text-decoration-line-through small">
                              {cartService.formatPrice(item.price)}
                            </span>
                          )}
                        </div>
                      </div>

                      {/* S·ªë l∆∞·ª£ng */}
                      <div className="col-md-3">
                        <div className="d-flex align-items-center justify-content-center">
                          <button
                            className="btn btn-outline-secondary btn-sm"
                            style={{ width: '35px', height: '35px' }}
                            onClick={() => handleUpdateQuantity(item.productId, (item.quantity || 1) - 1)}
                            disabled={isUpdating || (item.quantity || 1) <= 1}
                          >
                            <i className="fas fa-minus"></i>
                          </button>

                          <span className="mx-3 fw-bold fs-6">
                            {isUpdating ? (
                              <div className="spinner-border spinner-border-sm" role="status">
                                <span className="visually-hidden">Loading...</span>
                              </div>
                            ) : (
                              item.quantity || 1
                            )}
                          </span>

                          <button
                            className="btn btn-outline-secondary btn-sm"
                            style={{ width: '35px', height: '35px' }}
                            onClick={() => handleUpdateQuantity(item.productId, (item.quantity || 1) + 1)}
                            disabled={isUpdating}
                          >
                            <i className="fas fa-plus"></i>
                          </button>
                        </div>
                      </div>

                      {/* Th√†nh ti·ªÅn & Actions */}
                      <div className="col-md-3 text-end">
                        <p className="fw-bold text-danger h6 mb-2">
                          {cartService.formatPrice(itemTotal)}
                        </p>
                        <button
                          className="btn btn-outline-danger btn-sm"
                          onClick={() => handleRemoveItem(item.productId)}
                          disabled={isUpdating}
                        >
                          {isUpdating ? (
                            <div className="spinner-border spinner-border-sm me-1" role="status">
                              <span className="visually-hidden">Loading...</span>
                            </div>
                          ) : (
                            <i className="fas fa-trash me-1"></i>
                          )}
                          X√≥a
                        </button>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>

        {/* T·ªïng thanh to√°n */}
        <div className="col-lg-4">
          <div className="card shadow-sm sticky-top" style={{ top: '100px' }}>
            <div className="card-header bg-white">
              <h5 className="mb-0">
                <i className="fas fa-receipt me-2"></i>
                T·ªïng thanh to√°n
              </h5>
            </div>
            <div className="card-body">
              <div className="d-flex justify-content-between mb-2">
                <span>T·∫°m t√≠nh:</span>
                <span className="fw-semibold">{cartService.formatPrice(totalAmount)}</span>
              </div>

              <div className="d-flex justify-content-between mb-2">
                <span>Ph√≠ v·∫≠n chuy·ªÉn:</span>
                <span className="text-success fw-semibold">Mi·ªÖn ph√≠</span>
              </div>

              <div className="d-flex justify-content-between mb-2">
                <span>S·ªë l∆∞·ª£ng s·∫£n ph·∫©m:</span>
                <span className="fw-semibold">{totalItems}</span>
              </div>

              <hr />

              <div className="d-flex justify-content-between mb-4">
                <strong className="fs-5">T·ªïng c·ªông:</strong>
                <strong className="text-danger fs-4">
                  {cartService.formatPrice(totalAmount)}
                </strong>
              </div>

              <button 
                className="btn btn-warning w-100 py-3 fw-bold mb-3"
                onClick={() => navigate('/checkout')}
                disabled={updating}
              >
                <i className="fas fa-credit-card me-2"></i>
                TI·∫æN H√ÄNH THANH TO√ÅN
              </button>

              <Link 
                to="/product" 
                className="btn btn-outline-primary w-100"
              >
                <i className="fas fa-arrow-left me-2"></i>
                Ti·∫øp t·ª•c mua s·∫Øm
              </Link>

              <div className="mt-3 text-center">
                <small className="text-muted">
                  <i className="fas fa-lock me-1"></i>
                  Thanh to√°n an to√†n & b·∫£o m·∫≠t
                </small>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
