// src/services/productService.js
import api from "./api";

const productService = {
  /**
   * ✅ Lấy tất cả sản phẩm (có hỗ trợ phân trang)
   * @param {number} page - Trang hiện tại (zero-based)
   * @param {number} limit - Số sản phẩm mỗi trang
   */
  async getAllProducts(page = 0, limit = 8) {
    try {
      const { data } = await api.get(`/public/products?pageNumber=${page}&pageSize=${limit}`);
      return data;
    } catch (error) {
      console.error("❌ Lỗi khi lấy danh sách sản phẩm:", error);
      throw error;
    }
  },

  /**
   * ✅ Lấy sản phẩm theo danh mục (có phân trang)
   */
  async getProductsByCategory(categoryId, page = 0, limit = 8) {
    try {
      const { data } = await api.get(
        `/public/categories/${categoryId}/products?pageNumber=${page}&pageSize=${limit}`
      );
      return data;
    } catch (error) {
      console.error("❌ Lỗi khi lấy sản phẩm theo danh mục:", error);
      throw error;
    }
  },

  /**
   * ✅ Tìm kiếm sản phẩm theo từ khóa (có phân trang)
   */
  async searchProducts(keyword, page = 0, limit = 8) {
    try {
      const { data } = await api.get(
        `/public/products?search=${encodeURIComponent(keyword)}&pageNumber=${page}&pageSize=${limit}`
      );
      return data;
    } catch (error) {
      console.error("❌ Lỗi khi tìm kiếm sản phẩm:", error);
      throw error;
    }
  },

  /**
   * ✅ Lấy sản phẩm theo danh mục và từ khóa (có phân trang)
   */
  async getProductsByCategoryAndSearch(categoryId, keyword, page = 0, limit = 8) {
    try {
      const { data } = await api.get(
        `/public/categories/${categoryId}/products?search=${encodeURIComponent(keyword)}&pageNumber=${page}&pageSize=${limit}`
      );
      return data;
    } catch (error) {
      console.error("❌ Lỗi khi lấy sản phẩm theo danh mục và từ khóa:", error);
      throw error;
    }
  },

  /**
   * ✅ Lấy chi tiết sản phẩm theo ID
   */
  async getProductById(productId) {
    try {
      const { data } = await api.get(`/public/products/${productId}`);
      return data;
    } catch (error) {
      console.error("❌ Lỗi khi lấy sản phẩm:", error);
      throw error;
    }
  },


};

export default productService;
