import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import Swal from 'sweetalert2';

const GeminiChat = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [messages, setMessages] = useState([
        { role: 'ai', text: 'Chào nàng! Em là Ngọc Anh Beauty AI, em có thể giúp gì cho nàng hôm nay? ❤️' }
    ]);
    const [input, setInput] = useState('');
    const [loading, setLoading] = useState(false);
    
    const scrollRef = useRef(null);
    const currentUser = JSON.parse(localStorage.getItem('user'));
    const cartId = localStorage.getItem('cartId'); // Đảm bảo mày đã lưu cartId khi đăng nhập

    // Tự động cuộn xuống khi có tin nhắn mới
    useEffect(() => {
        scrollRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, [messages]);

    const handleSend = async () => {
        if (!input.trim() || loading) return;

        const userMsg = input.trim();
        setMessages(prev => [...prev, { role: 'user', text: userMsg }]);
        setInput('');
        setLoading(true);

        try {
            const res = await axios.post('http://localhost:8080/api/ai/ask', {
                message: userMsg,
                cartId: cartId
            });

            const data = res.data;
            
            // Thêm phản hồi từ AI vào danh sách tin nhắn
            setMessages(prev => [...prev, { 
                role: 'ai', 
                text: data.reply,
                suggestions: data.suggested_products // Danh sách sản phẩm gợi ý
            }]);

            // Nếu AI báo đã thêm vào giỏ hàng thành công
            if (data.added_product_id) {
                // Có thể phát một tiếng "Ting" hoặc bắn thông báo nhỏ
                console.log("Đã thêm SP ID: " + data.added_product_id);
            }

        } catch (error) {
            setMessages(prev => [...prev, { role: 'ai', text: 'Hic, em đang bận tí, nàng đợi em xíu nhé!' }]);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="gemini-chat-container" style={{ position: 'fixed', bottom: '20px', right: '20px', zIndex: 1000 }}>
            {/* Nút bấm mở Chat */}
            <button 
                onClick={() => setIsOpen(!isOpen)}
                className="btn btn-warning rounded-circle shadow-lg p-3"
                style={{ width: '60px', height: '60px' }}
            >
                <i className={`bi ${isOpen ? 'bi-x-lg' : 'bi-chat-dots-fill'} fs-4`}></i>
            </button>

            {/* Khung Chat */}
            {isOpen && (
                <div className="card shadow-2xl border-0 rounded-4 mt-2" style={{ width: '350px', height: '500px', display: 'flex', flexDirection: 'column' }}>
                    <div className="card-header bg-warning text-white p-3 rounded-top-4">
                        <h6 className="mb-0 fw-bold">✨ Ngọc Anh Beauty AI</h6>
                        <small>Đang trực tuyến</small>
                    </div>

                    <div className="card-body p-3 overflow-auto" style={{ flex: 1, backgroundColor: '#f8f9fa' }}>
                        {messages.map((msg, idx) => (
                            <div key={idx} className={`d-flex mb-3 ${msg.role === 'user' ? 'justify-content-end' : 'justify-content-start'}`}>
                                <div className={`p-2 rounded-3 shadow-sm ${msg.role === 'user' ? 'bg-warning text-white' : 'bg-white text-dark'}`} style={{ maxWidth: '85%', fontSize: '14px' }}>
                                    {msg.text}
                                    
                                    {/* Hiển thị Card sản phẩm gợi ý nếu có */}
                                    {msg.suggestions && msg.suggestions.length > 0 && (
                                        <div className="mt-2 row g-2">
                                            {msg.suggestions.map((p, i) => (
                                                <div key={i} className="col-12">
                                                    <div className="card border-0 shadow-sm p-2 d-flex flex-row align-items-center gap-2">
                                                        <img src={`http://localhost:8080/api/public/products/image/${p.image}`} width="40" height="40" className="rounded" alt="" />
                                                        <div className="flex-grow-1 overflow-hidden">
                                                            <div className="small fw-bold text-truncate">{p.productName}</div>
                                                            <div className="small text-danger">{p.specialPrice?.toLocaleString() || p.price?.toLocaleString()}₫</div>
                                                        </div>
                                                        <button className="btn btn-sm btn-outline-warning p-0 px-1"><i className="bi bi-eye"></i></button>
                                                    </div>
                                                </div>
                                            ))}
                                        </div>
                                    )}
                                </div>
                            </div>
                        ))}
                        {loading && <div className="text-muted small">Ngọc Anh đang gõ...</div>}
                        <div ref={scrollRef} />
                    </div>

                    <div className="card-footer bg-white p-2">
                        <div className="input-group">
                            <input 
                                type="text" 
                                className="form-control border-0 shadow-none" 
                                placeholder="Hỏi em về làm đẹp..."
                                value={input}
                                onChange={(e) => setInput(e.target.value)}
                                onKeyPress={(e) => e.key === 'Enter' && handleSend()}
                            />
                            <button onClick={handleSend} className="btn text-warning"><i className="bi bi-send-fill"></i></button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default GeminiChat;
