import { useEffect, useState, useCallback, useRef } from "react";
import { Link, useLocation } from "react-router-dom";
import productService from "../../services/productService";
import categoryService from "../../services/categoryService";

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [pageNumber, setPageNumber] = useState(0);
  const [pageSize] = useState(8);
  const [totalPages, setTotalPages] = useState(0);
  const [loading, setLoading] = useState(true);
  const location = useLocation();

  // State cho search text (hi·ªÉn th·ªã t·ª©c th√¨ tr√™n √¥ input)
  const [searchTerm, setSearchTerm] = useState("");

  const [filters, setFilters] = useState({
    categoryId: location.state?.selectedCategory || "",
    searchKeyword: "" // Keyword th·ª±c t·∫ø d√πng ƒë·ªÉ g·ªçi API
  });

  // 1. Debounce x·ª≠ l√Ω Search Keyword (Fix gi·∫≠t lag khi g√µ)
  useEffect(() => {
    const delayDebounceFn = setTimeout(() => {
      setFilters(prev => ({ ...prev, searchKeyword: searchTerm }));
      setPageNumber(0);
    }, 500); // ƒê·ª£i ng∆∞·ªùi d√πng d·ª´ng g√µ 500ms m·ªõi g·ªçi API

    return () => clearTimeout(delayDebounceFn);
  }, [searchTerm]);

  // 2. T·∫£i danh m·ª•c
  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await categoryService.getAllCategories();
        setCategories(response || []);
      } catch (err) { console.error("‚ùå Error:", err); }
    };
    fetchCategories();
  }, []);

  // 3. H√†m t·∫£i s·∫£n ph·∫©m m∆∞·ª£t m√†
  const fetchProducts = useCallback(async () => {
    setLoading(true);
    // Cu·ªôn l√™n ƒë·∫ßu trang m∆∞·ª£t m√† khi ƒë·ªïi trang/l·ªçc
    window.scrollTo({ top: 0, behavior: 'smooth' });

    try {
      let response;
      const { categoryId, searchKeyword } = filters;

      if (categoryId && searchKeyword) {
        response = await productService.getProductsByCategoryAndSearch(categoryId, searchKeyword, pageNumber, pageSize);
      } else if (categoryId) {
        response = await productService.getProductsByCategory(categoryId, pageNumber, pageSize);
      } else if (searchKeyword) {
        response = await productService.searchProducts(searchKeyword, pageNumber, pageSize);
      } else {
        response = await productService.getAllProducts(pageNumber, pageSize);
      }

      setProducts(response?.content || []);
      setTotalPages(response?.totalPages || 0);
    } catch (err) {
      setProducts([]);
    } finally {
      // Th√™m ƒë·ªô tr·ªÖ nh·ªè ƒë·ªÉ UI kh√¥ng b·ªã nh√°y qu√° nhanh
      setTimeout(() => setLoading(false), 300);
    }
  }, [pageNumber, pageSize, filters]);

  useEffect(() => {
    fetchProducts();
  }, [fetchProducts]);

  // X·ª≠ l√Ω Category t·ª´ trang kh√°c chuy·ªÉn sang
  useEffect(() => {
    if (location.state?.selectedCategory) {
      setFilters(prev => ({ ...prev, categoryId: location.state.selectedCategory }));
      setPageNumber(0);
      window.history.replaceState({}, document.title);
    }
  }, [location.state]);

  const handleClearFilters = () => {
    setSearchTerm("");
    setFilters({ categoryId: "", searchKeyword: "" });
    setPageNumber(0);
  };

  return (
    <section className="container mt-5 pb-5">
      <header className="mb-4 text-center animate__animated animate__fadeIn">
        <h3 className="fw-bold text-primary text-uppercase">
          {filters.categoryId ? `üìÇ ${categories.find(c => (c.categoryId || c.id) == filters.categoryId)?.categoryName || 'Danh m·ª•c'}` : "üõçÔ∏è T·∫•t c·∫£ s·∫£n ph·∫©m"}
        </h3>
        <p className="text-muted small">Kh√°m ph√° th·∫ø gi·ªõi m·ªπ ph·∫©m ch√≠nh h√£ng</p>
      </header>

      {/* B·ªò L·ªåC T·ªêI ∆ØU */}
      <div className="card shadow-sm border-0 mb-5 p-3 bg-white rounded-4">
        <div className="row g-3">
          <div className="col-md-4">
            <select 
              value={filters.categoryId} 
              onChange={(e) => { setFilters(prev => ({ ...prev, categoryId: e.target.value })); setPageNumber(0); }} 
              className="form-select border-0 bg-light rounded-pill px-3 shadow-none"
            >
              <option value="">T·∫•t c·∫£ danh m·ª•c</option>
              {categories.map((cat) => (
                <option key={cat.categoryId || cat.id} value={cat.categoryId || cat.id}>
                  {cat.categoryName || cat.name}
                </option>
              ))}
            </select>
          </div>
          <div className="col-md-5">
            <div className="input-group bg-light rounded-pill px-3 border-0">
              <span className="input-group-text bg-transparent border-0"><i className="fa fa-search text-muted"></i></span>
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="form-control bg-transparent border-0 shadow-none"
                placeholder="T√¨m serum, son m√¥i, kem ch·ªëng n·∫Øng..."
              />
            </div>
          </div>
          <div className="col-md-3">
            <button onClick={handleClearFilters} className="btn btn-outline-secondary w-100 rounded-pill border-0 bg-light shadow-none">
               L√†m m·ªõi
            </button>
          </div>
        </div>
      </div>

      {/* DANH S√ÅCH S·∫¢N PH·∫®M M∆Ø·ª¢T M√Ä */}
      <div className={`row transition-all ${loading ? 'opacity-50' : 'opacity-100'}`} style={{ transition: '0.4s' }}>
        {products.length > 0 ? (
          products.map((p) => (
            <div key={p.productId || p.id} className="col-xl-3 col-lg-4 col-md-6 mb-4">
              <div className="card h-100 border-0 shadow-sm card-hover rounded-4 overflow-hidden">
                <div className="position-relative text-center p-3">
                  {p.discount > 0 && <span className="badge bg-danger position-absolute top-0 start-0 m-3 shadow">-{p.discount}%</span>}
                  <Link to={`/product/${p.productId || p.id}`}>
                    <img 
                      src={`${IMAGE_BASE_URL}/${p.image}`} 
                      className="img-fluid" 
                      style={{ height: '200px', objectFit: 'contain' }}
                      alt={p.productName}
                    />
                  </Link>
                </div>
                <div className="card-body pt-0">
                  <h6 className="fw-bold text-dark text-truncate mb-2">{p.productName}</h6>
                  <div className="d-flex align-items-center mb-3">
                    <span className="text-danger fw-bold h5 mb-0">{(p.specialPrice || p.price).toLocaleString()} ‚Ç´</span>
