             <img src="https://cdn-icons-png.flaticon.com/512/7486/7486744.png" width="100" className="mb-3" alt="Empty" />
             <p className="text-muted">R·∫•t ti·∫øc, ch√∫ng t√¥i kh√¥ng t√¨m th·∫•y s·∫£n ph·∫©m n√†y!</p>
          </div>
        )}
      </div>

      {/* PH√ÇN TRANG */}
      {totalPages > 1 && (
        <nav className="mt-5">
          <ul className="pagination justify-content-center border-0">
             <li className={`page-item ${pageNumber === 0 ? 'disabled' : ''}`}>
               <button className="page-link border-0 rounded-circle mx-1 shadow-sm" onClick={() => setPageNumber(pageNumber - 1)}>
                  <i className="fa fa-chevron-left"></i>
               </button>
             </li>
             {[...Array(totalPages)].map((_, i) => (
               <li key={i} className={`page-item ${i === pageNumber ? 'active' : ''}`}>
                 <button className="page-link border-0 rounded-circle mx-1 shadow-sm" onClick={() => setPageNumber(i)}>{i + 1}</button>
