import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import productService from "../../services/productService";

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadAllProducts = async () => {
      try {
        setLoading(true);
        setError(null);

        console.log("üîÑ ƒêang t·∫£i t·∫•t c·∫£ s·∫£n ph·∫©m...");

        // Th·ª≠ c√°c c√°ch kh√°c nhau ƒë·ªÉ l·∫•y t·∫•t c·∫£ s·∫£n ph·∫©m
        let allProducts = [];

        try {
          // C√°ch 1: Th·ª≠ l·∫•y v·ªõi size l·ªõn
          const response1 = await productService.getAllProductsNoPagination();
          console.log("üì¶ Response t·ª´ getAllProductsNoPagination:", response1);
          allProducts = response1.content || response1 || [];
        } catch (err1) {
          console.log("‚ùå C√°ch 1 th·∫•t b·∫°i, th·ª≠ c√°ch 2...");

          try {
            // C√°ch 2: Th·ª≠ endpoint /products/all
            const response2 = await productService.getAllProductsAll();
            console.log("üì¶ Response t·ª´ getAllProductsAll:", response2);
            allProducts = response2.content || response2 || [];
          } catch (err2) {
            console.log("‚ùå C√°ch 2 th·∫•t b·∫°i, th·ª≠ c√°ch 3...");

            try {
              // C√°ch 3: G·ªçi nhi·ªÅu trang ƒë·ªÉ l·∫•y t·∫•t c·∫£
              allProducts = await getAllProductsByPages();
            } catch (err3) {
              console.log("‚ùå C√°ch 3 th·∫•t b·∫°i");
              throw new Error("Kh√¥ng th·ªÉ l·∫•y danh s√°ch s·∫£n ph·∫©m");
            }
          }
        }

        console.log("üõçÔ∏è T·∫•t c·∫£ s·∫£n ph·∫©m:", allProducts);
        console.log("üìä T·ªïng s·ªë s·∫£n ph·∫©m:", allProducts.length);

        setProducts(allProducts);

      } catch (err) {
        console.error("‚ùå L·ªói khi t·∫£i s·∫£n ph·∫©m:", err);
        setError("Kh√¥ng th·ªÉ t·∫£i danh s√°ch s·∫£n ph·∫©m: " + err.message);
      } finally {
        setLoading(false);
      }
    };

    loadAllProducts();
  }, []);

  // H√†m l·∫•y t·∫•t c·∫£ s·∫£n ph·∫©m b·∫±ng c√°ch g·ªçi nhi·ªÅu trang
  const getAllProductsByPages = async () => {
    let allProducts = [];
    let currentPage = 0;
    let hasMore = true;

    while (hasMore) {
      try {
        console.log(`üìÑ ƒêang t·∫£i trang ${currentPage}...`);
        const response = await productService.getAllProducts(currentPage, 50); // Size l·ªõn
        const pageData = response.content || [];

        if (pageData.length > 0) {
          allProducts = [...allProducts, ...pageData];
          currentPage++;

          // Ki·ªÉm tra c√≤n trang ti·∫øp theo kh√¥ng
          if (response.totalPages && currentPage >= response.totalPages) {
            hasMore = false;
          }
          if (pageData.length < 50) { // N·∫øu trang cu·ªëi c√≥ √≠t s·∫£n ph·∫©m
            hasMore = false;
          }
        } else {
          hasMore = false;
        }
      } catch (error) {
        console.error(`‚ùå L·ªói khi t·∫£i trang ${currentPage}:`, error);
        hasMore = false;
      }
    }

    return allProducts;
  };

  // Hi·ªÉn th·ªã loading
  if (loading) {
    return (
      <section className="padding-bottom">
        <header className="section-heading mb-4 text-center">
          <h3 className="title-section text-uppercase font-bold text-primary">
            üõçÔ∏è T·∫•t c·∫£ s·∫£n ph·∫©m
          </h3>
        </header>
        <div className="text-center py-5">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-2">ƒêang t·∫£i s·∫£n ph·∫©m...</p>
        </div>
      </section>
    );
  }

  // Hi·ªÉn th·ªã l·ªói
  if (error) {
    return (
      <section className="padding-bottom">
        <header className="section-heading mb-4 text-center">
          <h3 className="title-section text-uppercase font-bold text-primary">
            üõçÔ∏è T·∫•t c·∫£ s·∫£n ph·∫©m
          </h3>
        </header>
        <div className="alert alert-danger text-center">
          {error}
        </div>
      </section>
    );
  }

  return (
    <section className="padding-bottom">
      <header className="section-heading mb-4 text-center">
        <h3 className="title-section text-uppercase font-bold text-primary">
          üõçÔ∏è T·∫•t c·∫£ s·∫£n ph·∫©m ({products.length} s·∫£n ph·∫©m)
        </h3>
        {products.length > 0 && (
          <p className="text-success small">
            ‚úÖ ƒê√£ t·∫£i t·∫•t c·∫£ {products.length} s·∫£n ph·∫©m t·ª´ database
          </p>
        )}
      </header>

      <div className="row">
        {products.map((p, index) => {
          const productId = p.productId || p.product_id || p.id;
          const productName = p.productName || p.product_name || p.name;
          const price = p.price;
          const specialPrice = p.special_price || p.specialPrice;
          const discount = p.discount;
          const quantity = p.quantity;
          const image = p.image;
          const description = p.description;

          console.log(`üõí Product ${index + 1}:`, { 
            id: productId, 
            name: productName,
            price: price,
            specialPrice: specialPrice,
            quantity: quantity
          });

          return (
            <div key={productId || index} className="col-xl-3 col-lg-3 col-md-4 col-6 mb-4">
              <div className="card card-product-grid shadow-sm h-100">
                <div className="img-wrap">
                  <Link to={`/product/${productId}`} className="text-decoration-none">
                    <img
                      src={`${IMAGE_BASE_URL}/${image}`}
                      alt={productName}
                      onError={(e) => {
                        e.target.src = "https://cdn-icons-png.flaticon.com/512/679/679922.png";
                      }}
                      style={{
                        width: "100%",
                        height: "250px",
                        objectFit: "cover",
                        borderRadius: "10px",
                      }}
                    />
                  </Link>
                </div>

                <div className="info-wrap p-3 d-flex flex-column">
                  <Link to={`/product/${productId}`} className="text-decoration-none">
                    <h6 className="text-truncate text-dark font-weight-bold">
                      {productName || "T√™n s·∫£n ph·∫©m"}
                    </h6>
                  </Link>

                  <p className="text-muted small mb-2 flex-grow-1">
                    {(description || "ƒêang c·∫≠p nh·∫≠t m√¥ t·∫£...").substring(0, 40)}...
                  </p>

                  <div className="price-wrapper">
                    {specialPrice ? (
                      <>
                        <span className="price h5 text-danger mb-0">
                          {specialPrice?.toLocaleString()} ‚Ç´
                        </span>
                        <span className="original-price text-muted text-decoration-line-through small ms-2">
                          {price?.toLocaleString()} ‚Ç´
                        </span>
                      </>
                    ) : (
                      <span className="price h5 text-danger mb-0">
                        {price?.toLocaleString()} ‚Ç´
                      </span>
                    )}
                  </div>

                  {discount > 0 && (
                    <small className="text-success mt-1">
                      üî• Gi·∫£m {discount}%
                    </small>
                  )}

                  <small className={`mt-1 ${quantity === 0 ? 'text-danger' : 'text-success'}`}>
                    {quantity === 0 ? '‚ùå H·∫øt h√†ng' : `üì¶ C√≤n ${quantity} s·∫£n ph·∫©m`}
                  </small>
                </div>
              </div>
            </div>
          );
        })}
