import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import userService from "../../services/userService";

const EditProfile = () => {
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({
    first_name: "",
    last_name: "",
    email: "",
    mobile_number: ""
  });
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);
  const [message, setMessage] = useState({ type: "", text: "" });
  const navigate = useNavigate();

  useEffect(() => {
    // Debug chi ti·∫øt
    const storedUser = localStorage.getItem("user");
    const storedToken = localStorage.getItem("token");

    console.log("üõ†Ô∏è DEBUG EDIT PROFILE:", {
      hasUser: !!storedUser,
      hasToken: !!storedToken,
      user: storedUser ? JSON.parse(storedUser) : null,
      token: storedToken ? `Length: ${storedToken.length}` : "No token"
    });

    if (!storedUser || !storedToken) {
      console.log("‚ùå No user or token - redirecting to login");
      setMessage({ 
        type: "error", 
        text: "Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ ch·ªânh s·ª≠a h·ªì s∆°" 
      });
      setTimeout(() => navigate("/login"), 2000);
      return;
    }

    const initializeUser = async () => {
      try {
        const parsedUser = JSON.parse(storedUser);
        setUser(parsedUser);

        const userId = parsedUser.userId || parsedUser.user_id;
        if (!userId) {
          setMessage({ type: "error", text: "Kh√¥ng t√¨m th·∫•y ID ng∆∞·ªùi d√πng" });
          return;
        }

        console.log("üÜî Loading profile for user:", userId);
        setLoading(true);

        // TH·ª¨ NGHI·ªÜM: G·ªçi API kh√¥ng c·∫ßn token tr∆∞·ªõc
        try {
          const userData = await userService.getUserProfile(userId);
          console.log("‚úÖ User profile loaded:", userData);

          setFormData({
            first_name: userData.first_name || "",
            last_name: userData.last_name || "",
            email: userData.email || "",
            mobile_number: userData.mobile_number || ""
          });
        } catch (apiError) {
          console.error("‚ùå API Error:", apiError);

          // N·∫øu API l·ªói, d√πng d·ªØ li·ªáu t·ª´ localStorage
          setFormData({
            first_name: parsedUser.first_name || "",
            last_name: parsedUser.last_name || "",
            email: parsedUser.email || "",
            mobile_number: parsedUser.mobile_number || ""
          });

          setMessage({ 
            type: "warning", 
            text: "ƒêang s·ª≠ d·ª•ng th√¥ng tin t·ª´ b·ªô nh·ªõ t·∫°m" 
          });
        }

      } catch (error) {
        console.error("‚ùå Error initializing user:", error);
        setMessage({ 
          type: "error", 
          text: "L·ªói kh·ªüi t·∫°o. Vui l√≤ng th·ª≠ l·∫°i." 
        });
      } finally {
        setLoading(false);
      }
    };

    initializeUser();
  }, [navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Ki·ªÉm tra l·∫°i authentication tr∆∞·ªõc khi submit
