import React, { useEffect, useState, useCallback } from "react";
import { Link, useNavigate } from "react-router-dom";
import userService from "../services/userService";
import categoryService from "../services/categoryService";
import cartService from "../services/cartService";
import Swal from "sweetalert2";

import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";
import "../assets/fonts/fontawesome/css/all.min.css";
import "../assets/css/style.css"; 

const Header = () => {
  const [user, setUser] = useState(null);
  const [userInfo, setUserInfo] = useState(null);
  const [loadingCategories, setLoadingCategories] = useState(false);
  const [categories, setCategories] = useState([]);
  const [cartCount, setCartCount] = useState(0);
  const [searchTerm, setSearchTerm] = useState("");
  const navigate = useNavigate();

  // --- Logic giữ nguyên 100% ---
  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      navigate(`/product`, { state: { searchKeyword: searchTerm.trim() } });
    }
  };

  const initUser = useCallback(async () => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      const parsedUser = JSON.parse(storedUser);
      setUser(parsedUser);
      if (parsedUser.userId) {
        try {
          const data = await userService.getUserById(parsedUser.userId);
          setUserInfo(data);
        } catch (err) {
          console.error("Lỗi lấy thông tin user:", err);
        }
      }
    } else {
      setUser(null);
      setUserInfo(null);
    }
  }, []);

  const loadCartCount = async () => {
    try {
      const currentUser = JSON.parse(localStorage.getItem('user') || '{}');
      if (currentUser?.email) {
        const count = await cartService.getCartCount(currentUser.email);
        setCartCount(count);
      } else {
        const localCart = cartService.getCartFromStorage();
        setCartCount(cartService.getTotalItems(localCart?.products || []));
      }
    } catch (error) {
      setCartCount(0);
    }
  };

  const fetchCategories = async () => {
    setLoadingCategories(true);
    try {
      const data = await categoryService.getAllCategories();
      setCategories(data || []);
    } catch (err) {
      console.error("Lỗi danh mục:", err);
    } finally {
      setLoadingCategories(false);
    }
  };

  useEffect(() => {
    initUser();
    fetchCategories();
    window.addEventListener("authUpdated", initUser);
    return () => window.removeEventListener("authUpdated", initUser);
  }, [initUser]);

  useEffect(() => {
    loadCartCount();
    window.addEventListener('cartUpdated', loadCartCount);
    return () => window.removeEventListener('cartUpdated', loadCartCount);
  }, [user]);

  const handleCategoryClick = (category) => {
    navigate(`/product`, { 
      state: { 
        selectedCategory: category.categoryId || category.id, 
        categoryName: category.categoryName || category.name 
      } 
    });
  };

  const handleLogout = () => {
    Swal.fire({
      title: 'Bạn muốn đăng xuất?',
      icon: 'question',
      showCancelButton: true,
      confirmButtonColor: '#ff6a00',
      confirmButtonText: 'Đăng xuất',
      cancelButtonText: 'Hủy'
    }).then((result) => {
      if (result.isConfirmed) {
        localStorage.clear();
        setUser(null);
        setUserInfo(null);
        setCartCount(0);
        window.dispatchEvent(new Event("authUpdated"));
        navigate("/login");
      }
    });
  };

  const renderUserName = () => {
    if (!user) return "Tài khoản";
    return userInfo?.firstName || user.firstName || user.email?.split('@')[0] || "User";
  };

  return (
    <header className="section-header sticky-top bg-white shadow-sm">
      {/* Top Banner - Tinh chỉnh font và padding */}
      <div className="top-banner-ads py-1 small text-center fw-medium text-white" style={{ background: 'linear-gradient(90deg, #ff6a00, #ff9100)' }}>
        <i className="fa fa-bolt me-2"></i> Nội thành Hà Nội - TP. HCM | GIAO SIÊU TỐC 2H - FREESHIP 0Đ
      </div>

      <section className="header-main py-3 border-bottom">
        <div className="container">
          <div className="row align-items-center">
            {/* Logo - Font hiện đại hơn */}
            <div className="col-lg-2 col-md-3 col-6">
              <Link to="/" className="text-decoration-none">
                <h2 className="mb-0 fw-bold" style={{ letterSpacing: '-1.5px', color: '#333' }}>
                  Shop<span style={{ color: '#ff6a00' }}>BeAnh</span>
                </h2>
              </Link>
            </div>

            {/* Thanh tìm kiếm - Bo tròn cực đại và thêm shadow nhẹ */}
            <div className="col-lg-6 col-md-5 d-none d-md-block">
              <form className="d-flex align-items-center px-3 py-1 bg-light rounded-pill border shadow-sm" onSubmit={handleSearchSubmit}>
                <input 
                  type="text" 
                  placeholder="Tìm kiếm sản phẩm..." 
                  className="form-control border-0 bg-transparent shadow-none small" 
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
                <button type="submit" className="btn p-0 border-0 bg-transparent text-orange">
                  <i className="fa fa-search"></i>
                </button>
              </form>
            </div>

            {/* User & Cart - Thay đổi icon style */}
            <div className="col-lg-4 col-md-4 col-6">
              <div className="d-flex justify-content-end align-items-center gap-4">

                {/* Dropdown User */}
                <div className="user-dropdown-container position-relative">
                  <div className="d-flex align-items-center gap-2" style={{ cursor: 'pointer' }}>
                    <div className="bg-light rounded-circle d-flex align-items-center justify-content-center shadow-sm" style={{ width: '40px', height: '40px' }}>
                      <i className="fa fa-user text-dark"></i>
                    </div>
                    <div className="d-none d-lg-block">
                        <small className="text-muted d-block" style={{ fontSize: '10px' }}>Xin chào,</small>
                        <span className="fw-bold small">{renderUserName()}</span>
                    </div>
                  </div>

                  <ul className="user-mega-menu shadow-lg border-0 rounded-3 mt-2 overflow-hidden">
                    {!user ? (
                      <>
                        <li><Link to="/login" className="user-menu-item"><i className="fa fa-sign-in-alt me-2"></i> Đăng nhập</Link></li>
                        <li><Link to="/register" className="user-menu-item border-top"><i className="fa fa-user-plus me-2"></i> Đăng ký</Link></li>
                      </>
                    ) : (
                      <>
                        <li><Link to="/profile" className="user-menu-item"><i className="fa fa-id-card me-2"></i> Trang cá nhân</Link></li>
                        <li><Link to="/orders" className="user-menu-item"><i className="fa fa-list-alt me-2"></i> Đơn hàng</Link></li>
                        <li>
                          <button onClick={handleLogout} className="user-menu-item border-top w-100 text-start border-0 bg-transparent text-danger fw-bold">
                            <i className="fa fa-power-off me-2"></i> Đăng xuất
                          </button>
                        </li>
                      </>
                    )}
                  </ul>
                </div>

                {/* Giỏ hàng */}
                <Link to="/cart" className="text-dark text-decoration-none position-relative">
                   <div className="d-flex align-items-center gap-2">
                      <div className="bg-orange-light rounded-circle d-flex align-items-center justify-content-center shadow-sm position-relative" style={{ width: '40px', height: '40px' }}>
                        <i className="fa fa-shopping-cart text-orange"></i>
                        <span className="badge rounded-pill bg-danger position-absolute top-0 start-100 translate-middle border border-light" 
                            style={{ fontSize: '9px' }}>
                        {cartCount}
                        </span>
                      </div>
                      <div className="d-none d-lg-block">
                        <small className="text-muted d-block" style={{ fontSize: '10px' }}>Giỏ hàng</small>
                        <span className="fw-bold small">Sản phẩm</span>
                      </div>
                   </div>
                </Link>

              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Navigation Bar - Sắp xếp lại khoảng cách */}
      <nav className="nav-categories bg-white d-none d-lg-block shadow-sm">
        <div className="container">
          <ul className="nav d-flex align-items-center">
            {/* Mega Menu Danh mục */}
            <li className="nav-item dropdown-hover-container">
              <a className="nav-link fw-bold text-dark py-3 d-flex align-items-center" href="#" onClick={(e) => e.preventDefault()}>
                <i className="fa fa-bars me-2 text-orange"></i> DANH MỤC
              </a>
              <ul className="mega-dropdown-menu shadow-lg scrollable-menu border-0 rounded-bottom-3">
                {loadingCategories ? (
                  <li className="text-center p-3 small text-muted">Đang tải...</li>
                ) : (
                  categories.map((cat, idx) => (
                    <li key={idx}>
                      <button 
                        className="mega-dropdown-item border-0 bg-transparent w-100 text-start py-2 px-4" 
                        onClick={() => handleCategoryClick(cat)}
                      >
                        {cat.categoryName || cat.name}
                      </button>
                    </li>
                  ))
                )}
              </ul>
            </li>

            {/* Quick Links */}
            {!loadingCategories && categories.slice(0, 5).map((cat, idx) => (
              <li className="nav-item" key={`nav-quick-${idx}`}>
                <button 
                  className="nav-link text-muted border-0 bg-transparent py-3 px-3 fw-medium small hover-orange" 
                  onClick={() => handleCategoryClick(cat)}
                >
                  {cat.categoryName || cat.name}
                </button>
              </li>
            ))}

            <li className="nav-item ms-auto">
                <Link to="/product" className="nav-link text-orange py-3 fw-bold small">
                    TẤT CẢ SẢN PHẨM <i className="fa fa-chevron-right ms-1" style={{ fontSize: '10px' }}></i>
                </Link>
            </li>
          </ul>
        </div>
      </nav>

      <style>{`
        .text-orange { color: #ff6a00; }
        .bg-orange-light { background-color: #fff5ee; }
        .hover-orange:hover { color: #ff6a00 !important; }

        /* Dropdown style */
        .user-mega-menu {
          display: none;
          position: absolute;
          background: #fff;
          min-width: 200px;
          z-index: 1000;
          top: 100%;
          right: 0;
        }
        .user-dropdown-container:hover .user-mega-menu { display: block; animation: slideDown 0.2s ease; }

        .user-menu-item {
          padding: 12px 20px;
          display: block;
          color: #333;
          text-decoration: none;
          transition: 0.2s;
          font-size: 14px;
        }
        .user-menu-item:hover { background: #f8f9fa; color: #ff6a00; }

        .scrollable-menu {
          max-height: 420px;
          overflow-y: auto;
          min-width: 250px;
          position: absolute;
          background: #fff;
          display: none;
        }
        .dropdown-hover-container:hover .mega-dropdown-menu { display: block; animation: slideDown 0.2s ease; }

        .mega-dropdown-item {
          transition: all 0.2s;
          font-size: 14px;
          color: #555;
        }
        .mega-dropdown-item:hover {
          background-color: #fff5ee;
          color: #ff6a00;
          padding-left: 30px !important;
        }

        @keyframes slideDown {
          from { opacity: 0; transform: translateY(10px); }
          to { opacity: 1; transform: translateY(0); }
        }

        .scrollable-menu::-webkit-scrollbar { width: 4px; }
        .scrollable-menu::-webkit-scrollbar-thumb { background: #ff6a00; border-radius: 10px; }
      `}</style>
    </header>
  );
};

export default Header;
