import React, { useState, useEffect, useCallback } from "react";
import categoryService from "../../../services/categoryService";
import Swal from "sweetalert2";

const CategoryListAD = () => {
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [isEdit, setIsEdit] = useState(false);
  const [formData, setFormData] = useState({ categoryId: "", categoryName: "" });

  // State quản lý phân trang khớp với CategoryResponse
  const [pageConfig, setPageConfig] = useState({
    pageNumber: 0,
    pageSize: 10,
    totalPages: 0,
    totalElements: 0,
  });

  // ================= FETCH DATA =================
  const fetchCategories = useCallback(async (page = 0) => {
    try {
      setLoading(true);
      const data = await categoryService.getAllCategories(page, pageConfig.pageSize);
      
      // CategoryResponse trả về content là mảng danh mục
      setCategories(data.content || []);
      
      setPageConfig({
        pageNumber: data.pageNumber,
        pageSize: data.pageSize,
        totalPages: data.totalPages,
        totalElements: data.totalElements,
      });
    } catch (err) {
      console.error("Lỗi fetch:", err);
      Swal.fire("Lỗi", "Không thể tải danh mục", "error");
    } finally {
      setLoading(false);
    }
  }, [pageConfig.pageSize]);

  useEffect(() => {
    fetchCategories(0);
  }, [fetchCategories]);

  // ================= LOGIC MODAL =================
  const handleOpenModal = (cat = null) => {
    if (cat) {
      setIsEdit(true);
      setFormData({ categoryId: cat.categoryId, categoryName: cat.categoryName });
    } else {
      setIsEdit(false);
      setFormData({ categoryId: "", categoryName: "" });
    }
    setShowModal(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.categoryName.trim().length < 5) {
      return Swal.fire("Lỗi", "Tên phải từ 5 ký tự (Yêu cầu BE)", "warning");
    }

    try {
      const payload = { categoryName: formData.categoryName };
      if (isEdit) {
        await categoryService.updateCategory(formData.categoryId, payload);
      } else {
        await categoryService.createCategory(payload);
      }
      
      Swal.fire("Thành công!", "Dữ liệu đã được lưu.", "success");
      setShowModal(false);
      fetchCategories(pageConfig.pageNumber);
    } catch (err) {
      Swal.fire("Lỗi", err.response?.data?.message || "Thao tác thất bại", "error");
    }
  };

  const handleDelete = async (id) => {
    const confirm = await Swal.fire({
      title: "Xóa danh mục?",
      text: "Hành động này không thể hoàn tác!",
      icon: "warning",
      showCancelButton: true,
      confirmButtonText: "Xóa ngay"
    });

    if (confirm.isConfirmed) {
      try {
        await categoryService.deleteCategory(id);
        Swal.fire("Đã xóa!", "", "success");
        fetchCategories(0);
      } catch (err) {
        Swal.fire("Lỗi", "Không thể xóa danh mục này", "error");
      }
    }
  };

  return (
    <div className="p-4">
      <div className="card shadow-sm border-0">
        <div className="card-header bg-white d-flex justify-content-between align-items-center py-3">
          <h5 className="m-0 fw-bold"><i className="bi bi-tags me-2"></i>DANH MỤC</h5>
          <button className="btn btn-primary btn-sm px-3" onClick={() => handleOpenModal()}>+ Thêm mới</button>
        </div>

        <div className="table-responsive">
          <table className="table table-hover align-middle mb-0">
            <thead className="table-light">
              <tr>
                <th className="ps-4">ID</th>
                <th>Tên danh mục</th>
                <th className="text-center">Thao tác</th>
              </tr>
            </thead>
            <tbody>
              {loading ? (
                <tr><td colSpan="3" className="text-center py-4">Đang tải...</td></tr>
              ) : categories.map(c => (
                <tr key={c.categoryId}>
                  <td className="ps-4 text-muted">#{c.categoryId}</td>
                  <td className="fw-bold">{c.categoryName}</td>
                  <td className="text-center">
                    <button className="btn btn-sm btn-outline-info me-2" onClick={() => handleOpenModal(c)}>Sửa</button>
                    <button className="btn btn-sm btn-outline-danger" onClick={() => handleDelete(c.categoryId)}>Xóa</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Phân trang */}
        <div className="card-footer bg-white d-flex justify-content-between align-items-center">
          <small className="text-muted">Tổng: {pageConfig.totalElements} mục</small>
          <nav>
            <ul className="pagination pagination-sm mb-0">
              <li className={`page-item ${pageConfig.pageNumber === 0 ? 'disabled' : ''}`}>
                <button className="page-link" onClick={() => fetchCategories(pageConfig.pageNumber - 1)}>Trước</button>
              </li>
              {[...Array(pageConfig.totalPages)].map((_, i) => (
                <li key={i} className={`page-item ${pageConfig.pageNumber === i ? 'active' : ''}`}>
                  <button className="page-link" onClick={() => fetchCategories(i)}>{i + 1}</button>
                </li>
              ))}
              <li className={`page-item ${pageConfig.pageNumber >= pageConfig.totalPages - 1 ? 'disabled' : ''}`}>
                <button className="page-link" onClick={() => fetchCategories(pageConfig.pageNumber + 1)}>Sau</button>
              </li>
            </ul>
          </nav>
        </div>
      </div>

      {/* Modal */}
      {showModal && (
        <div className="modal d-block" style={{backgroundColor: "rgba(0,0,0,0.5)"}}>
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content border-0">
              <form onSubmit={handleSubmit}>
                <div className="modal-header border-0">
                  <h5 className="fw-bold">{isEdit ? "Sửa danh mục" : "Thêm danh mục"}</h5>
                  <button type="button" className="btn-close" onClick={() => setShowModal(false)}></button>
                </div>
                <div className="modal-body py-4">
                  <label className="form-label small fw-bold">Tên danh mục</label>
                  <input 
                    type="text" className="form-control" required 
                    value={formData.categoryName} 
                    onChange={e => setFormData({...formData, categoryName: e.target.value})}
                  />
                </div>
                <div className="modal-footer border-0">
                  <button type="submit" className="btn btn-primary w-100">Lưu dữ liệu</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CategoryListAD;
