import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import cartService from '../../services/cartService';

const Checkout = () => {
  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(true);
  const [processing, setProcessing] = useState(false);
  const [user, setUser] = useState(null);
  const [paymentMethod, setPaymentMethod] = useState('CASH');
  const [shippingAddress, setShippingAddress] = useState('');
  const navigate = useNavigate();

  // L·∫•y th√¥ng tin user t·ª´ localStorage
  const currentUser = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    if (!currentUser || !currentUser.email) {
      alert('‚ùå Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ thanh to√°n');
      navigate('/login');
      return;
    }
    loadCartAndUser();
  }, []);

  // ‚úÖ H√†m loadCartAndUser ho√†n ch·ªânh
  const loadCartAndUser = async () => {
    try {
      setLoading(true);

      // L·∫•y th√¥ng tin user
      setUser(currentUser);

      // L·∫•y cartId t·ª´ user info
      const cartId = currentUser.cart?.cartId;
      if (!cartId) {
        alert('‚ùå Kh√¥ng t√¨m th·∫•y gi·ªè h√†ng');
        navigate('/cart');
        return;
      }

      // ƒê·∫£m b·∫£o cartId l√† s·ªë
      const numericCartId = Number(cartId);
      if (isNaN(numericCartId)) {
        alert('‚ùå L·ªói gi·ªè h√†ng: ID kh√¥ng h·ª£p l·ªá');
        navigate('/cart');
        return;
      }

      // L·∫•y gi·ªè h√†ng theo cartId v√† email
      const cartData = await cartService.getCart(currentUser.email, numericCartId);

      // ‚úÖ ƒê·∫£m b·∫£o cart c√≥ c·∫•u tr√∫c ƒë√∫ng
      const formattedCart = {
        cartId: cartData.cartId,
        products: cartData.products || [],
        totalPrice: cartData.totalPrice || 0
      };

      setCart(formattedCart);

      console.log("‚úÖ ƒê√£ t·∫£i gi·ªè h√†ng cho checkout:", formattedCart);

    } catch (error) {
      console.error('‚ùå L·ªói khi t·∫£i gi·ªè h√†ng:', error);

      if (error.response?.status === 404) {
        alert('‚ùå Kh√¥ng t√¨m th·∫•y gi·ªè h√†ng. Vui l√≤ng th√™m s·∫£n ph·∫©m v√†o gi·ªè h√†ng tr∆∞·ªõc.');
        navigate('/cart');
      } else {
        alert('‚ùå L·ªói khi t·∫£i th√¥ng tin thanh to√°n: ' + (error.response?.data?.message || error.message));
        navigate('/cart');
      }
    } finally {
      setLoading(false);
    }
  };

  // ‚úÖ X·ª≠ l√Ω chuy·ªÉn ƒë·ªïi payment method
  const handlePaymentMethodChange = (method) => {
    const paymentMethodMap = {
      'COD': 'CASH',
      'BANKING': 'BANK_TRANSFER',
      'MOMO': 'MOMO_WALLET'
    };

    const finalMethod = paymentMethodMap[method] || method;
    console.log(`üí∞ Payment method: ${method} ‚Üí ${finalMethod}`);
    setPaymentMethod(finalMethod);
  };

  // ‚úÖ H√†m ƒë·∫∑t h√†ng
  const handlePlaceOrder = async () => {
    if (!cart || !cart.cartId) {
      alert('‚ùå Kh√¥ng t√¨m th·∫•y gi·ªè h√†ng');
      return;
    }

    if (!user || !user.email) {
      alert('‚ùå Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ ƒë·∫∑t h√†ng');
      navigate('/login');
      return;
    }

    if (!shippingAddress.trim()) {
      alert('‚ùå Vui l√≤ng nh·∫≠p ƒë·ªãa ch·ªâ giao h√†ng');
      return;
    }

    if (!window.confirm('X√°c nh·∫≠n ƒë·∫∑t h√†ng?\nƒê∆°n h√†ng s·∫Ω ƒë∆∞·ª£c x·ª≠ l√Ω ngay l·∫≠p t·ª©c.')) {
      return;
    }

    try {
      setProcessing(true);
      console.log('üõí ƒêang t·∫°o ƒë∆°n h√†ng...', {
        email: user.email,
        cartId: cart.cartId,
        paymentMethod,
        shippingAddress
      });

      // L∆∞u ƒë·ªãa ch·ªâ v√†o localStorage
      localStorage.setItem('shippingAddress', shippingAddress);

      // G·ªçi API t·∫°o ƒë∆°n h√†ng
      const order = await cartService.createOrder(user.email, cart.cartId, paymentMethod);
      console.log('‚úÖ ƒê∆°n h√†ng ƒë√£ t·∫°o:', order);

      // L√†m r·ªóng gi·ªè h√†ng sau khi ƒë·∫∑t h√†ng th√†nh c√¥ng
      await clearCartAfterOrder(user.email, cart.cartId);

      alert('üéâ ƒê·∫∑t h√†ng th√†nh c√¥ng! C·∫£m ∆°n b·∫°n ƒë√£ mua s·∫Øm.');

      // Chuy·ªÉn h∆∞·ªõng ƒë·∫øn trang th√†nh c√¥ng
      navigate('/order-success', { 
        state: { 
          order: order,
          message: 'ƒê∆°n h√†ng c·ªßa b·∫°n ƒë√£ ƒë∆∞·ª£c x√°c nh·∫≠n!' 
        } 
      });

    } catch (error) {
      console.error('‚ùå L·ªói khi ƒë·∫∑t h√†ng:', error);

      let errorMessage = 'L·ªói khi ƒë·∫∑t h√†ng';

      if (error.response?.status === 400) {
        const serverMessage = error.response.data?.message || '';
        if (serverMessage.includes('payment method')) {
          errorMessage = 'Ph∆∞∆°ng th·ª©c thanh to√°n kh√¥ng h·ª£p l·ªá';
        } else if (serverMessage.includes('cart') || serverMessage.includes('Cart')) {
          errorMessage = 'Gi·ªè h√†ng kh√¥ng h·ª£p l·ªá';
        } else {
          errorMessage = serverMessage;
        }
      } else if (error.response?.status === 404) {
        errorMessage = 'Kh√¥ng t√¨m th·∫•y gi·ªè h√†ng ho·∫∑c s·∫£n ph·∫©m';
      } else if (error.response?.status === 500) {
        errorMessage = 'M√°y ch·ªß ƒëang b·∫£o tr√¨, vui l√≤ng th·ª≠ l·∫°i sau';
      }

      alert(`‚ùå ${errorMessage}`);
    } finally {
      setProcessing(false);
    }
  };

  // ‚úÖ L√†m r·ªóng gi·ªè h√†ng sau khi ƒë·∫∑t h√†ng
  const clearCartAfterOrder = async (email, cartId) => {
    try {
      console.log(`üßπ ƒêang l√†m r·ªóng gi·ªè h√†ng: ${cartId}`);

      await cartService.clearCart(email, cartId);

      // C·∫≠p nh·∫≠t localStorage
      cartService.clearCartFromStorage();

      // C·∫≠p nh·∫≠t cart count global
      window.dispatchEvent(new Event('cartUpdated'));

      console.log('‚úÖ ƒê√£ l√†m r·ªóng gi·ªè h√†ng th√†nh c√¥ng');

    } catch (error) {
      console.error('‚ùå L·ªói khi l√†m r·ªóng gi·ªè h√†ng:', error);
      // Kh√¥ng throw error ·ªü ƒë√¢y v√¨ ƒë∆°n h√†ng ƒë√£ ƒë∆∞·ª£c t·∫°o th√†nh c√¥ng
    }
  };

  // T√≠nh t·ªïng ti·ªÅn
  const totalAmount = cart ? cartService.calculateTotal(cart.products) : 0;
  const shippingFee = totalAmount >= 200000 ? 0 : 15000; // Mi·ªÖn ph√≠ ship tr√™n 200k
  const finalAmount = totalAmount + shippingFee;

  if (loading) {
    return (
      <div className="container mt-4">
