import React, { useEffect, useState, useCallback } from "react";
import { Link, useNavigate } from "react-router-dom";
import userService from "../services/userService";

// ‚úÖ Import CSS, Font, Bootstrap
import "bootstrap/dist/css/bootstrap.min.css";
import "../assets/fonts/fontawesome/css/all.min.css";

// ‚úÖ Import h√¨nh ·∫£nh
import logo from "../assets/images/logo.png";
import flagVN from "../assets/images/icons/flags/VN.webp";

const Header = () => {
  const [user, setUser] = useState(null);
  const [displayName, setDisplayName] = useState("");
  const [userInfo, setUserInfo] = useState(null);
  const [loading, setLoading] = useState(false); // ‚úÖ Th√™m state loading
  const navigate = useNavigate();

  // ‚úÖ L·∫•y user t·ª´ localStorage - s·ª≠ d·ª•ng useCallback ƒë·ªÉ tr√°nh re-render
  const fetchUserInfo = useCallback(async (parsedUser) => {
    const userId = parsedUser.userId || parsedUser.user_id;
    if (!userId) return;

    setLoading(true);
    try {
      console.log("üîÑ ƒêang t·∫£i th√¥ng tin user...");

      // ‚úÖ S·ª≠ d·ª•ng Promise v·ªõi timeout ƒë·ªÉ tr√°nh loading v√¥ h·∫°n
      const timeoutPromise = new Promise((_, reject) => 
        setTimeout(() => reject(new Error("Timeout loading user info")), 5000)
      );

      const userDataPromise = userService.getUserById(userId);

      const userData = await Promise.race([userDataPromise, timeoutPromise]);

      console.log("‚úÖ D·ªØ li·ªáu user nh·∫≠n ƒë∆∞·ª£c:", userData);

      setUserInfo(userData);

      // ‚úÖ T·∫°o t√™n hi·ªÉn th·ªã t·ª´ first_name v√† last_name
      const fullName = `${userData.first_name || ""} ${userData.last_name || ""}`.trim();
      setDisplayName(fullName || userData.email || "User");
    } catch (err) {
      console.error("‚ùå L·ªói l·∫•y th√¥ng tin user:", err);
      // ‚úÖ Fallback nhanh: s·ª≠ d·ª•ng th√¥ng tin t·ª´ localStorage
      setDisplayName(parsedUser.email || parsedUser.first_name || "User");
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      try {
        const parsedUser = JSON.parse(storedUser);
        setUser(parsedUser);

        // ‚úÖ Ki·ªÉm tra n·∫øu ƒë√£ c√≥ th√¥ng tin ƒë·∫ßy ƒë·ªß trong localStorage th√¨ kh√¥ng c·∫ßn g·ªçi API
        if (parsedUser.first_name && parsedUser.last_name) {
          const fullName = `${parsedUser.first_name} ${parsedUser.last_name}`.trim();
          setDisplayName(fullName || parsedUser.email || "User");
          return;
        }

        // ‚úÖ Ch·ªâ g·ªçi API n·∫øu th·ª±c s·ª± c·∫ßn th√¥ng tin ƒë·∫ßy ƒë·ªß
        if (parsedUser.userId || parsedUser.user_id) {
          fetchUserInfo(parsedUser);
        } else {
          // ‚úÖ Fallback nhanh
          setDisplayName(parsedUser.email || "User");
        }
      } catch (error) {
        console.error("‚ùå Parse user l·ªói:", error);
        setDisplayName("User");
      }
    }
  }, [fetchUserInfo]);

  // ‚úÖ ƒêƒÉng xu·∫•t
  const handleLogout = () => {
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    setUser(null);
    setUserInfo(null);
    setDisplayName("");
    navigate("/login");
  };

  // ‚úÖ Hi·ªÉn th·ªã t√™n ng∆∞·ªùi d√πng
  const renderUserName = () => {
    if (loading) {
      return (
        <div className="d-flex align-items-center">
          <div className="spinner-border spinner-border-sm text-primary me-2" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <span>Loading...</span>
        </div>
      );
    }

    if (user) {
      if (userInfo) {
        return (
          <span>
            {userInfo.first_name} {userInfo.last_name}
          </span>
        );
      } else {
        // ‚úÖ Hi·ªÉn th·ªã th√¥ng tin t·ª´ localStorage n·∫øu c√≥
        if (user.first_name && user.last_name) {
          return (
            <span>
              {user.first_name} {user.last_name}
            </span>
          );
        }
        return <span>{user.email || "User"}</span>;
      }
    }

    return "My profile";
  };

  return (
    <header className="section-header">
      {/* ======= THANH TR√äN ======= */}
      <nav className="navbar d-none d-md-flex p-md-0 navbar-expand-sm navbar-light border-bottom">
        <div className="container">
          <button
            className="navbar-toggler"
            type="button"
            data-toggle="collapse"
            data-target="#navbarTop4"
            aria-controls="navbarNav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          <div className="collapse navbar-collapse" id="navbarTop4">
            <ul className="navbar-nav mr-auto">
              <li>
                {user ? (
                  <span className="nav-link">
                    üëã Hi, <strong>{displayName}</strong> |{" "}
                    <button
                      onClick={handleLogout}
                      className="btn btn-link p-0 text-danger"
                      style={{ textDecoration: "none" }}
                    >
                      Logout
                    </button>
                  </span>
                ) : (
                  <span className="nav-link">
                    Hi, <Link to="/login">Log In</Link> or{" "}
                    <Link to="/register">Register</Link>
                  </span>
                )}
              </li>
              <li>
                <a href="#" className="nav-link">
                  Deals
                </a>
              </li>
              <li>
                <a href="#" className="nav-link">
                  Sell
                </a>
              </li>
              <li>
                <a href="#" className="nav-link">
                  Help
                </a>
              </li>
            </ul>

            <ul className="navbar-nav">
              <li>
                <a href="#" className="nav-link">
                  <img src={flagVN} height="24" alt="flag" /> Ship to
                </a>
              </li>
              <li className="nav-item dropdown">
                <a
                  href="#"
                  className="nav-link dropdown-toggle"
                  data-toggle="dropdown"
                >
                  Watchlist
                </a>
                <ul className="dropdown-menu small">
                  <li>
                    <a className="dropdown-item" href="#">
                      First item
                    </a>
                  </li>
                  <li>
                    <a className="dropdown-item" href="#">
                      Second item
                    </a>
                  </li>
                  <li>
                    <a className="dropdown-item" href="#">
                      Third item
                    </a>
                  </li>
                </ul>
              </li>
              <li>
                <a href="#" className="nav-link">
                  My shop
                </a>
              </li>
              <li>
                <a href="#" className="nav-link">
                  <i className="fa fa-bell"></i>
                </a>
              </li>
              <li>
                <a href="#" className="nav-link">
                  <i className="fa fa-shopping-cart"></i>
                </a>
              </li>
            </ul>
          </div>
        </div>
      </nav>

      {/* ======= THANH CH√çNH ======= */}
      <div className="container">
        <section className="header-main border-bottom">
          <div className="row row-sm">
            {/* Logo */}
            <div className="col-6 col-sm col-md col-lg flex-grow-0">
              <Link to="/" className="brand-wrap">
                <img src={logo} alt="Logo" height={56} />
              </Link>
            </div>

            {/* Thanh t√¨m ki·∫øm */}
            <div className="col-lg-6 col-xl col-md-5 col-sm-12 flex-grow-1">
              <form action="#" className="search-header">
                <div className="input-group">
                  <input
                    type="text"
                    className="form-control"
                    placeholder="Search"
                  />
                </div>
              </form>
            </div>

            {/* N√∫t search */}
            <div className="col col-lg col-md flex-grow-0">
              <button className="btn btn-block btn-primary" type="submit">
                Search
              </button>
            </div>

            {/* C√°c icon b√™n ph·∫£i */}
            <div className="col-xl-4 col-lg-5 col-md-6">
              <div className="widgets-wrap float-md-right">
                {/* Widget Profile - Hi·ªÉn th·ªã t√™n ng∆∞·ªùi d√πng */}
                <div className="widget-header mr-3">
                  <a href="#" className="widget-view">
                    <div className="icon-area">
                      <i className="fa fa-user"></i>
                    </div>
                    <small className="text">
                      {renderUserName()}
                    </small>
                  </a>
                </div>

                <div className="widget-header mr-3">
                  <a href="#" className="widget-view">
                    <div className="icon-area">
                      <i className="fa fa-comment-dots"></i>
                      <span className="notify">1</span>
                    </div>
                    <small className="text">Message</small>
                  </a>
                </div>
                <div className="widget-header mr-3">
                  <a href="#" className="widget-view">
                    <div className="icon-area">
                      <i className="fa fa-store"></i>
                    </div>
                    <small className="text">Orders</small>
                  </a>
                </div>
                <div className="widget-header">
                  <a href="#" className="widget-view">
                    <div className="icon-area">
                      <i className="fa fa-shopping-cart"></i>
                    </div>
                    <small className="text">Cart</small>
                  </a>
                </div>
              </div>
            </div>
          </div>
        </section>

        {/* Menu ch√≠nh */}
        <nav className="navbar navbar-main navbar-expand pl-0">
          <ul className="navbar-nav flex-wrap">
            <li className="nav-item">
              <Link to="/" className="nav-link">
                Home
              </Link>
            </li>
            <li className="nav-item">
              <Link to="/product" className="nav-link">
                Products
              </Link>
            </li>
          </ul>
        </nav>
      </div>
    </header>
  );
};

export default Header;
