import { useEffect, useState } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import productService from "../../services/productService";

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductDetail = () => {
  const { productId } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    console.log("üîÑ Product ID t·ª´ URL:", productId, "Type:", typeof productId);

    const fetchProductDetail = async () => {
      // Ki·ªÉm tra n·∫øu productId kh√¥ng t·ªìn t·∫°i ho·∫∑c l√† chu·ªói 'undefined'
      if (!productId || productId === 'undefined' || productId === 'null') {
        setError("Kh√¥ng t√¨m th·∫•y ID s·∫£n ph·∫©m t·ª´ URL");
        setLoading(false);
        return;
      }

      try {
        setLoading(true);
        setError(null);
        console.log("üì° G·ªçi API v·ªõi ID:", productId);

        const productData = await productService.getProductById(productId);
        console.log("‚úÖ D·ªØ li·ªáu nh·∫≠n ƒë∆∞·ª£c:", productData);

        setProduct(productData);
      } catch (err) {
        console.error("‚ùå L·ªói khi t·∫£i chi ti·∫øt s·∫£n ph·∫©m:", err);
        setError(`Kh√¥ng th·ªÉ t·∫£i th√¥ng tin s·∫£n ph·∫©m: ${err.response?.data?.message || err.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchProductDetail();
  }, [productId]);

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-2">ƒêang t·∫£i th√¥ng tin s·∫£n ph·∫©m...</p>
        </div>
      </div>
    );
  }

  if (error || !product) {
    return (
      <div className="container mt-4">
        <div className="alert alert-danger text-center">
          <h4>‚ùå Kh√¥ng th·ªÉ t·∫£i s·∫£n ph·∫©m</h4>
          <p>{error || "S·∫£n ph·∫©m kh√¥ng t·ªìn t·∫°i"}</p>
          <p className="small text-muted">Product ID t·ª´ URL: <code>"{productId}"</code></p>
          <div className="mt-3">
            <button 
              className="btn btn-primary me-2"
              onClick={() => navigate(-1)}
            >
              ‚Üê Quay l·∫°i
            </button>
            <Link to="/product" className="btn btn-outline-primary">
              üè† V·ªÅ trang s·∫£n ph·∫©m
            </Link>
          </div>
        </div>
      </div>
    );
  }

  // S·ª≠ d·ª•ng c·∫£ productName v√† product_name ƒë·ªÉ ƒë·∫£m b·∫£o t∆∞∆°ng th√≠ch
  const productName = product.productName || product.product_name;
  const productPrice = product.price;
  const productSpecialPrice = product.special_price;
  const productQuantity = product.quantity;
  const productDiscount = product.discount;
  const productDescription = product.description;

  return (
    <div className="container mt-4">
      {/* Breadcrumb */}
      <nav aria-label="breadcrumb">
        <ol className="breadcrumb">
          <li className="breadcrumb-item">
            <Link to="/" className="text-decoration-none">üè† Trang ch·ªß</Link>
          </li>
          <li className="breadcrumb-item">
            <Link to="/product" className="text-decoration-none">S·∫£n ph·∫©m</Link>
          </li>
          <li className="breadcrumb-item active" aria-current="page">
            {productName}
          </li>
        </ol>
      </nav>

      <div className="row">
        {/* H√¨nh ·∫£nh s·∫£n ph·∫©m */}
        <div className="col-md-6">
          <div className="card shadow-sm">
            <img
              src={`${IMAGE_BASE_URL}/${product.image}`}
              alt={productName}
              onError={(e) =>
                (e.target.src =
                  "https://cdn-icons-png.flaticon.com/512/679/679922.png")
              }
              className="img-fluid rounded"
              style={{ maxHeight: "500px", objectFit: "cover" }}
            />
          </div>
        </div>

        {/* Th√¥ng tin s·∫£n ph·∫©m */}
        <div className="col-md-6">
          <div className="card shadow-sm p-4">
            <h1 className="h2 text-primary mb-3">{productName}</h1>

            {/* Gi√° s·∫£n ph·∫©m */}
            <div className="mb-3">
              {productSpecialPrice ? (
                <div>
                  <span className="h3 text-danger me-2">
                    {productSpecialPrice?.toLocaleString()} ‚Ç´
                  </span>
                  <span className="h5 text-muted text-decoration-line-through">
                    {productPrice?.toLocaleString()} ‚Ç´
                  </span>
                  {productDiscount > 0 && (
                    <span className="badge bg-success ms-2 fs-6">
                      -{productDiscount}%
                    </span>
                  )}
                </div>
              ) : (
                <span className="h3 text-danger">
                  {productPrice?.toLocaleString()} ‚Ç´
                </span>
              )}
            </div>

            {/* T√¨nh tr·∫°ng kho */}
            <div className="mb-3">
              <span className={`badge ${productQuantity === 0 ? 'bg-danger' : 'bg-success'} fs-6`}>
                {productQuantity === 0 ? 'H·∫øt h√†ng' : `C√≤n ${productQuantity} s·∫£n ph·∫©m`}
              </span>
            </div>

            {/* M√¥ t·∫£ s·∫£n ph·∫©m */}
            <div className="mb-4">
              <h5 className="text-dark">üìù M√¥ t·∫£ s·∫£n ph·∫©m</h5>
              <p className="text-muted" style={{ whiteSpace: 'pre-line' }}>
                {productDescription || "ƒêang c·∫≠p nh·∫≠t..."}
              </p>
            </div>

            {/* N√∫t h√†nh ƒë·ªông */}
            <div className="d-grid gap-2 d-md-flex">
             <Link
  to="/cart/cartId"
  className="btn btn-primary btn-lg flex-fill me-md-2"
  disabled={productQuantity === 0}
>
  üõí Th√™m v√†o gi·ªè h√†ng
</Link>
              <button 
                className="btn btn-outline-danger btn-lg flex-fill"
                disabled={productQuantity === 0}
              >
                ‚ù§Ô∏è Y√™u th√≠ch
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
