import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import orderService from '../../services/orderService';

const OrderList = () => {
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(true);
    const [cancelling, setCancelling] = useState(null);
    const [selectedOrder, setSelectedOrder] = useState(null);
    const [showDetail, setShowDetail] = useState(false);
    const [activeTab, setActiveTab] = useState('ALL');
    const navigate = useNavigate();

    const currentUser = JSON.parse(localStorage.getItem('user'));

    useEffect(() => {
        if (!currentUser) {
            navigate('/login');
            return;
        }
        loadOrders();
    }, []);

    const loadOrders = async () => {
        try {
            setLoading(true);
            const data = await orderService.getUserOrders(currentUser.email);
            const sorted = Array.isArray(data) 
                ? data.sort((a, b) => new Date(b.orderDate || b.createdAt) - new Date(a.orderDate || a.createdAt))
                : [];
            setOrders(sorted);
        } catch (error) {
            console.error('Lỗi tải đơn hàng:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleCancelOrder = async (orderId) => {
        if (!window.confirm('Bạn có chắc muốn hủy đơn hàng này?')) return;
        try {
            setCancelling(orderId);
            await orderService.cancelOrder(currentUser.email, orderId, 'Người dùng hủy');
            loadOrders();
            if (showDetail) setShowDetail(false);
        } catch (error) {
            alert('Lỗi khi hủy đơn');
        } finally {
            setCancelling(null);
        }
    };

    const filteredOrders = activeTab === 'ALL' 
        ? orders 
        : orders.filter(o => o.status === activeTab);

    if (loading) return (
        <div className="d-flex justify-content-center align-items-center" style={{height: '80vh'}}>
            <div className="spinner-grow text-primary" role="status"></div>
        </div>
    );

    return (
        <div className="container py-5">
            <div className="row mb-4">
                <div className="col-lg-8 mx-auto text-center">
                    <h2 className="fw-bold mb-3">Đơn hàng của tôi</h2>
                    <p className="text-muted">Theo dõi và quản lý lịch sử mua hàng của bạn</p>
                </div>
            </div>

            {/* Tab Filter */}
            <div className="d-flex justify-content-center mb-4 overflow-auto pb-2">
                <div className="btn-group shadow-sm bg-white rounded-pill p-1">
                    {['ALL', 'PENDING', 'CONFIRMED', 'DELIVERED', 'CANCELLED'].map(tab => (
                        <button
                            key={tab}
                            className={`btn rounded-pill px-4 ${activeTab === tab ? 'btn-primary shadow' : 'btn-white border-0'}`}
                            onClick={() => setActiveTab(tab)}
                        >
                            {tab === 'ALL' ? 'Tất cả' : orderService.formatOrderStatus(tab).text}
                        </button>
                    ))}
                </div>
            </div>

            <div className="row g-4">
                {filteredOrders.length === 0 ? (
                    <div className="col-12 text-center py-5">
                        <img src="https://cdn-icons-png.flaticon.com/512/11329/11329073.png" width="120" alt="empty" className="mb-3 opacity-50" />
                        <h5 className="text-muted">Không tìm thấy đơn hàng nào</h5>
                        <Link to="/product" className="btn btn-primary mt-3 px-4 rounded-pill">Tiếp tục mua sắm</Link>
                    </div>
                ) : (
                    filteredOrders.map(order => (
                        <OrderCard 
                            key={order.orderId} 
                            order={order} 
                            onViewDetail={(o) => { setSelectedOrder(o); setShowDetail(true); }}
                            onCancel={handleCancelOrder}
                            isCancelling={cancelling === order.orderId}
                        />
                    ))
                )}
            </div>

            {showDetail && (
                <OrderDetailModal 
                    order={selectedOrder} 
                    onClose={() => setShowDetail(false)} 
                    onCancel={handleCancelOrder}
                    cancelling={cancelling}
                />
            )}
        </div>
    );
};

// --- Sub Component: OrderCard ---
const OrderCard = ({ order, onViewDetail, onCancel, isCancelling }) => {
    const status = orderService.formatOrderStatus(order.status);
    const products = orderService.getOrderProducts(order);
    const total = orderService.calculateOrderTotal(order);

    return (
        <div className="col-12 col-md-6 col-lg-4">
            <div className="card border-0 shadow-sm h-100 transition-hover">
                <div className="card-body p-4">
                    <div className="d-flex justify-content-between align-items-start mb-3">
                        <div>
                            <span className="text-muted small">#{order.orderId}</span>
                            <div className="fw-bold">{orderService.formatDate(order.orderDate || order.createdAt)}</div>
                        </div>
                        <span className={`badge rounded-pill bg-${status.class}-subtle text-${status.class} px-3 py-2`}>
                            {status.text}
                        </span>
                    </div>

                    <div className="order-items-preview mb-3 border-top pt-3">
                        {products.slice(0, 1).map((p, i) => (
                            <div key={i} className="d-flex align-items-center mb-2">
                                <img src={p.image ? `http://localhost:8080/api/public/products/image/${p.image}` : '/placeholder.png'} 
                                     alt={p.productName} className="rounded" style={{width: 45, height: 45, objectFit: 'cover'}} />
                                <div className="ms-2 flex-grow-1 text-truncate small fw-medium">{p.productName}</div>
                                <div className="ms-2 small text-muted">x{p.quantity}</div>
                            </div>
                        ))}
                        {products.length > 1 && <div className="text-primary small text-center mt-2">Xem thêm {products.length - 1} sản phẩm khác...</div>}
                    </div>

                    <div className="d-flex justify-content-between align-items-end mt-auto">
                        <div>
                            <div className="small text-muted">Tổng cộng</div>
                            <div className="h5 mb-0 text-danger fw-bold">
                                {total.toLocaleString('vi-VN')}đ
                            </div>
                        </div>
                        <div className="btn-group">
                            <button className="btn btn-light btn-sm rounded-circle shadow-sm me-2" onClick={() => onViewDetail(order)}>
                                <i className="fas fa-eye"></i>
                            </button>
                            {orderService.canCancelOrder(order) && (
                                <button className="btn btn-outline-danger btn-sm rounded-pill px-3" 
                                        disabled={isCancelling} onClick={() => onCancel(order.orderId)}>
                                    {isCancelling ? '...' : 'Hủy'}
                                </button>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

// --- Sub Component: Detail Modal (Hóa đơn Clean) ---
const OrderDetailModal = ({ order, onClose, onCancel, cancelling }) => {
    if (!order) return null;
    const products = orderService.getOrderProducts(order);
    const total = orderService.calculateOrderTotal(order);
    const status = orderService.formatOrderStatus(order.status);

    return (
        <div className="modal show d-block animate-fade-in" style={{backgroundColor: 'rgba(0,0,0,0.6)', backdropFilter: 'blur(4px)'}}>
            <div className="modal-dialog modal-lg modal-dialog-centered">
                <div className="modal-content border-0 shadow-lg rounded-4 overflow-hidden">
                    <div className={`modal-header bg-${status.class} text-white border-0 py-3`}>
                        <h5 className="mb-0 fw-bold"><i className="fas fa-receipt me-2"></i>Chi tiết hóa đơn #{order.orderId}</h5>
                        <button className="btn-close btn-close-white" onClick={onClose}></button>
                    </div>
                    <div className="modal-body p-4 bg-light">
                        <div className="row g-4">
                            <div className="col-md-7">
                                <div className="bg-white rounded-4 p-4 shadow-sm h-100">
                                    <h6 className="fw-bold mb-3 border-bottom pb-2">Sản phẩm</h6>
                                    <div className="overflow-auto" style={{maxHeight: '300px'}}>
                                        {products.map((item, idx) => (
                                            <div key={idx} className="d-flex align-items-center mb-3 pb-3 border-bottom-dashed">
                                                <img src={item.image ? `http://localhost:8080/api/public/products/image/${item.image}` : '/placeholder.png'} 
                                                     className="rounded-3 me-3 shadow-sm" style={{width: 60, height: 60, objectFit: 'cover'}} />
                                                <div className="flex-grow-1">
                                                    <div className="fw-bold small">{item.productName}</div>
                                                    <div className="text-muted small">{item.quantity} x {(item.finalPrice || item.price).toLocaleString('vi-VN')}đ</div>
                                                </div>
                                                <div className="fw-bold text-dark">{(item.quantity * (item.finalPrice || item.price)).toLocaleString('vi-VN')}đ</div>
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            </div>
                            <div className="col-md-5">
                                <div className="bg-white rounded-4 p-4 shadow-sm">
                                    <h6 className="fw-bold mb-3 border-bottom pb-2">Thông tin nhận hàng</h6>
                                    <div className="small mb-2"><i className="fas fa-user me-2 text-muted"></i>{order.email}</div>
                                    <div className="small mb-2"><i className="fas fa-map-marker-alt me-2 text-muted"></i>{order.shippingAddress || 'N/A'}</div>
                                    <div className="small mb-3"><i className="fas fa-credit-card me-2 text-muted"></i>{orderService.formatPaymentMethod(order.paymentMethod)}</div>

                                    <div className="bg-danger-subtle rounded-3 p-3 mt-4">
                                        <div className="d-flex justify-content-between align-items-center mb-1">
                                            <span className="small">Tổng tiền hàng</span>
                                            <span className="fw-medium">{total.toLocaleString('vi-VN')}đ</span>
                                        </div>
                                        <div className="d-flex justify-content-between align-items-center mb-2">
                                            <span className="small">Phí vận chuyển</span>
                                            <span className="text-success fw-medium">Miễn phí</span>
                                        </div>
                                        <hr className="my-2" />
                                        <div className="d-flex justify-content-between align-items-center">
                                            <span className="fw-bold">Thanh toán</span>
                                            <span className="h5 mb-0 text-danger fw-bold">{total.toLocaleString('vi-VN')}đ</span>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="modal-footer border-0 p-3 bg-white justify-content-between">
                        <span className={`small text-${status.class} fw-bold`}>Trạng thái: {status.text}</span>
                        <div>
                            {orderService.canCancelOrder(order) && (
                                <button className="btn btn-outline-danger me-2 px-4 rounded-pill" 
                                        onClick={() => onCancel(order.orderId)}>Hủy đơn</button>
                            )}
                            <button className="btn btn-dark px-4 rounded-pill" onClick={onClose}>Đóng</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default OrderList;
