import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import productService from '../../services/productService';

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductSold = () => {
  const [allProducts, setAllProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(0);
  const [isLastPage, setIsLastPage] = useState(false);

  useEffect(() => {
    fetchAllProducts();
  }, []);

  // Chỉ tập trung vào API lấy tất cả sản phẩm (thường là /api/public/products)
  const fetchAllProducts = async (pageNumber = 0) => {
    setLoading(true);
    try {
      const res = await productService.getAllProducts(pageNumber, 8);
      if (pageNumber === 0) {
        setAllProducts(res.content || []);
      } else {
        setAllProducts(prev => [...prev, ...(res.content || [])]);
      }
      setIsLastPage(res.last); // Kiểm tra xem đã hết sản phẩm chưa
      setPage(pageNumber);
    } catch (err) {
      console.error('Lỗi tải sản phẩm:', err);
    } finally {
      setLoading(false);
    }
  };

  const loadMore = () => {
    if (!isLastPage) {
      fetchAllProducts(page + 1);
    }
  };

  const ProductItem = ({ p }) => {
    const productId = p.productId || p.product_id;
    return (
      <div className="col-xl-3 col-lg-4 col-md-6 col-6 mb-4">
        <div className="card h-100 border-0 shadow-sm hover-up overflow-hidden">
          <div className="p-3 text-center bg-white">
            <Link to={`/product/${productId}`}>
              <img
                src={`${IMAGE_BASE_URL}/${p.image}`}
                alt={p.productName}
                onError={(e) => (e.target.src = "https://cdn-icons-png.flaticon.com/512/679/679922.png")}
                className="img-fluid"
                style={{ height: "200px", objectFit: "contain" }}
              />
            </Link>
          </div>
          <div className="card-body p-3">
            <Link to={`/product/${productId}`} className="text-dark fw-bold text-decoration-none d-block mb-1 text-truncate">
              {p.productName}
            </Link>
            <div className="text-danger fw-bold h5 mb-2">
              {p.price?.toLocaleString()} ₫
            </div>
            <div className="d-flex justify-content-between align-items-center small text-muted">
              <span>Còn lại: {p.quantity}</span>
              <span className="badge bg-light text-dark"><i className="fa fa-star text-warning"></i> 5.0</span>
            </div>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="main-content-wrapper bg-light pb-5">

      {/* SECTION: KHÁM PHÁ MỸ PHẨM */}
      <section className="container pt-5">
        <div className="d-flex justify-content-between align-items-center mb-4 border-start border-4 border-primary ps-3">
          <div>
            <h3 className="fw-bold mb-0 text-uppercase">Tất cả sản phẩm</h3>
            <p className="text-muted mb-0">Mỹ phẩm chính hãng 100%</p>
          </div>
        </div>

        <div className="row">
          {allProducts.length > 0 ? (
            allProducts.map((p, i) => <ProductItem key={i} p={p} />)
          ) : (
            !loading && <div className="col-12 text-center my-5">Chưa có sản phẩm nào được hiển thị.</div>
          )}
        </div>

        {/* NÚT XEM THÊM */}
        {!isLastPage && allProducts.length > 0 && (
          <div className="text-center mt-4">
            <button 
              className="btn btn-primary px-5 rounded-pill shadow-sm fw-bold" 
              onClick={loadMore} 
              disabled={loading}
            >
              {loading ? <span className="spinner-border spinner-border-sm me-2"></span> : "XEM THÊM"}
            </button>
          </div>
        )}
      </section>
    </div>
  );
};

export default ProductSold;
