import React, { useState, useEffect, useCallback } from "react";
import categoryService from "../../../services/categoryService"; 
import Swal from "sweetalert2";

const CategoryListAD = () => {
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [isEdit, setIsEdit] = useState(false);
  const [formData, setFormData] = useState({ categoryId: "", categoryName: "" });

  // Quản lý phân trang khớp với CategoryResponse từ BE
  const [pageConfig, setPageConfig] = useState({
    pageNumber: 0,
    pageSize: 10,
    totalPages: 0,
    totalElements: 0,
  });

  // ================= FETCH DATA (Khớp với CategoryResponse) =================
  const fetchCategories = useCallback(async (page = 0) => {
    try {
      setLoading(true);
      // Gọi service. Hàm getAllCategories cần nhận tham số (page, size)
      // Nếu service của mày chưa nhận, hãy sửa nó hoặc dùng trực tiếp api.get ở đây
      const response = await categoryService.getAllCategories(page, pageConfig.pageSize);
      
      // Controller trả về CategoryResponse -> lấy .content
      // Lưu ý: data ở đây là kết quả từ service trả về
      setCategories(response.content || []); 
      
      setPageConfig({
        pageNumber: response.pageNumber,
        pageSize: response.pageSize,
        totalPages: response.totalPages,
        totalElements: response.totalElements,
      });
    } catch (err) {
      console.error("❌ Lỗi load Category:", err);
      Swal.fire("Lỗi", "Không thể tải danh sách danh mục", "error");
    } finally {
      setLoading(false);
    }
  }, [pageConfig.pageSize]);

  useEffect(() => {
    fetchCategories(0); // Luôn bắt đầu từ trang 0
  }, [fetchCategories]);

  // ================= SUBMIT (POST & PUT đúng URL) =================
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (formData.categoryName.trim().length < 5) {
      return Swal.fire("Lỗi", "Tên danh mục phải có ít nhất 5 ký tự (Valid check)", "warning");
    }

    try {
      // Body gửi lên khớp với Entity Category ở BE
      const categoryPayload = { categoryName: formData.categoryName };

      if (isEdit) {
        // Khớp @PutMapping({"/admin/categories/{categoryId}"})
        await categoryService.updateCategory(formData.categoryId, categoryPayload);
        Swal.fire("Thành công", "Đã cập nhật danh mục", "success");
      } else {
        // Khớp @PostMapping({"/admin/categories"})
        await categoryService.createCategory(categoryPayload);
        Swal.fire("Thành công", "Đã thêm danh mục mới", "success");
      }

      setShowModal(false);
      fetchCategories(pageConfig.pageNumber); 
    } catch (err) {
      const errMsg = err.response?.data?.message || "Thao tác thất bại";
      Swal.fire("Lỗi", errMsg, "error");
    }
  };

  // ================= DELETE (Khớp @DeleteMapping) =================
  const handleDelete = async (id) => {
    const result = await Swal.fire({
      title: "Xác nhận xóa?",
      text: "Xóa danh mục sẽ mất toàn bộ sản phẩm bên trong!",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#d33",
      confirmButtonText: "Xóa ngay",
    });

    if (result.isConfirmed) {
      try {
        // Đảm bảo trong categoryService mày có hàm: 
        // deleteCategory(id) { return api.delete(`/admin/categories/${id}`); }
        await categoryService.deleteCategory(id);
        Swal.fire("Đã xóa!", "Danh mục đã bị loại bỏ.", "success");
        fetchCategories(0);
      } catch (err) {
        Swal.fire("Lỗi", "Không thể xóa danh mục này!", "error");
      }
    }
  };

  // Mở modal
  const handleOpenModal = (cat = null) => {
    if (cat) {
      setIsEdit(true);
      setFormData({ categoryId: cat.categoryId, categoryName: cat.categoryName });
    } else {
      setIsEdit(false);
      setFormData({ categoryId: "", categoryName: "" });
    }
    setShowModal(true);
  };

  return (
    <div className="p-4">
      <div className="card shadow-sm border-0 rounded-3">
        <div className="card-header bg-white border-bottom py-3 d-flex justify-content-between align-items-center">
          <h5 className="mb-0 fw-bold">Danh Mục Sản Phẩm</h5>
          <button className="btn btn-dark btn-sm rounded-2" onClick={() => handleOpenModal()}>
            + Thêm Category
          </button>
        </div>
        
        <div className="table-responsive">
          <table className="table table-hover align-middle mb-0">
            <thead className="table-light">
              <tr>
                <th className="px-4">Mã</th>
                <th>Tên danh mục</th>
                <th className="text-center">Thao tác</th>
              </tr>
            </thead>
            <tbody>
              {loading ? (
                <tr><td colSpan="3" className="text-center py-5">Đang tải dữ liệu...</td></tr>
              ) : (
                categories.map((cat) => (
                  <tr key={cat.categoryId}>
                    <td className="px-4 text-secondary">#{cat.categoryId}</td>
                    <td className="fw-medium">{cat.categoryName}</td>
                    <td className="text-center">
                      <button className="btn btn-outline-primary btn-sm me-2" onClick={() => handleOpenModal(cat)}>Sửa</button>
                      <button className="btn btn-outline-danger btn-sm" onClick={() => handleDelete(cat.categoryId)}>Xóa</button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>

        {/* Thanh Phân Trang */}
        <div className="card-footer bg-white py-3 d-flex justify-content-between">
          <small className="text-muted">Tổng: {pageConfig.totalElements}</small>
          <nav>
            <ul className="pagination pagination-sm mb-0">
              <li className={`page-item ${pageConfig.pageNumber === 0 ? 'disabled' : ''}`}>
                <button className="page-link" onClick={() => fetchCategories(pageConfig.pageNumber - 1)}>Trước</button>
              </li>
              {[...Array(pageConfig.totalPages)].map((_, i) => (
                <li key={i} className={`page-item ${pageConfig.pageNumber === i ? 'active' : ''}`}>
                  <button className="page-link" onClick={() => fetchCategories(i)}>{i + 1}</button>
                </li>
              ))}
              <li className={`page-item ${pageConfig.pageNumber >= pageConfig.totalPages - 1 ? 'disabled' : ''}`}>
                <button className="page-link" onClick={() => fetchCategories(pageConfig.pageNumber + 1)}>Sau</button>
              </li>
            </ul>
          </nav>
        </div>
      </div>

      {/* Modal - Cấu trúc đơn giản cho Category */}
      {showModal && (
        <div className="modal d-block" style={{backgroundColor: "rgba(0,0,0,0.5)"}}>
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content border-0">
              <form onSubmit={handleSubmit}>
                <div className="modal-header border-0">
                  <h6 className="fw-bold mb-0">{isEdit ? "CẬP NHẬT" : "TẠO MỚI"}</h6>
                  <button type="button" className="btn-close" onClick={() => setShowModal(false)}></button>
                </div>
                <div className="modal-body">
                  <label className="form-label small text-muted text-uppercase fw-bold">Tên danh mục</label>
                  <input 
                    type="text" className="form-control" required
                    value={formData.categoryName}
                    onChange={e => setFormData({...formData, categoryName: e.target.value})}
                  />
                </div>
                <div className="modal-footer border-0">
                  <button type="submit" className="btn btn-primary w-100">Xác nhận</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CategoryListAD;
