import { useEffect, useState, useCallback } from "react";
import { useLocation } from "react-router-dom";
import productService from "../../services/productService";
import categoryService from "../../services/categoryService";
import ProductCard from "../product/ProductCard";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [pageNumber, setPageNumber] = useState(0);
  const [pageSize] = useState(8);
  const [totalPages, setTotalPages] = useState(0);
  const [loading, setLoading] = useState(true);
  const location = useLocation();

  const [searchTerm, setSearchTerm] = useState("");
  const [filters, setFilters] = useState({
    categoryId: location.state?.selectedCategory || "",
    searchKeyword: ""
  });

  // 1. Debounce Search (Tr√°nh lag khi g√µ)
  useEffect(() => {
    const delayDebounceFn = setTimeout(() => {
      setFilters(prev => ({ ...prev, searchKeyword: searchTerm }));
      setPageNumber(0);
    }, 500);
    return () => clearTimeout(delayDebounceFn);
  }, [searchTerm]);

  // 2. T·∫£i danh m·ª•c
  useEffect(() => {
    (async () => {
      try {
        const response = await categoryService.getAllCategories();
        setCategories(response || []);
      } catch (err) { console.error("‚ùå L·ªói t·∫£i danh m·ª•c:", err); }
    })();
  }, []);

  // 3. H√†m t·∫£i s·∫£n ph·∫©m (Logic t·ªëi ∆∞u)
  const fetchProducts = useCallback(async () => {
    setLoading(true);
    window.scrollTo({ top: 0, behavior: 'smooth' });

    try {
      let response;
      const { categoryId, searchKeyword } = filters;

      if (categoryId && searchKeyword) {
        response = await productService.getProductsByCategoryAndSearch(categoryId, searchKeyword, pageNumber, pageSize);
      } else if (categoryId) {
        response = await productService.getProductsByCategory(categoryId, pageNumber, pageSize);
      } else if (searchKeyword) {
        response = await productService.searchProducts(searchKeyword, pageNumber, pageSize);
      } else {
        response = await productService.getAllProducts(pageNumber, pageSize);
      }

      setProducts(response?.content || []);
      setTotalPages(response?.totalPages || 0);
    } catch (err) {
      setProducts([]);
    } finally {
      setTimeout(() => setLoading(false), 300);
    }
  }, [pageNumber, pageSize, filters]);

  useEffect(() => {
    fetchProducts();
  }, [fetchProducts]);

  // Nh·∫≠n category t·ª´ trang kh√°c (vd: Home)
  useEffect(() => {
    if (location.state?.selectedCategory) {
      setFilters(prev => ({ ...prev, categoryId: location.state.selectedCategory }));
      setPageNumber(0);
      window.history.replaceState({}, document.title);
    }
  }, [location.state]);

  const handleClearFilters = () => {
    setSearchTerm("");
    setFilters({ categoryId: "", searchKeyword: "" });
    setPageNumber(0);
  };

  return (
    <section className="container mt-5 pb-5 animate__animated animate__fadeIn">
      {/* Header & B·ªô l·ªçc t∆∞∆°ng t·ª± nh∆∞ tr∆∞·ªõc nh∆∞ng s·∫°ch h∆°n */}
      <header className="mb-5 text-center">
        <h2 className="fw-bold text-primary text-uppercase letter-spacing-1">
          {filters.categoryId 
            ? `üìÇ ${categories.find(c => (c.categoryId || c.id) == filters.categoryId)?.categoryName || 'Danh m·ª•c'}` 
            : "üõçÔ∏è Kh√°m ph√° m·ªπ ph·∫©m"}
        </h2>
        <div className="mx-auto bg-primary rounded-pill mt-2" style={{ width: '60px', height: '4px' }}></div>
      </header>

      {/* Thanh c√¥ng c·ª• l·ªçc */}
      <div className="card shadow-sm border-0 mb-5 p-3 bg-white rounded-4 border-start border-primary border-4">
        <div className="row g-3">
          <div className="col-md-4">
            <select 
              value={filters.categoryId} 
              onChange={(e) => { setFilters(prev => ({ ...prev, categoryId: e.target.value })); setPageNumber(0); }} 
              className="form-select border-0 bg-light rounded-pill px-4"
            >
              <option value="">T·∫•t c·∫£ danh m·ª•c</option>
              {categories.map((cat) => (
                <option key={cat.categoryId || cat.id} value={cat.categoryId || cat.id}>
                  {cat.categoryName || cat.name}
                </option>
              ))}
            </select>
          </div>
          <div className="col-md-5">
            <div className="input-group bg-light rounded-pill px-3">
              <span className="input-group-text bg-transparent border-0"><i className="fa fa-search text-muted"></i></span>
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="form-control bg-transparent border-0 shadow-none"
                placeholder="T√¨m t√™n s·∫£n ph·∫©m..."
              />
            </div>
          </div>
          <div className="col-md-3">
            <button onClick={handleClearFilters} className="btn btn-primary w-100 rounded-pill shadow-sm">
               <i className="fa fa-refresh me-2"></i> L√†m m·ªõi
            </button>
          </div>
        </div>
      </div>

      {/* HI·ªÇN TH·ªä S·∫¢N PH·∫®M */}
      <div className={`row transition-all ${loading ? 'opacity-50' : 'opacity-100'}`}>
        {products.length > 0 ? (
          products.map((p) => (
            <ProductCard key={p.productId || p.id} product={p} />
          ))
        ) : !loading && (
          <div className="text-center py-5">
             <i className="fa fa-search-minus display-1 text-light mb-4"></i>
             <h4 className="text-muted">Kh√¥ng t√¨m th·∫•y s·∫£n ph·∫©m ph√π h·ª£p</h4>
             <button className="btn btn-link" onClick={handleClearFilters}>Xem t·∫•t c·∫£ s·∫£n ph·∫©m</button>
          </div>
        )}
      </div>

      {/* PH√ÇN TRANG (T·ªëi ∆∞u giao di·ªán tr√≤n) */}
      {totalPages > 1 && (
        <nav className="mt-5">
          <ul className="pagination justify-content-center gap-2">
             <li className={`page-item ${pageNumber === 0 ? 'disabled' : ''}`}>
               <button className="page-link rounded-circle shadow-sm border-0" onClick={() => setPageNumber(pageNumber - 1)}>
                  <i className="fa fa-chevron-left"></i>
               </button>
             </li>
             {[...Array(totalPages)].map((_, i) => (
               <li key={i} className={`page-item ${i === pageNumber ? 'active' : ''}`}>
                 <button className="page-link rounded-circle shadow-sm border-0" onClick={() => setPageNumber(i)}>{i + 1}</button>
               </li>
             ))}
             <li className={`page-item ${pageNumber === totalPages - 1 ? 'disabled' : ''}`}>
               <button className="page-link rounded-circle shadow-sm border-0" onClick={() => setPageNumber(pageNumber + 1)}>
                  <i className="fa fa-chevron-right"></i>
               </button>
             </li>
          </ul>
        </nav>
      )}

      <style>{`
        .letter-spacing-1 { letter-spacing: 1px; }
        .page-link { width: 45px; height: 45px; display: flex; align-items: center; justify-content: center; transition: 0.3s; }
        .page-item.active .page-link { background: #0d6efd; color: white; transform: scale(1.1); }
        .transition-all { transition: opacity 0.4s ease-in-out; }
      `}</style>
    </section>
  );
};

export default ProductList;
