      const response = await api.put(`/public/carts/${cartId}/products/${productId}/quantity/${quantity}`);
      return response.data;
    } catch (error) {
      console.error('âŒ Lá»—i khi cáº­p nháº­t sá»‘ lÆ°á»£ng:', error);
      throw error;
    }
  },

  // XÃ³a sáº£n pháº©m
  removeFromCart: async (cartId, productId) => {
    try {
      const response = await api.delete(`/public/carts/${cartId}/products/${productId}`);
      return response.data;
    } catch (error) {
      console.error('âŒ Lá»—i khi xÃ³a sáº£n pháº©m:', error);
      throw error;
    }
  },

  // Táº¡o Ä‘Æ¡n hÃ ng - FIXED
  createOrder: async (email, cartId, paymentMethod) => {
    try {
      console.log('ðŸ’° Táº¡o Ä‘Æ¡n hÃ ng vá»›i:', { 
        email, 
        cartId, 
        paymentMethod,
        fullUrl: `/public/user=${email}/carts/${cartId}/payments/${paymentMethod}/order`
      });

      const response = await api.post(
        `/public/user=${email}/carts/${cartId}/payments/${paymentMethod}/order`
      );

      console.log('âœ… ÄÆ¡n hÃ ng táº¡o thÃ nh cÃ´ng:', response.data);
