      });
      throw error;
    }
  },

  // Láº¥y sá»‘ lÆ°á»£ng sáº£n pháº©m trong giá» hÃ ng
  getCartCount: async (email) => {
    try {
      const cart = await cartService.getActiveCart(email);
      if (!cart || !cart.products) return 0;
      return cart.products.reduce((total, item) => total + (item.quantity || 1), 0);
    } catch (error) {
      console.error('âŒ Lá»—i khi láº¥y sá»‘ lÆ°á»£ng giá» hÃ ng:', error);
      return 0;
    }
  },

  // Hoáº·c náº¿u báº¡n muá»‘n tÃ­nh tá»« localStorage cho nhanh
  getCartCountFromStorage: () => {
    try {
