  // âœ… THÃŠM: Hoáº·c hÃ m xÃ³a toÃ n bá»™ sáº£n pháº©m trong giá» hÃ ng
  removeAllFromCart: async (email, cartId) => {
    try {
      // Láº¥y giá» hÃ ng hiá»‡n táº¡i
      const cart = await cartService.getActiveCart(email);

