import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import authService from "../../services/authService";
import userService from "../../services/userService";

const Login = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

const handleSubmit = async (e) => {
  e.preventDefault();
  try {
    console.log("üîÑ Sending login request...");

    const res = await authService.login(form);
    console.log("‚úÖ LOGIN SUCCESS:", res.data);

    const loginData = res.data;

    // ‚úÖ L∆ØU D·ªÆ LI·ªÜU
    const userToStore = {
      userId: loginData.user_id,
      user_id: loginData.user_id, 
      email: loginData.email,
      first_name: loginData.first_name,
      last_name: loginData.last_name
    };

    localStorage.setItem("user", JSON.stringify(userToStore));
    localStorage.setItem("token", loginData['jwt-token']);

    console.log("üíæ User saved:", userToStore);
    alert("‚úÖ ƒêƒÉng nh·∫≠p th√†nh c√¥ng!");
    window.location.href = "/";

  } catch (err) {
    console.error("‚ùå LOGIN ERROR:", {
      status: err.response?.status,
      data: err.response?.data,
      message: err.message
    });

    // ‚úÖ HI·ªÇN TH·ªä L·ªñI T·ª™ BACKEND
    const backendError = err.response?.data?.message || err.response?.data?.error;
    alert("‚ùå " + (backendError || "ƒêƒÉng nh·∫≠p th·∫•t b·∫°i!"));
  }
};
  return (
    <div className="card mx-auto" style={{ maxWidth: "380px", marginTop: "100px" }}>
      <div className="card-body">
        <h4 className="card-title mb-4">Sign in</h4>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <input
              name="email"
              className="form-control"
              placeholder="Email"
              type="email"
              value={form.email}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <input
              name="password"
              className="form-control"
              placeholder="Password"
              type="password"
              value={form.password}
              onChange={handleChange}
              required
            />
          </div>

          <button type="submit" className="btn btn-primary btn-block">
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
