import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import productService from "../../services/productService";

export default function ProductDetail() {
  const { id } = useParams(); // Lấy productId từ URL
  const [product, setProduct] = useState(null);

  useEffect(() => {
    if (!id) return;

    const fetchProduct = async () => {
      try {
        const data = await productService.getProductById(id);
        setProduct(data);
        console.log("Product loaded:", data);
      } catch (error) {
        console.error("❌ Không tìm thấy sản phẩm:", error);
      }
    };

    fetchProduct();
  }, [id]);

  if (!product) return <p>Đang tải sản phẩm...</p>;

  return (
    <div className="product-detail">
      <h1>{product.productName}</h1>
      <img
        src={productService.getProductImage(product.image)}
        alt={product.productName}
        width={300}
      />
      <p>{product.description}</p>
      <p>Số lượng: {product.quantity}</p>
      <p>Giá gốc: {product.price.toLocaleString()} VND</p>
      <p>Giá giảm: {product.specialPrice.toLocaleString()} VND</p>
      <p>Giảm: {product.discount}%</p>
    </div>
  );
}
