import React, { Component } from "react";
import { Link } from "react-router-dom";
import logo from "../assets/images/logo.png";
import vnFlag from "../assets/images/icons/flags/VN.webp";
import "bootstrap/dist/css/bootstrap.min.css";

class Header extends Component {
  constructor(props) {
    super(props);
    this.state = { user: null };
  }

  componentDidMount() {
    this.loadUser();
  }

  // üîπ T√°ch h√†m load user ri√™ng cho d·ªÖ g·ªçi l·∫°i
  loadUser = () => {
    try {
      const storedUser = localStorage.getItem("user");
      if (storedUser) {
        this.setState({ user: JSON.parse(storedUser) });
      } else {
        this.setState({ user: null });
      }
    } catch {
      this.setState({ user: null });
    }
  };

  // üîπ X·ª≠ l√Ω ƒëƒÉng xu·∫•t
  handleLogout = () => {
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    this.setState({ user: null });
  };

  render() {
    const { user } = this.state;

    return (
      <header className="section-header">
        {/* --- Thanh tr√™n --- */}
        <nav className="navbar d-none d-md-flex p-md-0 navbar-expand-sm navbar-light border-bottom bg-light">
          <div className="container">
            <ul className="navbar-nav mr-auto">
              <li className="nav-item">
                {!user ? (
                  <span className="nav-link">
                    Hi, <Link to="/login">Log In</Link> or{" "}
                    <Link to="/register">Register</Link>
                  </span>
                ) : (
                  <span className="nav-link">
                    üëã Xin ch√†o, <strong>{user.first_name}</strong>
                  </span>
                )}
              </li>
            </ul>
            <ul className="navbar-nav">
              <li className="nav-item">
                <a href="#" className="nav-link">
                  <img src={vnFlag} height="22" alt="flag" /> Ship to
                </a>
              </li>
            </ul>
          </div>
        </nav>

        {/* --- Thanh gi·ªØa (logo + search + user info) --- */}
        <div className="container py-3 border-bottom">
          <div className="row align-items-center">
            {/* Logo */}
            <div className="col-6 col-md-3">
              <Link to="/" className="brand-wrap">
                <img src={logo} height={56} alt="Logo" />
              </Link>
            </div>

            {/* Search */}
            <div className="col-lg-6 col-md-6 col-sm-12">
              <form className="search-header">
                <div className="input-group">
                  <input
                    type="text"
                    className="form-control"
                    placeholder="T√¨m ki·∫øm s·∫£n ph·∫©m..."
                  />
                  <div className="input-group-append">
                    <button className="btn btn-primary" type="submit">
                      üîç
                    </button>
                  </div>
                </div>
              </form>
            </div>

            {/* User + Cart */}
            <div className="col-lg-3 col-md-3 col-sm-12 d-flex justify-content-end align-items-center gap-3">
              {user ? (
                <>
                  <Link
                    to="/profile"
                    className="text-dark d-flex align-items-center gap-2 text-decoration-none"
                  >
                    <i className="fa fa-user-circle fs-4"></i>
                    <span>{user.first_name}</span>
                  </Link>

                  <Link
                    to="/cart"
                    className="text-dark text-decoration-none"
                    title="Gi·ªè h√†ng"
                  >
                    <i className="fa fa-shopping-cart fs-4"></i>
                  </Link>

                  <button
                    onClick={this.handleLogout}
                    className="btn btn-outline-danger btn-sm"
                    style={{ borderRadius: "20px" }}
                  >
                    ƒêƒÉng xu·∫•t
                  </button>
                </>
              ) : (
                <Link
                  to="/login"
                  className="btn btn-primary rounded-pill px-3"
                >
                  Sign In
                </Link>
              )}
            </div>
          </div>
        </div>

        {/* --- Navbar d∆∞·ªõi --- */}
        <nav className="navbar navbar-main navbar-expand bg-light">
          <div className="container">
            <ul className="navbar-nav flex-wrap">
              <li className="nav-item">
                <Link to="/" className="nav-link">
                  Home
                </Link>
              </li>
              <li className="nav-item">
                <Link to="/product" className="nav-link">
                  Products
                </Link>
              </li>
            </ul>
          </div>
        </nav>
      </header>
    );
  }
}

export default Header;
