import React, { useEffect, useState } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import userService from "../services/userService";
import categoryService from "../services/categoryService";
import bannerService from "../services/bannerService";
import cartService from "../services/cartService";

import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";
import "../assets/fonts/fontawesome/css/all.min.css";
// Đảm bảo bạn đã import file css ở trên
import "../assets/css/style.css"; 

const Header = () => {
  const [user, setUser] = useState(null);
  const [userInfo, setUserInfo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [categories, setCategories] = useState([]);
  const [loadingCategories, setLoadingCategories] = useState(false);
  const [cartCount, setCartCount] = useState(0);

  const navigate = useNavigate();
  const location = useLocation();

  // Logic lấy dữ liệu User và Cart (Giữ nguyên logic cũ của bạn)
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      const parsedUser = JSON.parse(storedUser);
      setUser(parsedUser);
      if (parsedUser.userId) {
        userService.getUserById(parsedUser.userId).then(data => {
          setUserInfo(data);
        }).catch(err => console.error(err));
      }
    }
    fetchCategories();
  }, []);

  useEffect(() => {
    loadCartCount();
    window.addEventListener('cartUpdated', loadCartCount);
    return () => window.removeEventListener('cartUpdated', loadCartCount);
  }, [user]);

  const fetchCategories = async () => {
    setLoadingCategories(true);
    try {
      const data = await categoryService.getAllCategories();
      setCategories(data || []);
    } finally {
      setLoadingCategories(false);
    }
  };

  const loadCartCount = async () => {
    const currentUser = JSON.parse(localStorage.getItem('user') || '{}');
    if (currentUser?.email) {
      const count = await cartService.getCartCount(currentUser.email);
      setCartCount(count);
    }
  };

  const handleCategoryClick = (category) => {
    navigate(`/product`, { 
      state: { selectedCategory: category.categoryId || category.id, categoryName: category.categoryName } 
    });
  };

  const renderUserName = () => {
    if (!user) return "Tài khoản";
    return userInfo?.firstName || user.email?.split('@')[0] || "User";
  };

  return (
    <header className="section-header">
      {/* 1. TOP BANNER */}
      <div className="top-banner-ads">
        Nội thành Hà Nội - TP. Hồ Chí Minh | GIAO SIÊU TỐC 2H - FREESHIP 0Đ
      </div>

      {/* 2. MAIN HEADER (Màu cam) */}
      <section className="header-main-orange">
        <div className="container">
          <div className="row align-items-center">
            {/* Logo */}
            <div className="col-lg-2 col-md-3 col-6">
              <Link to="/" className="text-decoration-none">
                <h2 className="text-white fw-bold mb-0" style={{ letterSpacing: '-1px' }}>ShopBeAnh</h2>
              </Link>
            </div>

            {/* Search Bar */}
            <div className="col-lg-6 col-md-5 d-none d-md-block">
              <form className="search-bar-wrap">
                <i className="fa fa-search text-muted me-2"></i>
                <input 
                  type="text" 
                  placeholder="Tìm kiếm sản phẩm, thương hiệu..." 
                  className="form-control"
                />
              </form>
            </div>

            {/* Icons */}
            <div className="col-lg-4 col-md-4 col-6">
              <div className="d-flex justify-content-end align-items-center gap-4">
                {/* Widget Profile với tính năng Hover */}
<div className="user-dropdown-container">
  <Link 
    to={user ? "/profile" : "/login"} 
    className="header-orange-icons text-center"
  >
    <div className="text-center">
      <i className="fa fa-user-circle fa-lg d-block mb-1"></i>
      <span style={{ fontSize: '12px' }}>{renderUserName()}</span>
    </div>
  </Link>

  {/* Menu hiện ra khi rê chuột */}
  <ul className="user-mega-menu shadow-lg">
    {!user ? (
      <>
        <li>
          <Link to="/login" className="user-menu-item">
            <i className="fa fa-sign-in-alt"></i> Đăng nhập
          </Link>
        </li>
        <li>
          <Link to="/register" className="user-menu-item border-top">
            <i className="fa fa-user-plus"></i> Đăng ký
          </Link>
        </li>
      </>
    ) : (
      <>
        <li>
          <Link to="/profile" className="user-menu-item">
            <i className="fa fa-id-card"></i> Trang cá nhân
          </Link>
        </li>
        <li>
          <Link to="/orders" className="user-menu-item">
            <i className="fa fa-list-alt"></i> Đơn hàng của tôi
          </Link>
        </li>
        <li>
          <button 
            onClick={handleLogout} 
            className="user-menu-item border-top w-100 text-start border-0 bg-transparent text-danger"
          >
            <i className="fa fa-power-off"></i> Đăng xuất
          </button>
        </li>
      </>
    )}
  </ul>
</div>

                <Link to="/cart" className="header-orange-icons position-relative">
                  <div className="text-center">
                    <i className="fa fa-shopping-basket fa-lg d-block mb-1"></i>
                    <span className="badge rounded-pill bg-white text-danger position-absolute top-0 start-50 translate-middle-x" 
                          style={{ marginTop: '-8px', fontSize: '10px' }}>
                      {cartCount}
                    </span>
                    <span style={{ fontSize: '12px' }}>Giỏ hàng</span>
                  </div>
                </Link>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* 3. NAVIGATION (Hover Menu) */}
      <nav className="nav-categories shadow-sm d-none d-lg-block">
        <div className="container">
          <ul className="nav d-flex align-items-center">
            {/* MỤC DANH MỤC CÓ HOVER */}
            <li className="nav-item dropdown-hover-container">
              <a className="nav-link fw-bold text-dark" href="#">
                <i className="fa fa-bars me-2 text-warning"></i>DANH MỤC SẢN PHẨM
              </a>

              {/* Menu thực tế hiện ra khi rê chuột */}
              <ul className="mega-dropdown-menu">
                {loadingCategories ? (
                  <li className="text-center p-3 text-muted">Đang tải...</li>
                ) : (
                  categories.map((cat, idx) => (
                    <li key={idx}>
                      <a href="#" className="mega-dropdown-item" onClick={(e) => {
                        e.preventDefault();
                        handleCategoryClick(cat);
                      }}>
                        {cat.categoryName || cat.name}
                      </a>
                    </li>
