import React, { useState, useEffect, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import categoryService from "../../../services/categoryService";
import Swal from "sweetalert2";

const CategoryListAD = () => {
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [isEdit, setIsEdit] = useState(false);
  const [formData, setFormData] = useState({ categoryId: "", categoryName: "" });
  const navigate = useNavigate();

  // 1. Ki·ªÉm tra Role v√† Token
  const checkAuth = useCallback(() => {
    const token = localStorage.getItem("token");
    const role = localStorage.getItem("role"); 

    console.log("üîë [Check Auth] Token:", token ? "ƒê√£ l·∫•y ƒë∆∞·ª£c" : "TR·ªêNG");
    console.log("üë§ [Check Auth] Role:", role);

    if (!token || role !== "ADMIN") {
      console.error("üö´ Truy c·∫≠p b·ªã ch·∫∑n: Kh√¥ng ph·∫£i ADMIN ho·∫∑c ch∆∞a ƒëƒÉng nh·∫≠p");
      return false;
    }
    return true;
  }, []);

  // 2. L·∫•y d·ªØ li·ªáu t·ª´ Service
  const fetchCategories = useCallback(async () => {
    try {
      setLoading(true);
      console.log("üì° ƒêang g·ªçi getAllCategories t·ª´ service...");
      
      const response = await categoryService.getAllCategories();
      console.log("üì• D·ªØ li·ªáu th√¥ t·ª´ Service tr·∫£ v·ªÅ:", response);

      // V√¨ Controller c·ªßa m√†y tr·∫£ v·ªÅ CategoryResponse ch·ª©a "content"
      // N√™n ta ph·∫£i check xem n√≥ l√† m·∫£ng tr·ª±c ti·∫øp hay n·∫±m trong object
      const dataContent = response.content || (Array.isArray(response) ? response : []);
      
      console.log("üìä D·ªØ li·ªáu sau khi x·ª≠ l√Ω ƒë·ªÉ map v√†o table:", dataContent);
      setCategories(dataContent);
    } catch (err) {
      console.error("‚ùå L·ªói khi fetch d·ªØ li·ªáu:", err);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    if (checkAuth()) {
      fetchCategories();
    } else {
      Swal.fire("C·∫£nh b√°o", "M√†y kh√¥ng c√≥ quy·ªÅn truy c·∫≠p!", "error");
      navigate("/login");
    }
  }, [fetchCategories, checkAuth, navigate]);

  // 3. X·ª≠ l√Ω Th√™m/S·ª≠a
  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("üì§ D·ªØ li·ªáu g·ª≠i ƒëi:", formData);

    try {
      if (isEdit) {
        await categoryService.updateCategory(formData.categoryId, { categoryName: formData.categoryName });
      } else {
        await categoryService.createCategory({ categoryName: formData.categoryName });
      }
      
      Swal.fire("Th√†nh c√¥ng", "ƒê√£ l∆∞u danh m·ª•c!", "success");
      setShowModal(false);
      fetchCategories();
    } catch (err) {
      console.error("‚ùå L·ªói khi Save:", err.response?.data || err);
      Swal.fire("L·ªói", "Ki·ªÉm tra l·∫°i quy·ªÅn ADMIN ho·∫∑c Token!", "error");
    }
  };

  return (
    <div className="p-4">
      <div className="card shadow-sm border-0">
        <div className="card-header bg-white d-flex justify-content-between align-items-center">
          <h5 className="mb-0 fw-bold text-dark">DANH M·ª§C H·ªÜ TH·ªêNG</h5>
          <button className="btn btn-primary btn-sm" onClick={() => { setIsEdit(false); setFormData({categoryId:"", categoryName:""}); setShowModal(true); }}>
            + Th√™m Category
          </button>
        </div>

        <div className="table-responsive">
          <table className="table table-hover align-middle">
            <thead className="table-light">
              <tr>
                <th width="15%">ID</th>
                <th width="60%">T√™n danh m·ª•c</th>
                <th width="25%" className="text-center">H√†nh ƒë·ªông</th>
              </tr>
            </thead>
            <tbody>
              {loading ? (
                <tr><td colSpan="3" className="text-center py-5">ƒêang ki·ªÉm tra quy·ªÅn v√† t·∫£i d·ªØ li·ªáu...</td></tr>
              ) : categories.length > 0 ? (
                categories.map((cat) => (
                  <tr key={cat.categoryId}>
                    <td className="text-muted">#{cat.categoryId}</td>
                    <td className="fw-bold">{cat.categoryName}</td>
                    <td className="text-center">
                      <button className="btn btn-sm btn-outline-info me-2" onClick={() => { setIsEdit(true); setFormData(cat); setShowModal(true); }}>S·ª≠a</button>
                      <button className="btn btn-sm btn-outline-danger" onClick={async () => {
                        if(window.confirm("X√≥a nh√©?")) {
                            // T·∫°m th·ªùi g·ªçi API tr·ª±c ti·∫øp n·∫øu service c·ªßa m√†y ch∆∞a c√≥ h√†m delete chu·∫©n
                            try {
                                await categoryService.updateCategory(cat.categoryId, null); // Ho·∫∑c h√†m delete c·ªßa m√†y
                                fetchCategories();
                            } catch(e) { console.error(e); }
                        }
                      }}>X√≥a</button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr><td colSpan="3" className="text-center py-5 text-secondary">M·ªü Console (F12) ƒë·ªÉ xem t·∫°i sao kh√¥ng hi·ªán d·ªØ li·ªáu</td></tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* Modal - C·∫ßn ƒë·∫£m b·∫£o file JS h·ªó tr·ª£ JSX */}
      {showModal && (
        <div className="modal d-block" style={{backgroundColor: "rgba(0,0,0,0.5)"}}>
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content">
              <form onSubmit={handleSubmit}>
                <div className="modal-header">
                  <h5 className="modal-title">{isEdit ? "C·∫≠p nh·∫≠t danh m·ª•c" : "Th√™m danh m·ª•c m·ªõi"}</h5>
                  <button type="button" className="btn-close" onClick={() => setShowModal(false)}></button>
                </div>
                <div className="modal-body p-4">
                  <div className="mb-3">
                    <label className="form-label fw-bold">T√™n danh m·ª•c (Min 5 k√Ω t·ª±)</label>
                    <input 
                      type="text" className="form-control" 
                      value={formData.categoryName} 
                      onChange={e => setFormData({...formData, categoryName: e.target.value})}
                      required 
                    />
                  </div>
                </div>
                <div className="modal-footer">
                  <button type="submit" className="btn btn-primary w-100">L∆∞u l·∫°i</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CategoryListAD;
