import React, { useState, useEffect } from 'react';

// Link ảnh mẫu chất lượng cao (Bạn có thể thay bằng link ảnh trong thư mục public hoặc link online)
const HARDCODED_BANNERS = [
  {
    id: 1,
    title: "Khuyến mãi mùa hè",
    imageUrl: "https://images.unsplash.com/photo-1607082348824-0a96f2a4b9da?w=1920&q=80",
    link: "/shop"
  },
  {
    id: 2,
    title: "Bộ sưu tập mới",
    imageUrl: "https://images.unsplash.com/photo-1607082350899-7e105aa886ae?w=1920&q=80",
    link: "/new-arrivals"
  },
  {
    id: 3,
    title: "Ưu đãi thành viên",
    imageUrl: "https://images.unsplash.com/photo-1555529669-e69e7aa0ba9a?w=1920&q=80",
    link: "/sale"
  },
  {
    id: 4,
    title: "Sản phẩm bán chạy",
    imageUrl: "https://images.unsplash.com/photo-1441986300917-64674bd600d8?w=1920&q=80",
    link: "/best-sellers"
  },
  {
    id: 5,
    title: "Giảm giá đặc biệt",
    imageUrl: "https://images.unsplash.com/photo-1472851294608-062f824d29cc?w=1920&q=80",
    link: "/clearance"
  }
];

const Banner = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [imageLoaded, setImageLoaded] = useState({});

  // Tự động chuyển slide mỗi 5 giây
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % HARDCODED_BANNERS.length);
    }, 5000);
    return () => clearInterval(timer);
  }, []);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % HARDCODED_BANNERS.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + HARDCODED_BANNERS.length) % HARDCODED_BANNERS.length);
  };

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  const handleImageLoad = (id) => {
    setImageLoaded(prev => ({ ...prev, [id]: true }));
  };

  return (
    <section className="banner-section mb-5">
      <div className="container-fluid px-0">
        <div className="banner-slider position-relative overflow-hidden">

          {/* Track chứa các Slide */}
          <div 
            className="banner-track d-flex" 
            style={{ 
              transform: `translateX(-${currentSlide * 100}%)`,
              transition: 'transform 0.6s cubic-bezier(0.4, 0, 0.2, 1)' 
            }}
          >
            {HARDCODED_BANNERS.map((banner, index) => (
              <div key={banner.id} className="banner-slide flex-shrink-0 w-100 position-relative">

                {/* Hiệu ứng loading khi ảnh chưa tải xong */}
                {!imageLoaded[banner.id] && (
                  <div 
                    className="position-absolute top-0 start-0 w-100 h-100 d-flex align-items-center justify-content-center"
                    style={{ background: '#eee', zIndex: 1 }}
                  >
                    <div className="spinner-border text-warning" role="status"></div>
                  </div>
                )}

                <img
                  src={banner.imageUrl}
                  alt={banner.title}
                  className="img-fluid w-100"
                  style={{ 
                    height: '60vh',
                    minHeight: '400px',
                    maxHeight: '600px',
                    objectFit: 'cover'
                  }}
                  onLoad={() => handleImageLoad(banner.id)}
                />
              </div>
            ))}
          </div>

          {/* Nút Điều Hướng Trái/Phải */}
          <button
            className="position-absolute top-50 start-0 translate-middle-y btn btn-light rounded-circle shadow-sm m-3 d-none d-md-block"
            onClick={prevSlide}
            style={{ zIndex: 10, width: '45px', height: '45px', opacity: 0.8 }}
          >
            <i className="fas fa-chevron-left"></i>
          </button>

          <button
            className="position-absolute top-50 end-0 translate-middle-y btn btn-light rounded-circle shadow-sm m-3 d-none d-md-block"
            onClick={nextSlide}
            style={{ zIndex: 10, width: '45px', height: '45px', opacity: 0.8 }}
          >
            <i className="fas fa-chevron-right"></i>
          </button>

          {/* Dots Chỉ Số */}
          <div className="position-absolute bottom-0 start-50 translate-middle-x mb-4 d-flex" style={{ zIndex: 10 }}>
            {HARDCODED_BANNERS.map((_, index) => (
              <div
                key={index}
                onClick={() => goToSlide(index)}
                className={`mx-1 shadow-sm`}
                style={{ 
                  width: index === currentSlide ? '25px' : '10px', 
                  height: '10px',
                  borderRadius: '10px',
                  backgroundColor: index === currentSlide ? '#ff6a00' : '#fff',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease'
                }}
              />
            ))}
          </div>

        </div>
      </div>
    </section>
  );
};

export default Banner;
