import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import productService from '../../services/productService';
import postService from '../../services/postService';

const ProductSold = () => {
  const [topProducts, setTopProducts] = useState([]);
  const [allProducts, setAllProducts] = useState([]);
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(0);

  const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

  useEffect(() => {
    fetchInitialData();
  }, []);

  const fetchInitialData = async () => {
    setLoading(true);
    try {
      // 1. Lấy top sản phẩm bán chạy
      const topRes = await productService.getTopSellingProducts(0, 8);
      setTopProducts(topRes.content || []);

      // 2. Lấy danh sách sản phẩm thông thường
      const allRes = await productService.getAllProducts(0, 8);
      setAllProducts(allRes.content || []);

      // 3. Lấy tin tức tự động
      const externalPosts = await postService.getExternalPosts();
      setPosts(externalPosts.slice(0, 3)); // Chỉ lấy 3 bài mới nhất
    } catch (err) {
      console.error('Lỗi tải dữ liệu:', err);
    } finally {
      setLoading(false);
    }
  };

  const loadMoreProducts = async () => {
    const nextPage = page + 1;
    try {
      const res = await productService.getAllProducts(nextPage, 8);
      setAllProducts([...allProducts, ...(res.content || [])]);
      setPage(nextPage);
    } catch (err) {
      console.error('Lỗi tải thêm sản phẩm:', err);
    }
  };

  // Component con hiển thị Card Sản phẩm để tái sử dụng
  const ProductItem = ({ p, index, isTop }) => {
    const productId = p.productId || p.product_id;
    return (
      <div className="col-xl-3 col-lg-4 col-md-6 col-12 mb-4">
        <div className="card card-product-grid shadow-sm h-100 border-0 hover-up">
          {isTop && index < 3 && (
            <div className="position-absolute top-0 start-0 m-2 badge bg-warning text-dark shadow" style={{zIndex: 5}}>
              #{index + 1} Best Seller
            </div>
          )}
          <div className="img-wrap p-3 text-center">
            <Link to={`/product/${productId}`}>
              <img
                src={`${IMAGE_BASE_URL}/${p.image}`}
                alt={p.productName}
                onError={(e) => (e.target.src = "https://cdn-icons-png.flaticon.com/512/679/679922.png")}
                className="img-fluid"
                style={{ height: "200px", objectFit: "contain" }}
              />
            </Link>
          </div>
          <div className="info-wrap p-3 pt-0">
            <Link to={`/product/${productId}`} className="title text-dark fw-bold text-decoration-none d-block mb-2 text-truncate">
              {p.productName}
            </Link>
            <div className="price-wrap mb-2">
              <span className="price text-danger fw-bold h5">{(p.specialPrice || p.price).toLocaleString()} ₫</span>
              {p.specialPrice && <del className="text-muted ms-2 small">{p.price.toLocaleString()} ₫</del>}
            </div>
            <div className="d-flex justify-content-between align-items-center small text-muted mb-3">
              <span>Đã bán: {p.sold || 0}</span>
              <span><i className="fa fa-eye"></i> {p.views || 0}</span>
            </div>
            <Link to={`/product/${productId}`} className="btn btn-orange-outline w-100 rounded-pill btn-sm">
               Xem chi tiết
            </Link>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="main-content-wrapper bg-light pb-5">

      {/* SECTION 1: TOP SELLING */}
      <section className="container pt-5">
        <div className="section-heading-custom mb-4">
          <h3 className="fw-bold text-orange"><i className="fa fa-fire me-2"></i>SẢN PHẨM BÁN CHẠY</h3>
          <p className="text-muted">Được các mẹ tin dùng nhiều nhất trong tháng</p>
        </div>
        <div className="row">
          {topProducts.map((p, i) => <ProductItem key={`top-${i}`} p={p} index={i} isTop={true} />)}
        </div>
      </section>

      {/* SECTION 2: DISCOVER ALL */}
      <section className="container mt-5">
        <div className="section-heading-custom mb-4">
          <h3 className="fw-bold text-dark">KHÁM PHÁ THẾ GIỚI CỦA BÉ</h3>
          <p className="text-muted">Hàng ngàn sản phẩm chất lượng đang chờ đón</p>
        </div>
        <div className="row">
          {allProducts.map((p, i) => <ProductItem key={`all-${i}`} p={p} isTop={false} />)}
        </div>
        <div className="text-center mt-4">
          <button className="btn btn-orange px-5 rounded-pill shadow-sm" onClick={loadMoreProducts} disabled={loading}>
            {loading ? "Đang tải..." : "Xem thêm sản phẩm"}
          </button>
        </div>
      </section>

{/* --- SECTION BÀI VIẾT SKINCARE --- */}
<section className="container mt-5 pt-5 border-top">
  <div className="d-flex justify-content-between align-items-end mb-4">
    <div>
      <h3 className="fw-bold text-dark mb-1">
        <i className="fa fa-magic me-2 text-orange"></i>BÍ QUYẾT SKINCARE & LÀM ĐẸP
      </h3>
      <p className="text-muted mb-0">Cập nhật xu hướng mỹ phẩm và cách chăm sóc da khoa học</p>
    </div>
    <span className="badge rounded-pill bg-soft-orange text-orange px-3 py-2">
      <i className="fa fa-sync fa-spin me-1"></i> Tin mới nhất
    </span>
  </div>

  <div className="row">
    {posts.length > 0 ? (
      posts.slice(0, 3).map((post, idx) => (
        <div key={idx} className="col-md-4 mb-4">
          <div className="card border-0 shadow-sm h-100 overflow-hidden post-card-beauty">
            <div className="position-relative overflow-hidden">
              <img src={post.img} className="card-img-top transition-img" alt={post.title} style={{height: '220px', objectFit: 'cover'}} />
              <div className="category-tag">Làm đẹp</div>
            </div>
            <div className="card-body p-4">
              <small className="text-muted d-block mb-2">
                <i className="fa fa-calendar-alt me-1"></i> {post.date}
              </small>
              <h6 className="card-title fw-bold mb-3" style={{ lineHeight: '1.5', height: '48px', overflow: 'hidden' }}>
                {post.title}
              </h6>
              <p className="card-text text-muted small mb-3">
                {post.desc}
              </p>
              <a href={post.link} target="_blank" rel="noopener noreferrer" 
                 className="btn-link-beauty text-decoration-none fw-bold">
                Khám phá ngay <i className="fa fa-arrow-right ms-1"></i>
              </a>
            </div>
          </div>
        </div>
      ))
    ) : (
      <div className="col-12 text-center py-5">
        <div className="spinner-border text-orange mb-3" role="status"></div>
        <p className="text-muted">Đang cập nhật kiến thức làm đẹp cho bạn...</p>
      </div>
    )}
  </div>
</section>
    </div>
  );
};

export default ProductSold;
