// src/pages/cart/ProductCart.js
import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import cartService from '../../services/cartService';

const ProductCart = () => {
  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(true);
  const [updating, setUpdating] = useState(false);
  const navigate = useNavigate();

  const currentUser = JSON.parse(localStorage.getItem('user')) || { email: 'guest@example.com' };

  useEffect(() => {
    loadCart();
  }, []);

  const loadCart = async () => {
    try {
      setLoading(true);
      console.log("üîÑ ƒêang t·∫£i gi·ªè h√†ng cho user:", currentUser.email);

      const cartData = await cartService.getActiveCart(currentUser.email);
      console.log("üì¶ D·ªØ li·ªáu gi·ªè h√†ng:", cartData);

      setCart(cartData);

    } catch (error) {
      console.error('‚ùå L·ªói khi t·∫£i gi·ªè h√†ng:', error);
      alert('‚ùå L·ªói khi t·∫£i gi·ªè h√†ng: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

  const handleUpdateQuantity = async (productId, newQuantity) => {
    if (newQuantity < 1) return;

    try {
      setUpdating(true);

      if (!cart || !cart.cartId) {
        alert('‚ùå Kh√¥ng t√¨m th·∫•y gi·ªè h√†ng');
        return;
      }

      console.log(`üîÑ C·∫≠p nh·∫≠t s·ªë l∆∞·ª£ng s·∫£n ph·∫©m ${productId} th√†nh ${newQuantity}`);

      await cartService.updateQuantity(cart.cartId, productId, newQuantity);
      await loadCart();
      alert('‚úÖ ƒê√£ c·∫≠p nh·∫≠t s·ªë l∆∞·ª£ng');
    } catch (error) {
      console.error('‚ùå L·ªói khi c·∫≠p nh·∫≠t s·ªë l∆∞·ª£ng:', error);
      alert('‚ùå L·ªói khi c·∫≠p nh·∫≠t s·ªë l∆∞·ª£ng: ' + (error.response?.data?.message || error.message));
    } finally {
      setUpdating(false);
    }
  };

  const handleRemoveItem = async (productId) => {
    if (!window.confirm('B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a s·∫£n ph·∫©m n√†y kh·ªèi gi·ªè h√†ng?')) return;

    try {
      setUpdating(true);

      if (!cart || !cart.cartId) {
        alert('‚ùå Kh√¥ng t√¨m th·∫•y gi·ªè h√†ng');
        return;
      }

      console.log(`üóëÔ∏è X√≥a s·∫£n ph·∫©m ${productId} kh·ªèi gi·ªè h√†ng`);

      await cartService.removeFromCart(cart.cartId, productId);
      await loadCart();
      alert('‚úÖ ƒê√£ x√≥a s·∫£n ph·∫©m kh·ªèi gi·ªè h√†ng');
    } catch (error) {
      console.error('‚ùå L·ªói khi x√≥a s·∫£n ph·∫©m:', error);
      alert('‚ùå L·ªói khi x√≥a s·∫£n ph·∫©m: ' + (error.response?.data?.message || error.message));
    } finally {
      setUpdating(false);
    }
  };

  const handleCheckout = () => {
    if (!currentUser || !currentUser.email) {
      alert('‚ùå Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ ƒë·∫∑t h√†ng');
      navigate('/login');
      return;
    }

    if (!cart || !cart.products || cart.products.length === 0) {
      alert('‚ùå Gi·ªè h√†ng tr·ªëng');
      return;
    }

    // ƒêi·ªÅu h∆∞·ªõng ƒë·∫øn trang checkout
    navigate('/checkout');
  };

  // T√≠nh t·ªïng ti·ªÅn t·ª´ cart items
  const calculateItemTotal = (item) => {
    const price = item.productPrice || item.specialPrice || item.price || 0;
    const quantity = item.quantity || 1;
    return price * quantity;
  };

  // T√≠nh t·ªïng to√†n b·ªô gi·ªè h√†ng
  const calculateCartTotal = () => {
    if (!cart || !cart.products) return 0;
    return cart.products.reduce((total, item) => total + calculateItemTotal(item), 0);
  };

  // T√≠nh t·ªïng s·ªë s·∫£n ph·∫©m
  const calculateTotalItems = () => {
    if (!cart || !cart.products) return 0;
    return cart.products.reduce((total, item) => total + (item.quantity || 1), 0);
  };

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-2">ƒêang t·∫£i gi·ªè h√†ng...</p>
        </div>
      </div>
    );
  }

  if (!cart || !cart.products || cart.products.length === 0) {
    return (
      <div className="container mt-4">
        <div className="text-center py-5">
          <div className="empty-cart-icon mb-4">
