      <div className="row">
        <div className="col-12">
          <h1 className="h2 text-primary mb-3 text-center">Gi·ªè h√†ng c·ªßa b·∫°n</h1>

        </div>
      </div>

      <div className="row mt-4">
        {/* Danh s√°ch s·∫£n ph·∫©m */}
        <div className="col-lg-8">
          <div className="card shadow-sm mb-4">
            <div className="card-header bg-white d-flex justify-content-between align-items-center">
              <h5 className="mb-0">
                <i className="fas fa-list me-2"></i>
                Danh s√°ch s·∫£n ph·∫©m ({cart.products.length})
              </h5>
              <button 
                className="btn btn-outline-secondary btn-sm"
                onClick={loadCart}
                disabled={updating}
              >
                <i className="fas fa-sync-alt me-1"></i>
                L√†m m·ªõi
              </button>
            </div>
            <div className="card-body p-0">
              {cart.products.map((item, index) => {
                const itemTotal = calculateItemTotal(item);
                const itemPrice = item.productPrice || item.specialPrice || item.price || 0;

                return (
                  <div key={item.productId || index} className="border-bottom p-3">
                    <div className="row align-items-center">
                      {/* H√¨nh ·∫£nh */}
                      <div className="col-md-2">
                        <img
                          src={item.image ? `http://localhost:8080/api/public/products/image/${item.image}` : 'https://cdn-icons-png.flaticon.com/512/679/679922.png'}
